INSERT INTO `quest_template_locale` VALUES (36130, 'deDE', 'Einsatzbereit', 'Sprecht mit Misirin Starkzeh, Scharfseherin Kylanda und Chris Clarkie.', 'Strammstehen, $n! Wir haben keine Zeit zu verlieren. Sprecht mit meinen Leuten hier, sie werden Euch in alles Wichtige einweisen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'esES', 'A tu servicio', 'Habla con Misirin Dedofornido, la clarividente Kylanda y Chris Clarkie.', '¡Al frente, $n! No hay tiempo que perder. Ve a hablar con esos tipos y ellos te informarán.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'esMX', 'A la orden', 'Habla con Misirin Dedogordo, la clarividente Kylanda y Chris Clarkie.', '¡Al frente y al centro, $n! No hay tiempo que perder, ve a hablar con esos sujetos y ellos te informarán.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'frFR', 'Au rapport', 'Parlez à Misirin Rude-Orteil, à la Long-voyante Kylanda et à Chris Clarkie.', 'Droit sur l’objectif, $n ! Pas de temps à perdre, allez parler à ces gens et ils vous informeront.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'itIT', 'A rapporto', 'Parla con Misirin Piedegrosso, la Chiaroveggente Kylanda e Chris Clarkie.', 'In prima linea, $n! Non c\'è tempo da perdere, vai a parlare con i miei compagni, ti aggiorneranno sulla situazione.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'koKR', '임전 태세 보고', '미시린 스타우트토, 선견자 카일란다, 크리스 클라키와 대화해야 합니다.', '$n 님, 이제 정신 바짝 차리세요! 낭비할 시간이 없습니다. 어서 가서 임전 태세 보고를 마쳐주세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'ptBR', 'Apresentando-se para o serviço', 'Fale com Misirin Quepezão, com a Clarividente Kylanda e com Cristóvão Caldas.', 'Atenção, $n! Não há tempo a perder! Vá procurar esse pessoal. Eles colocarão você a par da situação.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'ruRU', 'Всегда готов!', 'Поговорите с Мизирин Пивохлеб, предсказательницей Киландой и Крисом Кларки.', 'Слушай сюда, $n! У меня времени нет, поговори с этими ребятами – они введут тебя в курс дела.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'zhCN', '报到', '与米思林·酒趾、先知奇兰达和克里斯·克拉奇谈一谈。', '出列，$n！别耽误时间了，去找这些家伙谈谈，他们会向你说明一切的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36130, 'zhTW', '前來報到', '與米絲琳‧斯圖托、先知凱蘭達和克里斯‧克拉奇交談。', '非常重要，$n！別浪費時間了，去和那些追隨者交談，並讓他們與你同行。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'deDE', 'Bewährungsprobe', 'Beweist Mokugg Hauwech Euer Talent, indem Ihr mit seinem Köder 5 Zangaraale fangt.', 'Als Krieger habt Ihr gezeigt, was Ihr könnt. Sehen wir doch mal, wie es um Eure Angelkenntnisse bestellt ist. Ich habe einen Köder aus den Eisstachelstechern gebastelt, die Ihr mir gebracht habt. Angelt damit in diesem Küstengewässer nach Zangaraalen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'esES', 'Demostrar tu valía', 'Demuestra lo que vales a Mokugg Tasacerveza pescando 5 anguilas de Zangar con el cebo suministrado.', 'Ahora que has demostrado ser $gun guerrero:una guerrera;, veamos qué tal tus dotes para la pesca. He elaborado un cebo con los aguijoneros de espinas gélidas que me has traído; úsalo para pescar las anguilas de Zangar de estas aguas costeras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'esMX', 'Pon a prueba tu valor', 'Pesca 5 anguilas zangar con el cebo recibido y prueba cuánto vales a Mokugg Tragacerveza.', 'Ahora que probaste que eres $gun guerrero:una guerrera;, veamos qué tan $gbueno:buena; eres pescando. Fabriqué un cebo con los aguijones muelehielo que me trajiste. Úsalo para atrapar las anguilas zangar en estas costas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'frFR', 'Prouver votre valeur', 'Prouvez votre valeur à Mokugg Bat-Bière en attrapant 5 Anguilles de Zangar avec l’appât fourni.', 'Maintenant que vous avez fait vos preuves au combat, voyons un peu votre habileté en matière de pêche. J’ai confectionné un appât avec les aiguillonneurs épines-glaciales que vous m’avez apportés. Utilisez-le pour attraper des anguilles de Zangar dans les eaux côtières.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'itIT', 'Provare il tuo valore', 'Dai dimostrazione delle tue abilità di pesca a Mokugg Spaccabirra catturando 5 Anguille di Zangar usando l\'esca che ti ha preparato.', 'Ora che hai dimostrato il tuo valore di $gguerriero:guerriera;, vediamo come te la cavi con la pesca. Ho preparato un\'esca con i pungiglioni che mi hai portato, usala per catturare le Anguille di Zangar nelle acque di questa terra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'koKR', '낚시 솜씨 증명하기', '주어진 미끼로 장가르 뱀장어 5마리를 잡아, 모쿠그 라지파운더에게 솜씨를 증명해야 합니다.', '자네가 전사라는 건 입증했으니, 이제 낚시 솜씨 좀 보겠네. 자네가 가져온 얼음등뼈 쐐기로 미끼를 만들었다네. 이걸로 이 해역에서 장가르 뱀장어를 잡아 보게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'ptBR', 'Prova de valor', 'Prove seu valor a Mokugg Meioquilo obtendo 5 Enguias Zíngaras usando a isca que lhe deram.', 'Agora que você provou seu valor em combate, vamos ver como se sai em pescaria. Eu criei uma isca com os aguilhoadores friospinha que você me trouxe. Use-a para pescar enguias zíngaras nestas águas costeiras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'ruRU', 'Доказательство силы', 'Докажите Мокугу Пиводуру свое умение, поймав 5 зангарских угрей на его наживку.', 'Ну то, что ты $gопытный воин:опытная воительница;, это понятно. А теперь посмотрим, какой из тебя рыбак. Я тут сообразил наживку из жал ледоспинных жальщиков, которые ты $gпринес:принесла;. Возьми ее да налови зангарских угрей в прибрежных водах.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'zhCN', '证明你的实力', '使用莫古格·重磅提供的鱼饵捕捉5条赞加鳗鱼，向莫古格证明你的实力。', '你已证明了自己是一位战士，现在让我们来瞧瞧你的钓鱼技术如何。我已经把你带来的那些冰脊钉刺者做成了鱼饵，你试试用它来捕捉近海的赞加鳗鱼。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36131, 'zhTW', '證明你的價值', '用提供的魚餌捕捉5條贊格鰻魚，向莫庫格‧猛捶證明你的能力。', '現在你已經證明自己是個戰士，讓我們看看你的釣魚技巧如何。我已經用你帶回來的冰脊釘刺製作了一個魚餌。用這個魚餌在岸邊水域捕捉贊格鰻魚。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'deDE', 'Angeln in unserer Garnison', 'Kehrt mit dem Anglertagebuch der Frostwölfe zu Mak\'jin zurück.', 'Ihr habt Euren Wert bewiesen, $gReisender:Reisende;. Jetzt nehmt das Anglertagebuch der Frostwölfe in Eure Garnison mit und studiert es aufmerksam. Dann werdet Ihr schon bald mehr fangen, als Ihr essen könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'esES', 'De pesca en la ciudadela', 'Vuelve con Mak\'jin con el diario de pesca Lobo Gélido.', 'Has demostrado tu valía, $gviajero:viajera;. Ahora, llévate este diario de pesca Lobo Gélido a tu ciudadela y estúdiatelo. Dentro de nada, pescarás todo lo que quieras para comer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'esMX', 'Pescando en la fortaleza', 'Vuelve con Mak\'jin con el diario de pesca Lobo Gélido.', 'Probaste tu valor, $gviajero:viajera;. Ahora llévate este diario de pesca Lobo Gélido a tu fortaleza y estúdialo. Pescarás todo lo que quieras comer en muy poquito tiempo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'frFR', 'Pêche au fief', 'Retournez voir Mak’jin avec le Journal de pêche loup-de-givre.', 'Vous avez prouvé votre valeur, $gvoyageur:voyageuse;. À présent, rapportez ce journal de pêche loup-de-givre à votre fief et étudiez-le. Vous serez capable d’attraper de quoi vous nourrir en un rien de temps.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'itIT', 'Pesca nella Guarnigione', 'Porta le Cronache di Pesca dei Lupi Bianchi a Mak\'jin.', 'Hai dimostrato il tuo valore, $gstraniero:straniera;. Prendi pure queste Cronache di Pesca dei Lupi Bianchi e riportale alla Guarnigione per studiarle. Non ci metterai molto a imparare a pescare ogni specie marina di questo pianeta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'koKR', '주둔지에서 낚시하기', '막진에게 서리늑대 낚시 일지를 가져가야 합니다.', '자네 솜씨는 잘 봤네, 여행자여. 이제 이 서리늑대 낚시 일지를 자네 주둔지로 가져가서 연구해 보게. 먹을 수 있는 물고기라면 모두 낚을 수 있게 될 걸세.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'ptBR', 'Lições de pesca na guarnição', 'Fale novamente com Mak\'jin levando o Diário de Pesca Lobo do Gelo.', 'Você provou seu valor, viajante. Agora leve este diário de pesca dos lobo do gelo para a sua guarnição e estude. Antes que você se dê conta, vai estar pescando mais do que pode comer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'ruRU', 'Гарнизонная рыбалка', 'Вернитесь к Мак\'джину с заметками рыбаков из клана Северного Волка.', 'Что ж, ты $gпоказал:показала;, на что $gспособен:способна;. На, возьми эти заметки рыбаков из клана Северного Волка, возвращайся в свой гарнизон и изучай на здоровье. Если осилишь, потом хоть кракена выловить сможешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'zhCN', '在自家要塞里垂钓', '带着《霜狼垂钓日志》回去找马克金。', '你已证明了你的价值，旅行者。带着这本《霜狼垂钓日志》回你的要塞好好学学。很快，你就能靠钓鱼养活自己啦。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36132, 'zhTW', '要塞垂釣', '帶著霜狼釣魚日誌向馬可金回報。', '你證明了你的能力，旅人。現在將這本霜狼釣魚日誌帶回要塞研讀，很快地你釣魚時就能滿載而歸。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'deDE', 'Entdeckung der Artefaktfragmente', 'Misirin Starkzeh in der Sturmschildfeste möchte, dass Ihr Kreaturen in den Außenbezirken von Ashran abseits der Hauptstraße tötet und 5 Artefaktfragmente zu Ecilam bringt.', 'Die Artefaktfragmente scheinen von Kreaturen zu stammen, die wir in den Außenbezirken von Ashran gefunden haben. Geht und tötet alle, die Ihr findet, und bringt mindestens fünf zu Ecilam dem Magier am Lagerfeuer in der Sturmschildfeste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'esES', 'Descubrir los fragmentos de artefacto', 'Misirin Dedofornido, en Bastión Escudo de Tormenta, quiere que vayas a matar criaturas en las afueras de Ashran, frente al camino principal, y que le lleves a Ecilam 5 fragmentos de artefacto.', 'Los fragmentos de artefacto parecen provenir de alguna criatura que hayamos visto en las afueras de Ashran. Sal a matar a las que encuentres y llévale al menos cinco a Ecilam el mago cerca de la hoguera de Bastión Escudo de Tormenta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'esMX', 'Descubriendo los fragmentos de artefacto', 'Misirin Dedogordo en el Fuerte Escudo de Tormenta quiere que mates criaturas en las afueras de Ashran, fuera del camino principal, y que le lleves 5 fragmentos de artefacto a Ecilam.', 'Parece que los fragmentos de artefacto vienen de cualquier criatura en las afueras de Ashran. Ve y mata a cuantas puedas y trae al menos cinco a Ecilam el Mago cerca de la fogata en el bastión Escudo de Tormenta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'frFR', 'Découvrir les fragments d’artéfacts', 'Au Bastion de Bouclier-des-Tempêtes, Misirin Rude-Orteil vous a demandé de tuer des créatures dans les environs d’A’shran, à l’écart de la route principale, et de rapporter 5 Fragments d’artéfacts à Ecilam.', 'Il semblerait que l’on trouve des fragments d’artéfacts sur les créatures des environs d’A’shran. Allez-y, tuez toutes celles que vous croiserez et rapportez-en au moins cinq au mage Ecilam, près du feu de camp du bastion de Bouclier-des-Tempêtes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'itIT', 'Alla scoperta dei Frammenti di Manufatto', 'Misirin Piedegrosso alla Roccaforte di Scudo del Vento ti ha chiesto di uccidere delle creature nei dintorni di Ashran fuori dalla strada principale e di portare 5 Frammenti di Manufatto a Ecilam.', 'Sembra che i Frammenti di Manufatto siano presenti in tutte le creature che abbiamo trovato ad Ashran. Vai là fuori, uccidine più che puoi e porta almeno cinque frammenti a Ecilam il Mago vicino al focolare della Roccaforte di Scudo del Vento.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'koKR', '유물 조각을 찾아라', '폭풍방패 요새에 있는 미시린 스타우트토가 아쉬란 외곽에서 아무 생명체나 처치한 후 유물 조각 5개를 모아 에실람에게 가져가라고 했습니다.', '유물 조각은 아쉬란 외곽에서 아무 생명체나 처치하면 나오는 것 같아요. 유물 조각을 최소한 5개 모아, 폭풍방패 요새 모닥불 근처에 있는 에실람에게 가져가 보세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'ptBR', 'Em busca dos fragmentos de artefato', 'Misrin Quepezão, que está na Bastilha Escudo Tonante, quer que você mate as criaturas nos arredores de Ashran, fora da estrada principal, e leve 5 Fragmentos de Artefato para Aicilam.', 'Parece que os fragmentos de artefato vêm de alguma criatura que encontramos nos arredores de Ashran. Vá e mate as que você encontrar para trazer pelo menos cinco delas para Aicilam, o Mago, perto da fogueira, na Bastilha Escudo Tonante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'ruRU', 'Калейдоскоп фрагментов', 'Убивайте существ на окраинах Ашрана в стороне от главной дороги и принесите 5 фрагментов артефакта Экиламу по просьбе Мизирин Пивохлеб из Преграды Ветров.', 'Ну и дела! Похоже, фрагменты артефакта так и сыплются из каждого существа, обитающего на окраинах Ашрана. Добудь не менее пяти фрагментов и принеси их магу Экиламу к костру в Преграде Ветров.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'zhCN', '找到神器碎片', '暴风之盾堡垒的米思林·酒趾要你在阿什兰郊野的大路边消灭怪物，并将5块神器碎片交给艾希兰姆。', '你似乎可以从阿什兰郊外的任何生物身上找到神器碎片。去那儿消灭那里的生物，并至少带5块碎片回暴风之盾堡垒，把它们交给营火附近的法师艾希兰姆。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36133, 'zhTW', '挖掘文物碎片', '在暴風之盾堡壘的米絲琳‧斯圖托，要你出外殺死艾斯蘭主要道路外圍的生物，並帶5片文物碎片交給艾希嵐。', '看來似乎可以從艾斯蘭周邊的任何生物上取得文物碎片。出去殺死你看到的任何生物，至少帶五片給暴風之盾堡壘營火附近的法師艾希嵐。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'deDE', 'Garnisonskampagne: Erwachen', 'Sprecht mit Thisalee Krähe in Gorgoreks Ruh in Gorgrond.', 'Wir haben Kriegsfürstin Klingenwut und das gestohlene Artefakt in den Salzfeldern im Westen Gorgronds ausfindig gemacht.$B$BThisalee Krähe wird sich dort mit Euch treffen. Sie hat einen Spähtrupp dabei.$B$BDie gesamte Garnison wurde in höchste Alarmbereitschaft versetzt. Wir müssen bereit sein, den Feind aufzuhalten, falls es nötig wird.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'esES', 'Campaña de la ciudadela: Despertar', 'Habla con Thisalee Cuervo en el Descanso de Gorgorek, en Gorgrond.', 'Hemos seguido el rastro de la señora de la guerra Furiafilo y del artefacto robado hasta los campos de sal al oeste de Gorgrond.$B$BThisalee Cuervo y un equipo de exploración avanzado se reunirán allí contigo.$B$BToda la ciudadela está en alerta máxima. Debemos estar listos para interceptar al enemigo si es necesario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'esMX', 'Campaña de fortaleza: despertar', 'Habla con Thisalee Cuervo en el Descanso de Gorgorek en Gorgrond.', 'Rastreamos a la Señora de la Guerra Filofurioso y el artefacto robado en los campos de sal, al oeste de Gorgrond.$B$BThisalee Cuervo te encontrará ahí junto a un grupo explorador de avanzada.$B$BToda la fortaleza está alerta. Debemos estar listos para interceptar al enemigo si es necesario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'frFR', 'Campagne du fief : réveil', 'Parlez à Thisalee Corbeau au Repos de Gorgorek en Gorgrond.', 'Nous avons pisté le seigneur de guerre Lames-Furieuses et l’artéfact volé jusqu’aux marais salants dans l’ouest de Gorgrond.$B$BThisalee Corbeau vous y rejoindra avec une unité de reconnaissance avancée.$B$BLe fief tout entier est en état d’alerte maximale. Nous devons nous tenir prêts à intercepter l’ennemi en cas de besoin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'itIT', 'Campagna della Guarnigione: Risveglio', 'Parla con Thisalee Alacorvina alla Caduta di Gorgorek nella regione di Gorgrond.', 'Abbiamo trovato la Signora della Guerra Lamafuriosa e il manufatto rubato presso le pozze saline della zona occidentale di Gorgrond.$B$BThisalee Alacorvina ti raggiungerà con un gruppo esplorativo avanzato.$B$BL\'intera Guarnigione è in massima allerta: dobbiamo essere pronti a intercettare il nemico, se necessario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'koKR', '주둔지 대장정: 각성', '고르그론드, 고르고렉의 추락지에 있는 티살리 크로우와 대화해야 합니다.', '유물을 훔친 전쟁군주 블레이드퓨리는 고르그론드 서쪽의 염전으로 향했습니다.$B$B티살리 크로우가 정찰대를 이끌고 대기하고 있습니다.$B$B주둔지 전체가 긴장 상태입니다. 필요하다면 적을 가로막을 각오도 해야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'ptBR', 'Campanha da Guarnição: Despertar', 'Fale com Tessália Corvina no Repouso de Gorgorek, em Gorgrond.', 'Nós rastreamos a Senhora da Guerra Furiagume e o artefato roubado até os campos salinos de Gorgrond Ocidental.$B$BTessália Corvina encontrará você lá com uma equipe de batedores.$B$BToda a guarnição está m alerta. Precisamos estar preparados para interceptar o inimigo se for necessário.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'ruRU', 'Гарнизонная кампания: пробуждение', 'Поговорите с Тизали Вороной в Покое Горгорека, что в Горгронде.', 'Мы выследили, куда скрылась Ярость Клинка с украденным артефактом – она в соляных полях на западе Горгронда.$B$BТизали Ворона встретит тебя там с отрядом разведчиков.$B$BВесь гарнизон приведен в состояние повышенной боеготовности. Мы должны быть готовы перехватить врага, если потребуется.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'zhCN', '要塞战役：觉醒', '到戈尔隆德的高格里克之眠找希萨莉·黑鸦谈一谈。', '我们一直跟踪刃怒督军和失窃的神器来到了戈尔隆德西部的盐场。$B$B希萨莉·黑鸦和一支侦察先头部队将在那里跟你汇合。$B$B整个要塞现在都处于高度戒备状态。我们必须做好在必要情况下拦截敌人的准备。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36134, 'zhTW', '要塞戰役：喚醒', '在格古隆德的格古瑞克之殞和黎沙莉‧烏鴉交談。', '我們一路追蹤亞茲珈‧刃怒，她帶著偷走的神器到格古隆德西邊的鹽場。$B$B黎沙莉‧烏鴉會和一組前哨斥候跟你在那邊碰面。$B$B整座要塞進入高度警戒。有必要的話，我們得攔截敵人。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'deDE', 'Garnisonskampagne: Erwachen', 'Sprecht mit Choluna in Gorgoreks Ruh in Gorgrond.', 'Wir hab\'n Kriegsfürstin Klingenwut und das gestohl\'ne Titanenartefakt im Norden von Gorgrond entdeckt.$B$BCholuna is\' auch schon da, mit \'nem Spähtrupp.$B$BIch hab schon ma\' die ganze Garnison hier flottgemacht. Trefft Euch mit ihr und fangt den Feind ab, falls nötig.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'esES', 'Campaña de la ciudadela: Despertar', 'Habla con Choluna en el Descanso de Gorgorek, en Gorgrond.', 'Hemos seguido el rah\'tro de la señora de la guerra Furiafilo y del ah\'tefacto robado hah\'ta el norte de Gorgrond$B$BCholuna eh’tá allí con un equipo de exploración.$B$BTengo a toda la ciudadela en aleh’ta máxima. Ve, reúnete con ella e intercepta al enemigo si es necesario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'esMX', 'Campaña de fortaleza: despertar', 'Habla con Choluna en el Descanso de Gorgorek en Gorgrond.', 'Rah\'treamos a la Señora de la Guerra Filofurioh\'o y el artefacto robado al oeh\'te de Gorgrond.$B$BCholuna te encontrará ahí con un grupo explorador.$B$BToda la fortaleza eh\'tá alerta. Vete, encuéntrala e intercepta al enemigo si eh\' neh\'esario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'frFR', 'Campagne du fief : réveil', 'Parlez à Choluna au Repos de Gorgorek en Gorgrond.', 'On a pisté l’seigneur de guerre Lames-Furieuses et l’artéfact volé jusque dans l’nord de Gorgrond.$B$BCholuna va s’y rendre avec une unité de reconnaissance.$B$BJ’ai placé tout l’fief en état d’alerte maximale. Allez la rejoindre et interceptez l’ennemi si nécessaire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'itIT', 'Campagna della Guarnigione: Risveglio', 'Parla con Choluna alla Caduta di Gorgorek nella regione di Gorgrond.', 'Abbiamo trovato la Signora della Guerra Lamafuriosa e il manufatto rubato nella zona settentrionale di Gorgrond.$B$BCholuna sta già sul posto con nu gruppo esplorativo avanzato.$B$BL\'intera Guarnigione nuostr\' sta in massima allerta. Raggiungi Choluna, dobbiamo stare pronti a intercettare il nemico, se necessario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'koKR', '주둔지 대장정: 각성', '고르그론드, 고르고렉의 추락지에 있는 초루나와 대화해야 합니다.', '유물을 훔친 전쟁군주 블레이드퓨리는 고르그론드 북쪽으로 향했다네.$B$B초루나가 거기서 정찰대를 이끌고 대기하고 있다네.$B$B내가 주둔지에 경보를 발동했다네. 가서 초루나를 만나게. 필요하다면 적을 막아 서고.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'ptBR', 'Campanha da Guarnição: Despertar', 'Fale com Choluna no Repouso de Gorgorek, em Gorgrond.', 'A gente rastreou a Senhora da Guerra Furiagume e o artefato roubado até o norte de Gorgrond.$B$BCholuna tá lá com uma equipe de batedores.$B$BA guarnição inteira tá de sobreaviso. Vai lá, encontra ela e, se for preciso, intercepta o inimigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'ruRU', 'Гарнизонная кампания: пробуждение', 'Поговорите с Чолуной в Покое Горгорека, что в Горгронде.', 'Мы выследили, куда скрылась Ярость Клинка с украденным артефактом – она на севере Горгронда.$B$BТам сейчас Чолуна со своими разведчиками.$B$BГарнизон тоже в полной боевой готовности. Ты иди, поговори с ней и перехвати врага, если надо будет.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'zhCN', '要塞战役：觉醒', '到戈尔隆德的高格里克之眠找楚伦娜谈一谈。', '我们一直跟踪刃怒督军和失窃的神器来到了戈尔隆德西部的盐场。$B$B楚伦娜和一支侦察部队已经在那里待命。$B$B整个要塞现在都处于高度戒备状态。快去跟她汇合吧，我们必须做好在必要情况下拦截敌人的准备。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36136, 'zhTW', '要塞戰役：喚醒', '回到格古隆德的格古瑞克之殞找克魯娜。', '我們一路追蹤亞茲珈‧刃怒，她帶著偷走的神器到了格古隆德的北邊。$B$B克魯娜會和斥候部隊跟你在那邊碰面。$B$B我會下令整座要塞進入高度警戒狀態。趕快，去找克魯娜，必要的時候我們得攔截敵人。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'deDE', 'Leichter Einstieg in die Holzfällerei', 'Besorgt eine Holzprobe vom kleinen Baum und bringt sie zu Holzherr Oktron.', 'Wir werden viel Holz brauchen, wenn wir diese Garnison zu einer geeigneten Festung ausbauen wollen. Das heißt, wir haben alle Hände voll zu tun.$B$BIch würde Euch nie bitten, Euch die Hände mit gewöhnlicher Arbeit schmutzig zu machen, aber Ihr müsst nach Holz für unser Sägewerk suchen. Sobald Ihr einen Baum markiert habt, wird irgendein kräftiger, fauler, nichtsnutziger Peon ihn fällen und Euch das Holz geben.$B$BFrostfeuer ist nicht der beste Ort für Bauholz, aber immerhin habe ich in der Nähe einen passenden kleinen Baum gesehen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'esES', 'El buen leñador', 'Consigue una muestra de madera de una madera pequeña y llévasela al señor leñador Oktron.', '$GSeñor:Señora;, necesitaremos mucha leña si queremos convertir esta ciudadela en una fortaleza decente. Eso significa que habrá que trabajárselo.$B$BAunque, desde luego, no te pediría que te ensuciaras las manos con trabajo manual, necesito que salgas a por madera para nuestro aserradero. En cuanto señales un árbol, vendrá uno de esos peones gandules a talarlo y te dará la leña.$B$BFuego Glacial no es el mejor sitio para encontrar madera, pero he visto un árbol adecuado aquí cerca.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'esMX', 'Empezando a talar de a poquito', 'Cosecha una muestra de madera de un madero pequeño y devuélveselo al señor leñador Oktron.', '$G Señor:Señora;, vamos a necesitar mucha madera para construir esta fortaleza con una estructura decente. Eso significa que habrá que trabajar un poco.$B$BAunque jamás te pediría que te ensucies con trabajo manual, necesito que salgas a encontrar madera para nuestro molino. Cuando marques un árbol, un peón bueno para nada pero en buen estado físico irá y lo talará para darte la madera.$B$BPirofrío no es el mejor lugar para encontrar madera, pero vi un árbol chiquito por acá cerca.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'frFR', 'Une vocation de bûcheron', 'Récupérez un Échantillon de bois sur un petit arbre et apportez-le au Seigneur du bois Oktron.', '$GMessire:Madame;, il va nous falloir du bois en grande quantité pour faire de ce fief une vraie forteresse. Nous allons donc avoir un peu de travail.$B$BJe ne me permettrais jamais de vous demander de vous salir les mains avec de basses besognes, mais vous me rendriez service en allant repérer du bois pour la scierie. Marquez un arbre, et un flemmard de péon bon à rien mais costaud viendra l’abattre avant de vous laisser le bois.$B$BGivrefeu n’est peut-être pas l’endroit idéal pour se fournir en bois, mais je pense bien avoir aperçu un petit arbre qui pourrait tout à fait convenir, non loin d’ici.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'itIT', 'Primi passi da boscaiolo', 'Raccogli un Pezzo di Tronco da un Tronco Piccolo e riportalo al Signore del Legno Oktron.', '$gSignore:Signora;, avremo bisogno di molta legna se vogliamo che questa Guarnigione diventi una fortezza degna di tal nome. Ciò significa che dovremo darci da fare.$B$BNon oserei mai chiederti di sporcarti le mani, ma ho bisogno che tu vada là fuori a cercare della legna adatta ai nostri scopi. Una volta segnalati gli alberi da abbattere, i nostri Peoni buoni a nulla penseranno al resto.$B$BFuocogelo non è certo il luogo migliore dove trovare della legna, ma ho visto qualche alberello nelle vicinanze che potrebbe fare al caso nostro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'koKR', '벌목하는 법 익히기', '작은 나무에서 목재 견본을 구해 목재 군주 오크트론에게 가지고 가야 합니다.', '이 주둔지를 괜찮은 요새로 만들려면 나무가 많이 필요하오. 그러니까 일을 좀 해야 한다는 말이오.$B$B사령관한테 직접 고된 일까지 시킬 생각은 없다오. 그저 밖으로 나가 제재소에 필요한 목재를 찾아주시오. 적당한 나무를 찾아서 표시를 해두면, 게으르고 아무 짝에도 쓸모없는 일꾼 한 놈이 나타날 거요. 녀석이 나무를 베고, 당신에게 목재를 건네줄 거요.$B$B서리불꽃은 목재를 찾기 좋은 곳은 아니지만, 마침 근처에 적당한 나무가 있는 걸 봐뒀소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'ptBR', 'Pegando leve no serviço de serraria', 'Extraia uma Amostra de Lenho do Lenho Pequeno e entregue-a ao Mestre-de-Madeira Oktron.', '$gSenhor:Senhora;, vamos precisar de muita madeira para transformar essa guarnição em uma fortaleza decente. Isso significa que vamos ter um pouco de trabalho.$B$BNão vou pedir que você faça o trabalho sujo com suas próprias mãos, mas preciso que você pelo menos encontre lenho. Assim que você tiver marcado uma árvore, um peão saudável e vida-mansa virá cortá-la e lhe entregará o lenho.$B$BFogofrio não é a melhor área para conseguir lenho, mas eu vi uma pequena árvore aqui perto que deve servir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'ruRU', 'Рубка леса для чайников', 'Найдите образец древесины небольшого дерева и принесите старшему лесозаготовщику Октрону.', 'Командир, нам потребуется много древесины, чтобы превратить этот гарнизон в надежную крепость. А значит, нам придется немного поработать.$b$bСамо собой, я не могу просить тебя заниматься физическим трудом, но все же необходимо, чтобы ты $gнашел:нашла; древесину для нашей лесопилки. Когда ты отметишь дерево, какой-нибудь бестолковый крепкий батрак срубит его и передаст тебе древесину.$b$bХребет Ледяного Огня не самое лучшее место для ее добычи, но я видел неподалеку подходящее небольшое дерево.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'zhCN', '伐木工入门', '从小型木料上收集木材样本，并交给木材管理员奥克顿。', '$G先生:女士;，如果我们想把这座要塞打造成合格的堡垒，就需要用到一大堆木料。那意味着我们得做些工作才行。$B$B当然，我是肯定不会麻烦你去干脏活累活的，可我需要你去帮我们的伐木场寻找木料。等你标记好一棵树之后，就会有一个身强体壮却又好吃懒做的苦工前去砍伐，把木料交给你。$B$B霜火岭并不是个盛产木材的地方，可我在附近发现了一棵还算不错的小树。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36137, 'zhTW', '輕鬆伐木', '從小型樹木上採集木料樣本，然後向木料大王歐克特朗回報。', '$G先生:女士;，如果想將這座要塞建造成合適的堡壘，我們需要大量木料。這表示我們有些工作要做。$B$B雖然我不打算讓你做粗重工作，但還是需要你外出為我們的鋸木廠尋找木料。你在一棵樹上做記號後，就會有好手好腳但懶得要命的苦工來砍樹，然後把木料給你。$B$B霜火峰並不是生產木料的地方，但我的確看到附近有適合的小型樹木。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'deDE', 'Mit Holz Kohle machen', 'Sammelt 10 Stücke Holz in ganz Draenor und erteilt dann dem Sägewerk Eurer Garnison einen Arbeitsauftrag.', 'Jetzt, da Ihr wisst, wie man die Peons für die Holzernte an die Arbeit schickt, können wir einen Arbeitsauftrag für Euer Sägewerk erstellen.$B$BDoch zunächst brauchen wir Holz, das wir verarbeiten können!$B$BÜberall in Draenor gibt es saftige Bäume, die zum Fällen reif sind. Geht hinaus und findet passendes Holz für das Sägewerk. Kommt hierher zurück, wenn Ihr genug habt und wir beginnen mit einem Arbeitsauftrag.$B$BUnd lasst wie immer die Peons die harte Arbeit tun. Ihr müsst sie nur beschützen. Draenor ist ein gefährlicher Ort!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'esES', 'Convertir madera en beneficios', 'Recoge 10 trozos de madera de los alrededores de Draenor y encarga un pedido en el aserradero de tu ciudadela.', 'Ahora que ya sabes cómo hacer que los peones se pongan a recolectar madera, vamos a crear un pedido para tu aserradero.$B$B¡Pero antes necesitaremos madera!$B$BTodo Draenor está lleno de jugosos árboles listos para la tala. Ve a buscar madera adecuada para el aserradero. Vuelve aquí cuando ya tengas bastante y nos pondremos a procesar un pedido.$B$BY, como siempre, deja que sean los peones quienes hagan el trabajo duro. Tú limítate a protegerlos. ¡Draenor es un lugar peligroso!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'esMX', 'De madera a dinero', 'Reúne 10 piezas de madera de los alrededores de Draenor y luego haz un pedido en el aserradero de tu fortaleza.', 'Ahora que sabes cómo mandar a los peones a recolectar madera, haremos un pedido para tu aserradero.$B$BPero primero, vamos a necesitar algo de madera para procesar.$B$BPor todo Draenor hay jugosos árboles listos para talar. Sal y encuentra la madera apropiada para el aserradero. Vuelve cuando tengas suficiente y pondremos manos a la obra en la orden de trabajo.$B$BComo de costumbre, deja que los peones hagan el trabajo duro. Solo mantenlos a salvo. ¡Draenor es un lugar peligroso!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'frFR', 'Changer le bois en bénéfices', 'Récupérez 10 tronçons de Bois en Draenor puis passez une commande à la Scierie de votre Fief.', 'Maintenant que vous savez comment assigner des péons au ramassage du bois, nous allons passer une commande à votre scierie.$B$BMais pour commencer, il va nous falloir quelque chose à scier !$B$BDraenor regorge d’arbres splendides qui ne demandent qu’à être abattus. Allez donc chercher du bon bois pour la scierie. Revenez quand vous en aurez suffisamment et nous nous intéresserons à cette commande.$B$BEt comme toujours, laissez le boulot ingrat aux péons, mais pensez à les protéger. Draenor est un endroit dangereux !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'itIT', 'Dalla legna al profitto', 'Recupera 10 Pezzi di Tronco dagli alberi di Draenor, quindi assegna un Incarico di Lavoro alla Segheria della tua Guarnigione.', 'Ora che sai come gestire i Peoni, dobbiamo assegnare degli incarichi di lavoro alla nostra Segheria.$B$BPer prima cosa ci servirà altra legna da processare!$B$BSparsi per tutta Draenor ci sono dei rigogliosi alberi che attendono solo di essere abbattuti. Trova della legna di qualità per la nostra Segheria e, quando ne avrai abbastanza, riportala qui per dare inizio a un incarico di lavoro.$B$BCome sempre, lascia che siano i Peoni a occuparsi del lavoro sporco, tu pensa soltanto a difenderli; Draenor è un luogo pericoloso!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'koKR', '목재로 돈 버는 방법', '드레노어에서 목재 10개를 구하고 주둔지 제재소에 제품을 주문해야 합니다.', '이제 일꾼을 시켜 목재를 구하는 법을 익혔으니, 제재소에 제품을 주문할 수 있소.$B$B하지만 생산을 하려면 역시 또 목재가 필요하지 않겠소?!$B$B벌목하기에 적당한 나무가 드레노어 곳곳에 있소. 가서 제재소에서 쓸만한 목재를 찾아 보시오. 충분한 양을 모아 오면 작업을 시작하리다.$B$B평상시처럼 힘든 일은 일꾼을 시키시오. 그들을 안전하게 지켜주기만 하면 되오. 드레노어는 위험한 곳이니까 말이오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'ptBR', 'Transformando madeira em lucro', 'Colete 10 pedaços de Lenho em Draenor, depois Coloque uma Ordem de Serviço na Serraria da sua Guarnição.', 'Agora que você sabe como colocar os peões para trabalhar na coleta de lenho, vamos criar uma ordem de serviço para a sua serraria.$B$BPrimeiro, vamos precisar de lenho para processar!$B$BEm toda Draenor há árvores prontinhas para serem derrubadas. Vá procurar lenho adequado para sua serraria. Volte quando tiver o suficiente e daremos início a uma ordem de serviço.$B$BComo sempre, deixe os lenhadores fazerem o trabalho pesado. Basta protegê-los. Draenor é um lugar perigoso!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'ruRU', 'Золотое дерево', 'Соберите 10 связок древесины со всего Дренора и обеспечьте работой лесопилку своего гарнизона.', 'Теперь ты знаешь, как заставить батраков собирать древесину, так что перейдем к заказу для твоей лесопилки.$b$bНо сначала нам понадобится немного дерева для обработки!$b$bПо всему Дренору растут деревья, которые только и ждут, чтобы их срубили. Найди подходящую древесину для нашей лесопилки. Возвращайся, когда достаточно соберешь, и мы приступим к работе над заказом.$b$bКак обычно, тяжелую работу оставь батракам. Только защищай их, все-таки на Дреноре опасно.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'zhCN', '化木材为利润', '从德拉诺各地收集10份木料，然后在你要塞的伐木场里下达一份产品订单。', '既然你已经知道如何让苦工们去砍伐木料了，我们接下来要为你的伐木场创建一份产品订单。$B$B但首先，我们得找些可供加工的木料！$B$B德拉诺到处都盛产着可供砍伐的高大树木。去为我们的伐木场找些合适的木料。等找到足够之后再回到这里来，我们将开始完成一份产品订单。$B$B老样子，还是把累活留给苦工们做，只要保护好他们就行。德拉诺可是个危险的地方！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36138, 'zhTW', '點木成金', '從德拉諾四周收集10塊木料，然後在你要塞的鋸木廠下訂單。', '現在你知道如何叫苦工去採收木料了，接著我們要開始在你的鋸木廠下訂單。$B$B但首先我們需要木料才能進行！$B$B德拉諾到處都是成熟可供採伐的上好木頭。去尋找適合鋸木廠處理的木頭，取得足夠的木料後就回來，我們就可開始執行訂單了。$B$B如同平常一樣，把砍樹的工作交給苦工，不過記得好好保護他們，德拉諾是很危險的！', '', '', '', '', '', '', 0);
