INSERT INTO `quest_template_locale` VALUES (36185, 'deDE', 'Gerechter Handel', 'Garaal erwartet Eure Lieferung.', 'In Ordnung, bitte sehr!$B$BBringt sie schnell zu Garaal. Er hat ein großes Interesse an detailreichen und feinen Handwerksstücken. Er wird Eure Arbeit sicher zu schätzen wissen.', '', '', '', '', '', 'Bringt die Waren zu Garaal in Embaari.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'esES', 'Un trato justo', 'Garaal aguarda tu entrega.', 'Bien. Aquí tienes.$B$BProcura llevárselos rápidamente a Garaal. Está muy interesado en los detalles y en la artesanía de calidad. Seguro que sabrá apreciar tu trabajo.', '', '', '', '', '', 'Entrega los productos a Garaal en la Aldea Embaari.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'esMX', 'Cambio justo', 'Garaal espera tu entrega.', 'Muy bien. Aquí tienes.$B$BAsegúrate de entregárselos a Garaal rápido. Tiene un gran interés en el detalle y en la artesanía fina. De seguro apreciará tu trabajo.', '', '', '', '', '', 'Entrégale los objetos a Garaal en la Aldea Embaari.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'frFR', 'Un marché honnête', 'Garaal attend votre livraison.', 'Très bien. Voilà pour vous.$B$BRemettez-les à Garaal sans attendre. L’artisanat raffiné l’intéresse au plus haut point. Il appréciera sans aucun doute votre travail.', '', '', '', '', '', 'Allez remettre les marchandises à Garaal au Village d’Embaari.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'itIT', 'Uno scambio equo', '<Garaal è in attesa della tua consegna.>', 'Perfetto, ecco fatto.$B$BAssicurati di consegnare tutto a Garaal il prima possibile, è sempre interessato a prodotti di alta qualità. Apprezzerà molto il tuo lavoro.', '', '', '', '', '', 'Consegna i prodotti a Garaal a Embaari.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'koKR', '공정한 거래', '가랄에게 물건을 배달해야 합니다.', '자, 다 됐어요.$B$B어서 가랄에게 이걸 갖다주세요. 그는 잘 손질된 품질 좋은 재료에 관심이 많죠. 당신의 수고에 대단히 고마워할 거예요.', '', '', '', '', '', '엠바리 마을에 있는 가랄에게 물건을 배달해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'ptBR', 'Negócio justo', 'Garaal espera pela entrega.', 'Certo. Aqui está.$B$BLembre-se de entregar rapidamente a Garaal. Ele se interessa sobremaneira por detalhes e refinamento. Certamente teu trabalho vai agradá-lo.', '', '', '', '', '', 'Entregue as mercadorias para Garaal na Aldeia Embaari.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'ruRU', 'Честная сделка', 'Гараал ждет вас.', 'Ну ладно. Держи.$b$bСкорее отнеси все Гараалу. Он любит мелкие детали и тонкую работу. Так что ты ему точно понравишься.', '', '', '', '', '', 'Отнесите товары Гараалу в деревню Эмбаари.', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'zhCN', '公平交易', '加莱尔在等你送货。', '好了，搞定。$B$B一定要尽快把它们交给加莱尔。他对细节和精湛的做工有着孜孜不倦的追求。你的作品肯定会得到他的赞许。', '', '', '', '', '', '把商品交给安波里村的加莱尔。', 0);
INSERT INTO `quest_template_locale` VALUES (36185, 'zhTW', '公平交易', '賈拉爾正等著你的貨物。', '好了，來。$B$B快點把貨物交給賈拉爾。他一向對細緻的手工藝有很大的興趣，一定會喜歡你的東西。', '', '', '', '', '', '將貨物交給安伯瑞村的賈拉爾。', 0);
