INSERT INTO `quest_template_locale` VALUES (36248, 'deDE', 'Ein gestohlenes Herz', 'Besiegt Azuka Klingenwut.', 'Dieser Überfall ist ein offensichtlicher Versuch, das Herz von Gorgorek zu ergreifen.$b$bWir müssen unsere Wachen verstärken, bevor es zu spät ist!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'esES', 'Un corazón robado', 'Derrota a Azuka Furiafilo.', 'Está claro que esta invasión responde a un intento de hacerse con el corazón de Gorgorek.$b$b¡Tenemos que aumentar la guardia antes de que sea demasiado tarde!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'esMX', 'Un corazón robado', 'Derrota a Azuka Filofurioso.', 'Esta invasión claramente es un intento de apoderarse del Corazón de Gorgorek.$b$b¡Debemos mejorar nuestra guardia antes de que sea demasiado tarde!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'frFR', 'Le cœur dérobé', 'Triomphez d’Azuka Lames-Furieuses.', 'Cette invasion est manifestement une tentative pour s’emparer du cœur de Gorgorek.$b$bNous devons renforcer la garde avant qu’il soit trop tard !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'itIT', 'Il cuore rubato', 'Sconfiggi Azuka Lamafuriosa.', 'Questa invasione è chiaramente un tentativo di mettere le mani sul Cuore di Gorgorek.$b$bDobbiamo fortificare le nostre difese prima che sia troppo tardi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'koKR', '훔친 심장', '아즈카 블레이드퓨리를 꺾어야 합니다.', '이 공격의 목적은 고르고렉의 심장인 것이 분명하네.$b$b너무 늦기 전에 경비를 강화해야 하네!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'ptBR', 'Um coração roubado', 'Derrote Azuka Furiagume.', 'A invasão claramente tinha o objetivo de conquistar o Coração de Gorgorek.$b$bTemos que fortalecer a guarda antes que seja tarde demais!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'ruRU', 'Украденное сердце', 'Сразите Азуку Ярость Клинка.', 'Целью этого нападения явно было захватить сердце Горгорека.$b$bНужно усилить охрану, пока еще не поздно.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'zhCN', '被偷走的混沌之心', '击败阿祖卡·刃怒。', '这次入侵的目的显然是夺取高格里克之心。$b$b我们得尽快加固防线，否则就迟了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36248, 'zhTW', '被偷走的心臟', '擊敗亞茲珈‧刃怒。', '這場侵略行動的目標明顯是針對格古瑞克之心而來。$b$b我們必須加強防禦，不然後果不堪設想！', '', '', '', '', '', '', 0);
