INSERT INTO `quest_template_locale` VALUES (36376, 'deDE', 'Goreninvasion!', 'Schlagt die Goreninvasion zurück.$B$B(Sprecht erneut mit Unteroffizier Krähler, um zu beginnen.)', 'Die Garnison wird von Erdbeben erschüttert, und unsere Späher sind schon seit Stunden überfällig.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'esES', '¡Invasión de los goren!', 'Repele la invasión de los goren de tu ciudadela.$B$B(Habla de nuevo con el Sargento Crowler para empezar).', 'Ha habido terremotos en la ciudadela, y hace horas que no sabemos de nuestros exploradores.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'esMX', '¡Invasión goren!', 'Repele la invasión goren.$B$B(Habla con el sargento Crowler de nuevo para comenzar)', 'Los temblores han estado sacudiendo la fortaleza y nuestros exploradores han dejado de enviar informes hace horas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'frFR', 'Invasion de gorens !', 'Repoussez l’invasion des gorens.$B$B(Parlez de nouveau au Sergent Crowler pour commencer.)', 'Des séismes secouent le fief, et nos éclaireurs n’ont pas donné de nouvelles depuis des heures.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'itIT', 'Invasione dei Goren!', 'Respingi l\'invasione dei Goren nella tua Guarnigione.$B$BParla ancora con il Sergente Crowler per iniziare.', 'Dei terremoti stanno facendo tremare la Guarnigione, e i nostri esploratori non inviano rapporti da ore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'koKR', '고렌의 침략!', '고렌의 침략으로부터 주둔지를 방어해야 합니다.$B$B(하사관 크로울러와 다시 대화하면 시작됩니다.)', '지진이 계속되며 주둔지를 흔드는군요. 게다가 정찰병들의 보고를 받을 때가 몇 시간이나 지났습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'ptBR', 'Invasão Goren!', 'Rechace a invasão de gorens.$B$B(Fale com o Sargento Rastejo novamente para começar)', 'Tremores têm sacudido a guarnição, e há horas nossas batedores não dão sinal.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'ruRU', 'Нападение горенов!', 'Отразите нападение горенов.$B$B(Чтобы начать, еще раз поговорите с сержантом Краулером.)', 'У нас тут было землетрясение, гарнизон тряхануло пару раз. Да еще несколько разведчиков с задания не вернулось.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'zhCN', '鬣蜥人入侵！', '击退鬣蜥人的入侵。$B$B（再次与克劳勒中士交谈来开始行动）', '要塞下方的大地一直在震颤，而我们的斥候也已经好几个小时没来回报了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36376, 'zhTW', '戈倫入侵！', '擊退侵略要塞的戈倫。$B$B(再次與克羅勒士官談話即可開始)', '要塞不斷發生地震，我們的斥候也好幾個小時沒有回報。', '', '', '', '', '', '', 0);
