INSERT INTO `quest_template_locale` VALUES (36425, 'frFR', 'Omelette d’œufs de ravageurs', 'Détruisez à coups de pied $1oa Œufs de ravageurs au Camp d’Écorche-Balafre.', '<Un œuf suintant et agité de spasmes. Il est sans doute plein de larves.$B$BUn bon coup de pied devrait régler le problème.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36425, 'itIT', 'Calcio alle uova', 'Prendi a calci $1oa Uova di Devastatore nell\'Accampamento Sfregiaspina.', '<Vedi un uovo umido e pulsante, sicuramente pieno di larve di devastatore.>$B$B<Un calcio ben assestato dovrebbe bastare per risolvere il problema.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36425, 'koKR', '알을 뻥!', '가시흉터 야영지에 있는 칼날발톱 알 $1oa개를 걷어차야 합니다.', '<축축하게 젖어 고동치는 알입니다. 칼날발톱 유충이 들어 있는 게 분명합니다.$B$B한번 뻥 차 주면 처리할 수 있을 것 같습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36425, 'ptBR', 'Pisando em ovos', 'Chute $1oa Ovos de Assolador no Acampamento de Rasgante.', '<Um ovo pulsante, repleto de larvas de assolador.$B$BUm chute bem dado vai dar conta do recado.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36425, 'ruRU', 'Яйцебой', 'Разбейте $1oa |4яйцо:яйца:яиц; опустошителя в лагере Тернистого Шрама.', '<Влажное пульсирующее яйцо опустошителя, несомненно, содержащее зародыш.$B$BБыстрый удар ноги решит проблему.>', '', '', '', '', '', '', 0);
