INSERT INTO `quest_template_locale` VALUES (36477, 'esES', 'Promesas rotas', 'Busca en la Feria de la Luna Negra a alguien que pueda estar interesado en el anillo.', 'Un anillo muy rayado, ligeramente manchado de sangre.$B$BTal vez en la Feria de la Luna Negra te puedan aportar más información al respecto.', '', '', '', '', '', '', 0);
