INSERT INTO `quest_template_locale` VALUES (36477, 'frFR', 'Promesses rompues', 'Trouvez quelqu’un que l’anneau intéresse à la Foire de Sombrelune.', 'Un anneau couvert de rayures, vaguement taché de sang.$B$BQuelqu’un à la foire de Sombrelune pourrait en savoir plus à son sujet.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36477, 'itIT', 'Promesse infrante', 'Trova qualcuno alla Fiera di Lunacupa che potrebbe essere interessato a questo anello.', '<Un anello fortemente graffiato, leggermente macchiato di sangue.>$B$B<Qualcuno alla Fiera di Lunacupa potrebbe essere in grado di darti ulteriori informazioni su questo oggetto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36477, 'koKR', '깨어진 약속', '다크문 축제에서 이 반지에 흥미를 가진 사람을 찾아야 합니다.', '반지에 심한 흠집이 있고 희미한 핏자국이 남아 있습니다.$B$B다크문 축제에 있는 사람 중 누군가가 이 반지에 대한 정보를 알고 있을지도 모릅니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36477, 'ptBR', 'Promessas quebradas', 'Encontre alguém na Feira de Negraluna que possa se interessar pelo anel.', 'Um anel muito arranhado com uma leve mancha de sangue.$B$BPode ser que alguém da Feira de Negraluna possa dizer alguma coisa sobre este item.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36477, 'ruRU', 'Нарушенные обещания', 'Найдите кого-нибудь на ярмарке Новолуния, кого бы заинтересовало это кольцо.', 'Сильно поцарапанное кольцо, чуть запятнанное кровью.$B$BВозможно, кто-нибудь на ярмарке Новолуния знает об этой вещи больше.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36477, 'zhCN', '破碎誓言', '在暗月马戏团里寻找对指环感兴趣的人。', '一枚磨损严重的指环，表面还沾了几丝血迹。$B$B暗月马戏团里也许有人知道关于这样物品的更多信息。', '', '', '', '', '', '', 0);
