INSERT INTO `quest_template_locale` VALUES (36501, 'ruRU', 'Нападение Железной Орды', 'Стрелок Арефеон', 'По приказу его высочества короля Вариана Ринна, все способные держать в руках оружие граждане должны прибыть к воздаятелю Марааду на Расколотый береговой плацдарм в Выжженных землях.$B$BДобраться до пристани у крепости Северной Стражи можно, взяв в Пиратской бухте лодку до Кабестана и пройдя по побережью на юг.$B$BВо славу и за честь Альянса!', '', '', '', '', '', 'Поговорите с воздаятелем Мараадом в xxxx, что в Выжженных землях.', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'deDE', 'Ruhmestrophäe: Biolante', 'Bringt die Trophäe zu Maraad am Hochpass in Gorgrond.', 'Der Sieg über Biolante hat Euch eine Trophäe eingebracht, die es würdig ist, in Eurem Außenposten zur Schau gestellt zu werden. $b$bBringt sie zu Maraad, um Euren Ruhm zu mehren!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'esES', 'Trofeo de la gloria: Biolante', 'Llévale el trofeo a Maraad en Paso Alto, en Gorgrond.', 'Derrotar a Biolante te ha hecho ganar un trofeo digno de ser mostrado en tu avanzada.$b$b¡Devuélvele esto a Maraad para que tu gloria sea aún mayor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'esMX', 'Trofeo de la gloria: Biolante', 'Llévale el trofeo a Maraad en el Paso Alto, Gorgrond.', 'Derrotar a Biolante te dejó un trofeo digno de alardeo en tu puesto de avanzada.$b$b¡Devuélvele esto a Maraad para aumentar tu gloria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'frFR', 'Trophée glorieux : Biolante', 'Apportez le trophée à Maraad à la Haute-Route, en Gorgrond.', 'En triomphant de Biolante, vous avez obtenu un trophée digne d’être exhibé dans votre avant-poste.$b$bApportez-le à Maraad pour vous couvrir de gloire !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'itIT', 'Trofeo della gloria: Biolante', 'Porta il trofeo a Maraad presso Altopasso nella regione di Gorgrond.', 'Con la vittoria su Biolante ti sei $gaggiudicato:aggiudicata; un trofeo di grande valore da sfoggiare con orgoglio al tuo avamposto.$b$bRiportalo a Maraad per incrementare la tua gloria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'koKR', '영광의 전리품: 바이올란테', '고르그론드의 오름길에 있는 마라아드에게 전리품을 가져가야 합니다.', '바이올란테를 처치한 후 자부심을 느낄 만한 전리품을 손에 넣었습니다.$b$b오름길에 있는 마라아드에게 이걸 가져가십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'ptBR', 'Troféu do Triunfo: Biolante', 'Leve o troféu a Maraad, que está no Passoalto, em Gorgrond.', 'Derrotar Biolante lhe rendeu um troféu digno de aclamação.$b$bLeve isto a Maraad em Passoalto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'ruRU', 'Почетный трофей: Биожор', 'Отнесите трофей Марааду на Верхний Путь в Горгронде.', 'Вы победили Биожора и завоевали трофей, которым не стыдно похвалиться в своем лагере. $b$bОтнесите трофей Марааду – это прославит вас еще больше!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'zhCN', '荣耀的证明：拜奥兰特', '把战利品带到戈尔隆德的高山走廊，交给玛尔拉德。', '击败拜奥兰特后，你获得了一件值得去岗哨炫耀的战利品。$b$b把它交还给玛尔拉德，来提升你的荣耀！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36502, 'zhTW', '光榮戰利品：拜歐蘭特', '帶著戰利品到格古隆德的高地之路找瑪銳德。', '擊敗拜歐蘭特讓你獲得一件值得在哨站炫耀的戰利品。$b$b將戰利品交給瑪銳德以增加你的榮譽！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36503, 'deDE', 'Ruhmestrophäe: Biolante', 'Bringt die Trophäe zu Durotan in der Wildwacht in Gorgrond.', 'Der Sieg über Biolante hat Euch eine Trophäe eingetragen, die der Anerkennung würdig ist.$B$BBringt dies zu Durotan in der Wildwacht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36503, 'esES', 'Trofeo de la gloria: Biolante', 'Llévale el trofeo a Durotan en Avanzada de las Bestias, en Gorgrond.', 'Derrotar a Biolante te ha hecho ganar un trofeo digno de reconocimiento.$b$bLleva esto a Durotan en Avanzada de las Bestias.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36503, 'esMX', 'Trofeo de la gloria: Biolante', 'Llévale el trofeo a Durotan en el Observatorio de Bestias, Gorgrond.', 'Derrotar a Biolante te dejó un trofeo digno de aclamación.$b$bLlévale esto a Durotan en el Observatorio de Bestias.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36503, 'frFR', 'Trophée glorieux : Biolante', 'Apportez le trophée à Durotan au Guet des Bêtes, en Gorgrond.', 'En triomphant de Biolante, vous avez obtenu un trophée prouvant votre valeur.$b$bApportez-le à Durotan, au guet des Bêtes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36503, 'itIT', 'Trofeo della gloria: Biolante', 'Porta il trofeo a Durotan presso Roccabestia nella regione di Gorgrond.', 'Con la vittoria su Biolante ti sei $gaggiudicato:aggiudicata; un trofeo di grande valore.$b$bConsegnalo a Durotan a Roccabestia.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36503, 'koKR', '영광의 전리품: 바이올란테', '고르그론드의 야수 감시터에 있는 듀로탄에게 전리품을 가져가야 합니다.', '바이올란테를 처치한 후 자부심을 느낄 만한 전리품을 손에 넣었습니다.$b$b야수 감시터에 있는 듀로탄에게 이걸 가져가십시오.', '', '', '', '', '', '', 0);
