INSERT INTO `quest_template_locale` VALUES (36503, 'ruRU', 'Почетный трофей: Биожор', 'Отнесите трофей Дуротану на Звериную заставу в Горгронде.', 'Вы победили Биожора и завоевали трофей, которым не стыдно похвалиться в своем лагере. $b$bОтнесите трофей Дуротану на Звериную заставу.', '', '', '', '', '', '', 0);
