INSERT INTO `quest_template_locale` VALUES (36508, 'deDE', 'Gehört der zu Euch?', 'Sprecht mit Rexxar und bürgt für Rangari Erdanii.', 'Die hab ich in der Nähe herumlungernd gefunden. Sie behauptet, eine Verbündete von Euch zu sein.$b$bIst das wahr? Oder soll ich sie langsam über offenem Feuer kochen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'esES', '¿Es de los tuyos?', 'Habla con Rexxar y responde por la Rangari Erdanii.', 'La he encontrado merodeando por aquí cerca. Dice ser una aliada tuya.$b$b¿Es eso cierto? ¿O va a cocerse lentamente en mi fuego?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'esMX', '¿Este es uno de los tuyos?', 'Habla con Rexxar y responde por Rangari Erdanii.', 'Encontré a esta merodeando por aquí cerca. Dice ser tu aliada.$b$b¿Es cierto? ¿O la pongo a cocinar a fuego lento?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'frFR', 'Ça vient de chez vous ?', 'Parlez à Rexxar et disculpez la Rangari Erdanii.', 'J’ai trouvé cette fille en train de rôder dans les parages. Elle prétend faire partie de vos alliés.$b$bEst-ce exact, ou dois-je commencer à la cuisiner lentement à ma manière ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'itIT', 'È una tua conoscenza?', 'Parla con Rexxar e garantisci per la Guardaselve Erdanii.', 'Ho trovato questa nascosta nelle vicinanze, dice di essere una dei tuoi.$b$bÈ vero? Altrimenti finirà dritta dritta a fuoco lento sul mio falò.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'koKR', '자네 일행인가?', '렉사르와 대화해서 랑가리 에르다니의 신원을 보증해야 합니다.', '이자가 근처에 숨어 있는 걸 발견했네. 자네 동맹이라더군.$b$b그게 사실인가? 아니면 이자를 화톳불 위에 매달아도 되겠나?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'ptBR', 'É um dos seus?', 'Fale com Rexxar em favor da Rangari Erdanii.', 'Encontramos esta aqui espreitando. Ela diz ser sua aliada.$B$BÉ verdade? Ou posso cozinhar ela em fogo brando?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'ruRU', 'Это кто-то из твоих?', 'Поговорите с Рексаром и поручитесь за рангари Эрдани.', 'Она пряталась в кустах. Говорит, что твой союзник.$b$bЭто правда? Или мне зажарить ее на медленном огне?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'zhCN', '这是你的同伴吗？', '与雷克萨交谈，并为游侠艾达妮做担保。', '我发现这家伙躲在附近。她号称是你的同伴。$b$b这是真的吗？还是说她只是在我的篝火附近炖东西吃？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36508, 'zhTW', '是你的人嗎？', '與雷克薩談話，為遊俠厄達妮擔保。', '我找到這個在附近躲躲藏藏的人。她說她是你的盟友。$b$b這是真的嗎？還是要我在火上慢慢的把她的肉烤熟？', '', '', '', '', '', '', 0);
