INSERT INTO `quest_template_locale` VALUES (36602, 'ruRU', 'Новости из Награнда', 'Поговорите с Рокханом в своем гарнизоне на хребте Ледяного Огня.', 'Пока тебя не было, заходил один солдат. Сказал что-то про караван, направляющийся в Награнд.\n\nВ общем, с ним что-то стряслось. Это же земля клана Песни Войны.', '', '', '', '', '', '', 0);
