INSERT INTO `quest_template_locale` VALUES (36646, 'ptBR', 'Primeira ordem de serviço de engenharia', 'Inicie e colete sua primeira ordem de serviço de engenharia na Ala de Engenharia.', 'Agora que a ala de engenharia está concluída, podemos começar a produzir poeira draeneica. Quando tiver a matéria-prima, fale com meu assistente para iniciar a primeira ordem de serviço.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36646, 'ruRU', 'Первый заказ для инженерной мастерской', 'Сделайте свой первый заказ для инженерной мастерской, затем заберите готовую продукцию.', 'Теперь у тебя есть инженерная мастерская, и мы можем начать производство частей механизмов. Как только добудешь нужные материалы, поговори с моим помощником, и мы начнем работу над первой партией.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36646, 'zhCN', '你的第一份工程学订单', '在工坊开始你的第一份工程学订单，并收集产品。', '既然你已经有了工坊，我们可以开始为你生产弹簧齿轮了。等你找到材料就跟我的助手说一声，开始第一份产品订单吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36646, 'zhTW', '你的第一張工程學訂單', '在工程學作坊開始和點收你的第一張工程學訂單。', '現在工程學作坊已經完工了，我們可以開始為你製作發條零件。當你取得足夠的材料之後，請和我的助手交談，就可以進行第一張訂單。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'deDE', 'Euer erster Arbeitsauftrag für Inschriftenkunde', 'Beginnt Euren ersten Arbeitsauftrag für Inschriftenkunde bei der Schreibstube und holt ihn anschließend ab.', 'Jetzt, da Ihr über eine Schreibstube verfügt, können wir Kriegsfarben für Euch fertigen. Sprecht mit meinem Bruder, wenn Ihr die Bestandteile zusammengetragen habt, damit wir mit dem ersten Arbeitsauftrag beginnen können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'esES', 'Tu primer pedido de inscripción', 'Empieza y recoge tu primer pedido de inscripción en las dependencias del escriba.', 'Ahora que las dependencias del escriba están completadas, podemos comenzar a producir pinturas de guerra para ti. Cuando tengas los ingredientes, habla con mi ayudante para iniciar el primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'esMX', 'Tu primer pedido de inscripción', 'Comienza y recoge tu primer pedido de inscripción en el Scriptorium.', 'Ahora que el Scriptorium está listo, podemos comenzar a producir pinturas de guerra para ti. Cuando tengas los ingredientes, habla con mi asistente para empezar el primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'frFR', 'Votre première commande de calligraphie', 'Lancez puis récupérez votre première commande de Calligraphie à l’Étude de scribe.', 'À présent que l’étude de scribe est terminée, nous pouvons commencer à vous fournir des peintures de guerre. Quand vous aurez les ingrédients, allez parler à mon assistant pour lancer la première commande.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'itIT', 'La tua prima produzione di Runografia', 'Avvia la tua prima produzione di Runografia e riscuoti l\'ordine alla fine dei lavori.', 'Ora che hai un Ateneo di Runografia, possiamo iniziare a produrre Colori di Guerra. Quando avrai i materiali necessari, parla con il mio assistente per iniziare il primo incarico di lavoro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'koKR', '첫 번째 주문각인 제품', '주문각인사의 처소에서 첫 번째 제품을 주문하고 생산된 제품을 획득해야 합니다.', '이제 주문각인사의 처소가 완성되었으니, 사령관님을 위해 전쟁 물감을 제작할 수 있습니다. 재료를 다 구한 후 제 조수에게 말씀해 주시면 작업을 시작하겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'ptBR', 'Primeira ordem de serviço de escrivania', 'Inicie e colete sua primeira ordem de serviço de escrivania na Ala do Escriba.', 'Agora que a ala do escriba está concluído, podemos começar a produzir poeira draeneica. Quando tiver a matéria-prima, fale com meu assistente para iniciar a primeira ordem de serviço.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'ruRU', 'Первый заказ для мастерской начертателя', 'Сделайте свой первый заказ для мастерской начертателя, затем заберите готовую продукцию.', 'Теперь у тебя есть мастерская начертателя, и мы можем начать производство красок сражений. Как только добудешь нужные материалы, поговори с моим помощником, и мы начнем работу над первой партией.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'zhCN', '你的第一份铭文订单', '在铭文师之家开始你的第一份铭文订单，并收集产品。', '既然你已经有了铭文师之家，我们可以开始为你生产战争颜料了。等你找到材料就跟我的助手说一声，开始第一份产品订单吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36647, 'zhTW', '你的第一張銘文學訂單', '在雕銘書房開始和點收你的第一張銘文學訂單。', '現在雕銘書房已經完工了，我們可以開始為你製作戰爭顏料。當你取得足夠的材料之後，請和我的助手交談，就可以進行第一張訂單。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'deDE', 'Angriff auf die Steinzornklippen', 'Greift die Oger der Blutschläger in den Steinzornklippen am Frostfeuergrat an.', 'Oger der Blutschläger vom Frostfeuergrat überfallen Handelsrouten überall auf dem Kontinent und zwingen gefangene Soldaten, in ihren Schlackeminen zu arbeiten.\n\nBodrick Grau hat befohlen, verbündete Truppen zu befreien, die als Zwangsarbeiter im Hauptlager der Blutschläger an den Steinzornklippen arbeiten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'esES', 'Asalto a los Acantilados Rocafuria', 'Ataca a los ogros Machacasangre en los Acantilados Rocafuria, en la Cresta Fuego Glacial.', 'Los ogros Machacasangre de la Cresta Fuego Glacial han estado emboscando las rutas comerciales de todo el continente, y a los soldados capturados los ponen a trabajar en sus Minas Machacasangre.\n\nBodrick Grey ha solicitado el rescate de las tropas aliadas capturadas en la Base Machacasangre, en los Acantilados Rocafuria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'esMX', 'Asalto en los Acantilados Furia Pétrea', 'Ataca a los ogros Machacasangre en los Acantilados Furia Pétrea en la Cresta Pirofrío.', 'Los ogros Machacasangre de Cresta Pirofrío han tendido emboscadas en las rutas de comercio en todo el continente y explotan a los soldados secuestrados para trabajar en sus minas de escoria.\n\nBodrick Gris ha pedido el rescate de tropas amigas prisioneras en los dominios Machacasangre en Acantilados Furia Pétrea.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'frFR', 'Assaut sur les falaises Fureur-de-Pierre', 'Attaquez les Ogres de la Masse-Sanglante aux Falaises Fureur-de-Pierre, à la Crête de Givrefeu.', 'Les Ogres de la Masse-Sanglante de la crête de Givrefeu ont tendu des embuscades sur les routes commerciales de tout le continent et font travailler les soldats captifs dans leurs mines de scories.\n\nBodrick Grey a demandé qu’on libère les troupes alliées qui ont été emmenées en captivité à la base principale des Masses-Sanglantes, aux falaises Fureur-de-Pierre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'itIT', 'Assalto alle Rupi di Rocciafurente', 'Assalta gli Ogre Magliorosso alle Rupi di Rocciafurente nella Landa di Fuocogelo.', 'Gli Ogre Magliorosso della Landa di Fuocogelo hanno assalito le nostre rotte commerciali nella regione e costringono i soldati catturati a lavorare nelle loro miniere.\n\nBodrick Grey ha richiesto il salvataggio degli agenti tenuti prigionieri nell\'avamposto primario dei Magliorosso presso le Rupi di Rocciafurente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'koKR', '바위격노 절벽 공격', '서리불꽃 마루의 바위격노 절벽에 있는 피망치 오우거를 공격해야 합니다.', '서리불꽃 마루의 피망치 오우거들이 대륙 전체의 교역로에 매복해 있다가 상단을 공격하고, 사로잡은 병사들을 잿가루 광산의 노예로 전락시키고 있습니다.\n\n보드릭 그레이가 바위격노 절벽의 피망치 소굴에 있는 포로들을 구출해 달라고 요청해 왔습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'ptBR', 'O ataque aos Penhascos Furiapedra', 'Ataque os ogros Malho Sangrento nos Penhascos Furiapedra, na Serra Fogofrio.', 'Os ogros Malho Sangrento da Serra Fogofrio estão fazendo emboscadas nas rotas comerciais de todo o continente e colocando os prisioneiros para trabalhar nas suas minas de escória.\n\nBodrick Gris solicitou o resgate de soldados aliados presos no complexo principal do Malho Sangrento, nos Penhascos Furiapedra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'ruRU', 'Атака на Земли Каменного Неистовства', 'Атакуйте огров из клана Кровавого Молота в землях Каменного Неистовства на хребте Ледяного Огня.', 'Огры из клана Кровавого Молота с хребта Ледяного Огня устраивают засады на торговых путях всего континента и заставляют пленных солдат работать в своих шлаковых шахтах.\n\nБоудрик Грей просит освободить попавших в плен союзников, которых держат в главном лагере клана Кровавого Молота в землях Каменного Неистовства.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'zhCN', '突袭石怒崖', '突袭霜火岭石怒崖的血槌食人魔。', '来自霜火岭的血槌食人魔一直在整个大陆的各条贸易路线上伏击往来商人，还强迫被俘的士兵在他们的炉渣矿井里工作。\n\n伯德里克·格雷下令救出那些被囚禁在石怒崖血槌大本营里的友方军队士兵。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36648, 'zhTW', '進攻石怒崖', '在霜火峰突擊石怒崖的血槌巨魔。', '來自霜火峰的血槌巨魔不斷突襲這片土地的貿易路線，並且把被俘的士兵丟到他們的熔渣礦坑中工作。\n\n伯德里克‧格雷要求你救出石怒崖的血槌主要營地中，被俘的友軍部隊。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'deDE', 'Angriff auf den Hafen von Shattrath', 'Greift die Sargereitruppen in Shattrath in Talador an.', 'Shattrath ist in die Hände der Sargereitruppen gefallen, die von dem verräterischen Exarchen Socrethar angeführt werden.\n\nDie Mitglieder der Sha\'tarverteidigung haben um Unterstützung gegen die Truppen des Schattenrates im Ostteil der Stadt gebeten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'esES', 'Asalto al Puerto de Shattrath', 'Ataca a las tropas Sargerei en la Ciudad de Shattrath, en Talador.', 'La Ciudad de Shattrath está en manos de tropas Sargerei, dirigidas por el exarca traidor, Socrethar.\n\nLos miembros de la Defensa Sha\'tari no han dejado de pedir ayuda contra las tropas del Consejo de la Sombra en las partes orientales de la ciudad.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'esMX', 'Asalto al puerto de Shattrath', 'Ataca a las fuerzas sargerei en la Ciudad de Shattrath, en Talador.', 'La Ciudad de Shattrath está en manos de las fuerzas Sargerei, bajo el mando del traidor Exarca Socrethar.\n\nLos miembros de la Defensa Sha\'tari han solicitado ayuda contra las fuerzas del Consejo de la Sombra en las áreas al este de la ciudad.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'frFR', 'Assaut sur le port de Shattrath', 'Attaquez les Troupes sargereï à Shattrath, en Talador.', 'Shattrath est aux mains de troupes sargereï commandées par Socrethar, l’exarque félon.\n\nLes membres de la défense sha’tari ont demandé de l’aide face aux troupes du Conseil des ombres, dans la partie orientale de la ville.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'itIT', 'Assalto al Porto di Shattrath', 'Assalta le forze dei Sargerei a Shattrath nella regione di Talador.', 'Shattrath è nelle mani dei Sargerei sotto il comando dell\'Esarca traditore, Socrethar.\n\nI membri dei Protettori Sha\'tari hanno richiesto aiuto contro le forze del Concilio dell\'Ombra nell\'area orientale della città.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'koKR', '샤트라스 항구 공격', '탈라도르의 샤트라스에 있는 살게레이 세력을 공격해야 합니다.', '배신자 소크레타르 총독이 이끄는 살게레이 세력의 손에 샤트라스가 점령당했습니다.\n\n샤타리 수호자들이 샤트라스 동부에 있는 어둠의 의회 세력을 몰아내는 것을 도와달라고 요청해 왔습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'ptBR', 'Ataque ao Porto de Shattrath', 'Ataque as forças Sargerei em Shattrath, que fica em Talador.', 'Shattrath está nas mãos das forças Sargerei lideradas por Socrethar, o Exarca traidor.\n\nOs membros da Defesa Sha\'tari estão pedindo auxílio contra as forças do Concílio das Sombras na zona leste da cidade.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'ruRU', 'Атака на гавань Шаттрата', 'Атакуйте саргерайские войска в городе Шаттрате, что в Таладоре.', 'Шаттрат находится под контролем саргерайских войск под предводительством экзарха-предателя Сокретара.\n\nЗащитники Ша\'тар просят помощи в борьбе с силами Совета Теней на востоке города.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'zhCN', '突袭沙塔斯港口', '突袭塔拉多沙塔斯城的萨格雷军队。', '沙塔斯城现在处于萨格雷军队的控制下，他们的首领是大主教索克雷萨那个叛徒。\n\n沙塔尔防御者的成员们请求援助，对抗沙塔斯城东部区域内的暗影议会军队。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36649, 'zhTW', '進攻撒塔斯港', '在塔拉多爾的撒塔斯城，突擊薩格萊的兵力。', '撒塔斯城目前受到背叛主教索奎薩爾所領導的薩格萊兵力控制。\n\n撒塔斯守軍的成員已經提出協助要求，要在東側城市抵抗暗影議會的兵力。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'deDE', 'Ogerinvasion!', 'Schlagt die Invasion Eurer Garnison durch die Oger zurück.$B$B(Sprecht erneut mit Unteroffizier Krähler, um zu beginnen.)', 'Späher melden, dass Ogerrudel durch das Schattenmondtal marschieren. Wie es scheint, wir haben wir Eindruck gemacht. Was meint Ihr, Kommandant?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'esES', '¡Invasión de los ogros!', 'Repele la invasión de los ogros de tu ciudadela.$B$B(Habla de nuevo con el Sargento Crowler para empezar).', 'Nos informan de que hay grupos de ogros avanzando por el Valle Sombraluna. Hemos dejado huella, ¿eh, Comandante?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'esMX', '¡Invasión ogra!', 'Repele la invasión ogra a tu fortaleza.$B$B(Habla con el sargento Crowler de nuevo para comenzar)', 'Los exploradores informan que manadas de ogros marchan a través del Valle Sombraluna. Les dejamos una buena impresión, ¿no, Comandante?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'frFR', 'Invasion d’Ogres !', 'Repoussez l’invasion des Ogres contre votre fief.$B$B(Parlez de nouveau au Sergent Crowler pour commencer.)', 'Nos éclaireurs font état de bandes d’ogres traversant la vallée d’Ombrelune. Nous leur avons fait grosse impression, hein, commandant ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'itIT', 'Invasione degli Ogre!', 'Respingi l\'invasione degli Ogre nella tua Guarnigione.$B$BParla ancora con il Sergente Crowler per iniziare.', 'Gli esploratori ci hanno informato che gruppi di Ogre stanno marciando attraverso la Valle di Torvaluna. Pare si siano ricordati di noi, comandante!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'koKR', '오우거의 침략!', '오우거의 침략으로부터 주둔지를 방어해야 합니다.$B$B(하사관 크로울러와 다시 대화하면 시작됩니다.)', '정찰병들이 보고하길 오우거 무리가 어둠달 골짜기로 진군하고 있다고 합니다. 우리가 녀석들한테 꽤 깊은 인상을 줬나 본데요, 사령관님?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'ptBR', 'Invasão Ôgrica!', 'Rechace a invasão ôgrica à sua guarnição.$B$B(Fale com o Sargento Rastejo novamente para começar)', 'Os batedores dizem que grupos de ogros marcham pelo Vale da Lua Negra. Causamos uma impressão neles, não, Comandante?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'ruRU', 'Нападение огров!', 'Отбейте нападение огров на ваш гарнизон.$B$B(Чтобы начать, еще раз поговорите с сержантом Краулером.)', 'Разведчики докладывают о бандах огров, пересекающих Долину Призрачной Луны. Похоже, они все никак не оставят нас в покое, а, командир?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'zhCN', '食人魔入侵！', '击退食人魔对你要塞的入侵。$B$B（再次与克劳勒中士交谈来开始行动）', '据斥候回报，食人魔大军正在穿过影月谷。我们上次已经让他们知道厉害了是吧，指挥官？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36650, 'zhTW', '巨魔入侵！', '擊退侵略要塞的巨魔。$B$B(再次與克羅勒士官談話即可開始)', '斥候回報，有群巨魔正穿越影月谷。指揮官，讓他們對我們留下深刻的印象，你覺得怎樣？', '', '', '', '', '', '', 0);
