INSERT INTO `quest_template_locale` VALUES (36655, 'koKR', '강철 호드의 침략!', '하사관 크로울러와 대화해야 합니다.', '지평선 너머로 연기가 피어 오르는군요... 강철 호드가 오고 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36655, 'ptBR', 'Invasão da Horda de Ferro!', 'Fale com o Sargento Rastejo.', 'Há fumaça no horizonte... A Horda de Ferro está vindo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36655, 'ruRU', 'Нападение Железной Орды!', 'Поговорите с сержантом Краулером.', 'На горизонте виднеется дым... Железная Орда уже совсем близко.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36655, 'zhCN', '钢铁部落入侵！', '与克劳勒中士谈一谈。', '远方有几股黑烟……钢铁部落来了。', '', '', '', '', '', '', 0);
