INSERT INTO `quest_template_locale` VALUES (36817, 'koKR', '부서진 벼랑 공격', '어둠달 오크와 공허의 하수인들을 최대한 많이 처치해야 합니다.', '[PH]Commander! You just missed a group of rangari with some disturbing news. The Shadowmoon Orcs have been spotted in the hills above Anguish Fortress. The scouts reported seeing strange rituals and beings of pure darkness. Those orcs must be up there trying to summon more minions from the Void!$B$BIf we do not intervene all we have gained in Shadowmoon Valley might be lost!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36817, 'ptBR', 'O ataque ao Precipício Alquebrado', 'Elimine o máximo de orcs da Lua Negra e servos do Caos que conseguir.', '[PH]Comandante, acabou de passar um grupo de rangaris com péssimas notícias! Viram orcs da Lua Negra nas colinas acima da Fortaleza da Angústia. Os batedores disseram ter visto rituais estranhos e seres de pura escuridão. Aqueles orcs devem estar tentando evocar mais servos vindos do Caos!$B$BSe não intervirmos, corremos o risco de perder tudo que conquistamos no Vale da Lua Negra!', '', '', '', '', '', '', 0);
