INSERT INTO `quest_template_locale` VALUES (36830, 'deDE', 'Schattenratinvasion!', 'Schlagt die Invasion Eurer Garnison durch den Schattenrat zurück.$B$B(Sprecht erneut mit Unteroffizier Krähler, um zu beginnen.)', 'Die Dämonen sind uns auf den Fersen, Kommandant. Die Milch der Kuh ist sauer. Ein altes Weib hat mich heute Morgen schief angeguckt. Der Himmel sieht einfach nicht... richtig aus.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'esES', '¡Invasión del Consejo de la Sombra!', 'Repele la invasión del Consejo de la Sombra contra tu ciudadela.$B$B(Habla de nuevo con el sargento Crowler para empezar).', 'Los demonios nos pisan los talones, Comandante. La leche de las vacas está agria. Una vieja bruja me echó una mirada maligna esta mañana. El cielo no parece... normal.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'esMX', '¡Invasión del Consejo de la Sombra!', 'Repele la invasión del Consejo de la Sombra a tu fortaleza.$B$B(Habla con el sargento Crowler de nuevo para comenzar)', 'Los demonios nos pisan los talones, Comandante. La leche de las vacas es agria. Una vieja bruja me miró mal esta mañana. El cielo ciertamente no se ve... bien.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'frFR', 'Invasion du Conseil des ombres !', 'Repoussez l’invasion du Conseil des ombres contre votre Fief.$B$B(Parlez de nouveau au Sergent Crowler pour commencer.)', 'Les démons sont sur nos talons, commandant. Ça tourne au vinaigre. Une mégère m’a lancé un regard mauvais ce matin. Le ciel est… bizarre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'itIT', 'Invasione del Concilio dell\'Ombra!', 'Respingi l\'invasione del Concilio dell\'Ombra nella tua Guarnigione.$B$BParla ancora con il Sergente Crowler per iniziare.', 'I demoni ci stanno alle calcagna, comandante. Il latte delle mucche è inacidito. Una strega mi ha guardato male stamattina. Il cielo ha qualcosa di... sbagliato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'koKR', '어둠의 의회의 침략!', '어둠의 의회의 침략으로부터 주둔지를 방어해야 합니다.$B$B(하사관 크로울러와 다시 대화하면 시작됩니다.)', '악의 기운이 느껴집니다, 사령관님. 우유는 시큼하고, 오늘 아침엔 한 노파가 사악한 눈으로 절 흘겨보더군요. 하늘도 뭔가... 이상합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'ptBR', 'Invasão do Concílio das Sombras!', 'Rechace a invasão do Concílio das Sombras à sua guarnição.$B$B(Fale com o Sargento Rastejo novamente para começar)', 'Os demônios estão em nosso encalço, Comandante. O leite das vacas azedou. Uma bruxa velha me olhou com o maior olho gordo hoje de manhã. O céu não parece... certo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'ruRU', 'Нападение Совета Теней!', 'Отразите нападение Совета Теней на свой гарнизон.$B$B(Чтобы начать, поговорите еще раз с сержантом Краулером.)', 'Демоны наступают нам на пятки, командир. Молоко скисло. Старая ведьма на меня утром посмотрела как-то особенно злобно. И небо над головой какое-то... не такое.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'zhCN', '暗影议会入侵！', '击退暗影议会对你要塞的入侵。$B$B（再次与克劳勒中士交谈来开始行动）', '恶魔已经近在咫尺了，指挥官。连牛奶都是酸的。一只老母羊今天早上还白了我一眼。天空看着也不太……对劲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36830, 'zhTW', '暗影議會入侵！', '擊退侵略要塞的暗影議會。$B$B(再次與克羅勒士官談話即可開始)', '指揮官，惡魔緊追在後。牛奶是酸的。今天早上巫婆對我露出一個邪惡的表情。天空看起來就是…不對勁。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'deDE', 'Schattenratinvasion!', 'Schlagt die Invasion Eurer Garnison durch den Schattenrat zurück.$B$B(Sprecht erneut mit Unteroffizier Grimmkinn, um zu beginnen.)', 'Ein kaltes Feuer brennt in meinen Adern, Kommandant. So, als ob ein Hexer mein Herz in seinem grausamen Griff halten würde. Soll sich der Schattenrat doch zeigen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'esES', '¡Invasión del Consejo de la Sombra!', 'Repele la invasión del Consejo de la Sombra contra tu ciudadela.$B$B(Habla de nuevo con el sargento Faucefasta para comenzar.)', 'Un fuego frío recorre mis venas, Comandante. Como si un brujo me atenazara el corazón. ¡Que se deje ver el Consejo de la Sombra!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'esMX', '¡Invasión del Consejo de la Sombra!', 'Repele la invasión del Consejo de la Sombra de tu fortaleza.$B$B(Habla con el sargento Boca Siniestra de nuevo para comenzar)', 'Un fuego gélido corre por mi sangre, Comandante. Como si un brujo tuviera mi corazón en sus garras. ¡Deja que el Consejo de la Sombra presente a sí mismo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'frFR', 'Invasion du Conseil des ombres !', 'Repoussez l’invasion du Conseil des ombres contre votre fief.$B$B(Parlez de nouveau au Sergent Mors-Sinistre pour commencer.)', 'Un feu glacé coule dans mes veines, commandant. Comme si un démoniste tenait mon cœur entre ses mains. Que les membres du Conseil des ombres se montrent !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'itIT', 'Invasione del Concilio dell\'Ombra!', 'Respingi l\'invasione del Concilio dell\'Ombra nella tua Guarnigione.$B$BParla ancora con il Sergente Faucitetre per iniziare.', 'Sento un fuoco gelido bruciare nel mio sangue, comandante. È come se uno Stregone stringesse il mio cuore nelle sue grinfie. Facciamo uscire allo scoperto il Concilio dell\'Ombra!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'koKR', '어둠의 의회의 침략!', '어둠의 의회의 침략으로부터 주둔지를 방어해야 합니다.$B$B(하사관 그림죠와 다시 대화하면 시작됩니다.)', '사령관, 내 핏속에 차가운 불길이 타오르는군. 흑마법사 하나가 내 심장을 손아귀에 쥐고 있는 것 같은 기분일세. 저 어둠의 의회 놈들이 모습을 드러내게 하세!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'ptBR', 'Invasão do Concílio das Sombras!', 'Rechace a invasão do Concílio das Sombras à sua guarnição.$B$B(Fale com o Sargento Presatroz novamente para começar)', 'Um fogo frio ferve meu sangue, Comandante. Como se um bruxo tivesse meu coração em suas guardas. Que o Concílio das Sombras se revelem!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'ruRU', 'Нападение Совета Теней!', 'Отразите нападение Совета Теней на свой гарнизон.$B$B(Чтобы начать, поговорите еще раз с сержантом Зловещей Утробой.)', 'Моя кровь словно медленно вскипает, командир. Как будто какой-то могучий чернокнижник стиснул мое сердце своей когтистой лапой. Я чую – Совет Теней приближается!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'zhCN', '暗影议会入侵！', '击退暗影议会对你要塞的入侵。$B$B（再次与格里姆加中士交谈来开始行动）', '在我的血液中燃起了冷焰，指挥官。就像术士的魔爪攫住了我的心。让暗影议会自食苦果吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36831, 'zhTW', '暗影議會入侵！', '擊退侵略要塞的暗影議會。$B$B(再次與士官邪顎談話即可開始)', '在我的鮮血中有股冰冷之焰，指揮官。看來術士已經把魔爪伸入我的心臟。讓暗影議會自行現身吧！', '', '', '', '', '', '', 0);
