INSERT INTO `quest_template_locale` VALUES (36862, 'ruRU', 'Мастерская Паросвистов', 'Поговорите с Броном Небесным Рогом, чтобы отправиться в Мастерскую Паросвистов.', 'Командир, сегодня утром к нам прибыл курьер.$B$BОн сказал, что явился из мастерской Паросвистов – гоблинского поселения на пиках Арака. Там просят нашей помощи – курьер говорит, у них там какая-то экологическая катастрофа.$B$BНаши силы сейчас растянуты, но заручиться их доверием лишним не будет – так мы сможем получить ценного союзника.$B$BОтправляйся туда и посмотри, какую помощь мы можем предложить. Я говорила с распорядителем полетов, он уже подготовил для тебя особый рейс.', '', '', '', '', '', '', 0);
