INSERT INTO `quest_template_locale` VALUES (36930, 'ptBR', 'Nêmesis: O destruidor de draeneis', 'Mate 500 Jogadores Draeneicos em combates de Jogador contra Jogador através de Draenor.', '<Raza\'kul lhe estende o coração ensanguentado.>$B$BO ritual está completo. Seu nêmesis está marcado para morrer.$B$BAgora, sim, terá início o trabalho de livrar o mundo deste inimigo. Mate todos os draeneis que se opuserem à Horda. Faça com que suas mortes sirvam de exemplo ao resto da Aliança: se não deixarem Draenor, eles morrerão.$B$BVolte quando tiver saciado sua fúria com o sangue dos draeneis.$B$BVença o inimigo e você será conhecid$go:a; como $go destruidor:a destruidora; de draeneis. Se fracassar, você não será ninguém.', '', '', '', '', '', '', 0);
