INSERT INTO `quest_template_locale` VALUES (37124, 'zhTW', '獵食者之牙', '詢問賢者帕努納與岡恩牙齒項鍊有關的事。', '在我們出外尋找可以馴服的野獸時，這個項鍊從獸人獵人身上掉了出來。我從來沒看過動物們對它產生的強烈反應！', '', '', '', '', '', '', 0);
