INSERT INTO `quest_template_locale` VALUES (37191, 'deDE', 'Sanketsu, die brennende Klinge', 'Bringt Sanketsu, die brennende Klinge, zu Leutnant Dorn in Eurer Garnison.', 'Die Klinge ist glühend heiß. Das Juwel auf dem Schwertgriff strahlt Euch an, als wolle es Euch auffordern, das Schwert zu ergreifen.$B$BVielleicht gibt es in Eurer Garnison jemanden, der sich diese Waffe gerne ansehen würde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'esES', 'Sanketsu, el Filo Ardiente', 'Llévale Sanketsu, el Filo Ardiente, a la teniente Thorn, en tu ciudadela.', 'La hoja arde al tacto. La joya de su empuñadura te mira como retándote a asirla.$B$BTal vez alguien de tu ciudadela estaría interesado en ver esta arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'esMX', 'Sanketsu, el Filo Ardiente', 'Llévale a Sanketsu, el Filo Ardiente, al teniente Espina en tu fortaleza.', 'La espada es muy caliente al tacto. La joya en su empuñadura te mira directamente, como si te estuviera pidiendo que la tomes.$B$BQuizás alguien en tu fortaleza esté interesado en ver esta arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'frFR', 'Sanketsu, la lame ardente', 'Apportez Sanketsu, la lame ardente, au Lieutenant Épine à votre fief.', 'La lame est brûlante au toucher. La gemme incrustée dans sa garde vous fixe comme pour vous mettre au défi de saisir l\'épée.$b$bPeut-être qu’elle intéressera quelqu’un de votre fief.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'itIT', 'Sanketsu, la Lama Ardente', 'Porta Sanketsu la Lama Ardente al Tenente Thorn nella tua Guarnigione.', '<La lama è incandescente al tocco. Il gioiello sull\'elsa ti fissa, come se ti stesse sfidando a reclamarlo.>$B$B<Forse qualcuno nella tua Guarnigione potrebbe essere interessato a esaminare quest\'arma.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'koKR', '불타는 칼날, 산케츠', '주둔지의 부관 쏘른에게 불타는 칼날 산케츠를 가져가야 합니다.', '칼에 손을 대자 불타는 듯한 열기가 느껴집니다. 자루에 박힌 보석이 당신을 노려보며 마치 차지해 보라고 도발하는 듯합니다.$B$B주둔지에 있는 누군가가 이 무기에 관심을 보일지도 모르겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'ptBR', 'Sanketsu, a Lâmina Ardente', 'Leve Sanketsu, a Lâmina Ardente, para a Tenente Carda na sua Guarnição.', 'A lâmina queima ao toque. A joia cravada na empunhadura fica $go:a; encarando, como se $go:a; desafiasse a tomá-la para si.$B$BTalvez alguém na sua guarnição se interesse pela arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'ruRU', 'Шанкецу, Пылающий клинок', 'Отнесите Пылающий клинок Шанкецу лейтенанту Торн в ваш гарнизон.', 'К оружию больно прикоснуться – оно обжигает. Драгоценный камень на рукояти как будто смотрит на вас в упор, словно бросая вам вызов.$B$BВозможно, кому-то из вашего гарнизона будет интересно взглянуть на это оружие.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'zhCN', '烈焰之刃桑克苏', '把烈焰之刃桑克苏带回你的要塞给索恩中尉看看。', '这把剑火烫火烫的，剑柄上的珠宝似乎在瞪视着你，看你敢不敢接下它。$B$B或许你要塞里的人会有兴趣看到这件武器。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37191, 'zhTW', '灼熱之刃「爆燃」', '把灼熱之刃「爆燃」帶給要塞中的索恩妮中尉。', '刀刃摸起來十分灼熱。劍柄的珠寶瞪著你，看你敢不敢伸手去握。$B$B或許要塞中有什麼人會對這把武器感興趣。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'deDE', 'Sanketsu, die brennende Klinge', 'Bringt Sanketsu, die brennende Klinge, zu Kriegsmeister Zog in Eurer Garnison.', 'Die Klinge ist glühend heiß. Das Juwel auf dem Schwertgriff strahlt Euch an, als wolle es Euch auffordern, das Schwert zu ergreifen.$B$BVielleicht gibt es in Eurer Garnison jemanden, der sich diese Waffe gerne ansehen würde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'esES', 'Sanketsu, el Filo Ardiente', 'Llévale Sanketsu, el Filo Ardiente, al maestro de guerra Zog, en tu ciudadela.', 'La hoja arde al tacto. La joya de su empuñadura te mira como retándote a asirla.$B$BTal vez alguien de tu ciudadela estaría interesado en ver esta arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'esMX', 'Sanketsu, el Filo Ardiente', 'Llévale Sanketsu, el Filo Ardiente, al Maestro de guerra Zog en tu fortaleza.', 'La espada es muy caliente al tacto. La joya en su empuñadura te mira directamente, como si te estuviera pidiendo que la tomes.$B$BQuizás alguien en tu fortaleza esté interesado en ver esta arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'frFR', 'Sanketsu, la lame ardente', 'Apportez Sanketsu, la lame ardente au Maître de guerre Zog, à votre fief.', 'La lame est brûlante au toucher. La gemme incrustée dans sa garde vous fixe comme pour vous mettre au défi de saisir l\'épée.$b$bPeut-être qu’elle intéressera quelqu’un de votre fief.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'itIT', 'Sanketsu, la Lama Ardente', 'Porta Sanketsu la Lama Ardente al Maestro di Guerra Zog nella tua Guarnigione.', '<La lama è incandescente al tocco. Il gioiello sull\'elsa ti fissa, come se ti stesse sfidando a reclamarlo.>$B$B<Forse qualcuno nella tua Guarnigione potrebbe essere interessato a esaminare quest\'arma.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'koKR', '불타는 칼날, 산케츠', '주둔지의 전투대장 조그에게 불타는 칼날 산케츠를 가져가야 합니다.', '칼에 손을 대자 불타는 듯한 열기가 느껴집니다. 자루에 박힌 보석이 당신을 노려보며 마치 차지해 보라고 도발하는 듯합니다.$B$B주둔지에 있는 누군가가 이 무기에 관심을 보일지도 모르겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'ptBR', 'Sanketsu, a Lâmina Ardente', 'Leve Sanketsu, a Lâmina Ardente, para o Mestre Guerreiro Zog na sua guarnição.', 'A lâmina queima ao toque. A joia cravada na empunhadura fica $go:a; encarando, como se $go:a; desafiasse a tomá-la para si.$B$BTalvez alguém na sua guarnição se interesse pela arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'ruRU', 'Шанкецу, Пылающий клинок', 'Отнесите Пылающий клинок Шанкецу воеводе Зогу в ваш гарнизон.', 'К оружию больно прикоснуться – оно обжигает. Драгоценный камень на рукояти как будто смотрит на вас в упор, словно бросая вам вызов.$B$BВозможно, кому-то из вашего гарнизона будет интересно взглянуть на это оружие.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'zhCN', '烈焰之刃桑克苏', '把烈焰之刃桑克苏带回你的要塞给战争大师佐格看看。', '这把剑火烫火烫的，剑柄上的珠宝似乎在瞪视着你，看你敢不敢接下它。$B$B或许你要塞里的人会有兴趣看到这件武器。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37192, 'zhTW', '灼熱之刃「爆燃」', '把灼熱之刃「爆燃」帶給要塞中的將領佐格。', '刀刃摸起來十分灼熱。劍柄的珠寶瞪著你，看你敢不敢伸手去握。$B$B或許要塞中有什麼人會對這把武器感興趣。', '', '', '', '', '', '', 0);
