INSERT INTO `quest_template_locale` VALUES (37296, 'deDE', 'Mangel an Wespen', 'Tötet $1oa Witwenwespen.', 'Ihre Stiche! Diese verfluchten Wespen summen tagaus, tagein und ihre ständige Aggression belastet mich schwer.$b$bIch werde mich erst dann um Euer Angebot kümmern, wenn jemand sich dieser Plagegeister angenommen hat.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'esES', 'Carencia de avispas', 'Mata a $1oa avispas viudas.', '¡Esos aguijones! Esas malditas avispas se pasan día y noche zumba que te zumba y no puedo con su agresividad.$b$bIgnoraré tu oferta hasta que nos hayamos librado de ese incordio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'esMX', 'Sin avispas', 'Mata $1oa avispas viudas.', '¡Las picaduras! Estas malditas avispas zumban día y noche y su constante agresión recae sobre mí.$b$bNo haré nada con tu oferta hasta que estas molestias sean controladas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'frFR', 'Pénurie de guêpes', 'Tuez $1oa Guêpes veuves.', 'Les dards ! Ces maudites guêpes patrouillent jour et nuit et leurs agressions incessantes m’abattent.$b$bJe ne m’occuperai pas de votre offre avant que vous m’ayez débarrassé de ces nuisibles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'itIT', 'Vespe in lutto', 'Elimina $1oa Vespe Vedove.', 'Che punture! Queste maledette vespe ronzano giorno e notte, e le loro costanti aggressioni mi mettono in difficoltà.$b$bNon ascolterò le tue richieste fin quando non porrai fine a questa scocciatura.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'koKR', '말벌의 비존재', '과부 말벌 $1oa마리를 처치해야 합니다.', '벌들! 이 저주받은 말벌들이 밤낮없이 윙윙거리고 덤비는 통에 살 수가 없네.$b$b이 성가신 놈들이 사라지기 전엔 자네 제안에 대해 뭐라 말할 수가 없겠어.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'ptBR', 'Uma ausência de vespas', 'Mate $1oa Vespas Viúvas.', 'Os ferrões! Essas vespas malditas zumbem dia e noite e a agressão constante delas me cansa tanto.$b$bE não farei nada quanto a sua oferta até que esse aborrecimento seja resolvido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'ruRU', 'Дефицит ос', 'Убейте $1oa |4осу-вдову:осы-вдовы:ос-вдов;.', 'Жала! Эти проклятые осы жужжат круглые сутки, и их постоянная агрессия меня утомляет.$b$bЯ никак не отвечу на твое предложение, пока с этим неудобством не будет покончено.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'zhCN', '先除掉毒蜂再说', '消灭$1oa只寡妇毒蜂。', '那些该死的毒蜂一天到晚来蛰我，弄得我精疲力竭。$b$b除非你把它们都解决掉，否则你送什么来也没用。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37296, 'zhTW', '無蜂至上', '殺死$1oa隻寡婦蜂。', '那些刺！那些欠詛咒的蜂日夜嗡個不停，持續不斷讓我倍感壓力。$b$b在這些麻煩解決之前，我無法考慮你的提案。', '', '', '', '', '', '', 0);
