INSERT INTO `quest_template_locale` VALUES (37474, 'esES', 'Los principios básicos van primero', 'Habla con Audrey Burnhep en Ciudad de Ventormenta.', 'Muestras una buena actitud, $n, pero primero tienes que aprender los principios básicos de los duelos de mascotas. Visita a Audrey Burnhep, en Ciudad de Ventormenta, y ella te ayudará a empezar.', '', '', '', '', '', 'Habla con: Audrey Burnhep. Zona: Ciudad de Ventormenta.', 0);
