INSERT INTO `quest_template_locale` VALUES (37474, 'frFR', 'Commençons par les bases', 'Parlez à Audrey Burnhep à Hurlevent.', 'Vous avez un sacré panache, $n, mais vous devez d’abord apprendre les bases du combat de mascottes. Allez voir Audrey Burnhep à Hurlevent, elle vous aidera.', '', '', '', '', '', 'Parlez à Audrey Burnhep à Hurlevent.', 0);
INSERT INTO `quest_template_locale` VALUES (37474, 'itIT', 'Prima le basi', 'Parla con Audrey Burnhep a Roccavento.', 'Hai un grande spirito, $n, ma devi prima imparare le basi degli scontri tra mascotte. Fai visita ad Audrey Burnhep a Roccavento, lei ti insegnerà tutto il necessario per iniziare.', '', '', '', '', '', 'Parla con Audrey Burnhep a Roccavento.', 0);
INSERT INTO `quest_template_locale` VALUES (37474, 'koKR', '기초 다지기', '스톰윈드에 있는 오드리 번헵과 대화해야 합니다.', '훌륭한 기백이로군요, $n 님. 하지만 일단은 애완동물 대전의 기본을 배우셔야죠. 스톰윈드에 있는 오드리 번헵을 찾아가 보십시오. 시작할 수 있게 그녀가 도와줄 겁니다.', '', '', '', '', '', '스톰윈드에 있는 오드리 번헵과 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (37474, 'ptBR', 'Vamos começar do básico', 'Fale com Audreia Répibãrne em Ventobravo.', 'Grande espírito, $n! Mas terá que aprender o básico sobre batalhas de mascote primeiro. Visite Audreia Répibãrne em Ventobravo e ela $go:a; iniciará.', '', '', '', '', '', 'Fale com Audreia Répibãrne em Ventobravo.', 0);
INSERT INTO `quest_template_locale` VALUES (37474, 'ruRU', 'Все по порядку', 'Поговорите с Одри Бернхеп в Штормграде.', 'У тебя есть воля к победе, $n, но сперва нужно обучиться основам битв питомцев. Обратись к Одри Бернхеп в Штормграде.', '', '', '', '', '', 'Поговорите с Одри Бернхеп в Штормграде.', 0);
INSERT INTO `quest_template_locale` VALUES (37474, 'zhCN', '夯实基本功', '与暴风城的奥黛丽·伯恩赫普交谈。', '不错的气势，$n，但你在宠物对战这块可得从头学起。去暴风城找奥黛丽·伯恩赫普吧，她会将基本功传授给你的。', '', '', '', '', '', '与暴风城的奥黛丽·伯恩赫普交谈。', 0);
