INSERT INTO `quest_template_locale` VALUES (37561, 'itIT', 'Essenza del Dominatore di Ferro', 'Consegna un\'Essenza del Dominatore di Ferro.', 'Riscatta la tua Essenza del Dominatore di Ferro dall\'Esarca Maladaar e seleziona un pezzo d\'armatura a tua scelta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37561, 'koKR', '강철 제압자 정수', '강철 제압자 정수를 획득해야 합니다.', '총독 말라다르에게 강철 제압자 정수를 가져다주면, 원하는 보상을 선택할 수 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37561, 'ptBR', 'Essência do Subjugador Férreo', 'Obtenha uma Essência do Subjugador Férreo.', 'Entregue a Essência do Subjugador Férreo ao Exarca Maladaar para receber um armamento de sua escolha.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37561, 'ruRU', 'Сущность железного покорителя', 'Раздобудьте сущность железного покорителя.', 'Отдайте экзарху Маладаару сущность железного покорителя в обмен на предмет экипировки по вашему выбору.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37561, 'zhCN', '钢铁胜利者的精华', '收集一个钢铁胜利者的精华。', '带着你的钢铁胜利者的精华去找大主教玛拉达尔，从他那里选一件合用的装备吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37561, 'zhTW', '鋼鐵鎮壓者精華', '收集一個鋼鐵鎮壓者精華。', '和瑪拉達爾主教兌換你的鋼鐵鎮壓者精華，並選擇一項軍備。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'deDE', 'Essenz des Eisernen Beschützers', 'Erhaltet eine Essenz des Eisernen Beschützers.', 'Löst Eure Essenz des Eisernen Beschützers bei Exarch Maladaar ein, um einen Rüstungsgegenstand Eurer Wahl zu erhalten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'esES', 'Esencia del protector de hierro', 'Obtén una esencia del protector de hierro.', 'Entrega la esencia del protector de hierro al exarca Maladaar para seleccionar el armamento que tú quieras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'esMX', 'Esencia del Protector de hierro', 'Obtén una esencia del Protector de hierro', 'Canjea tu esencia del Protector de hierro con el exarca Maladaar para seleccionar un armamento de tu elección.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'frFR', 'L\'essence du protecteur de Fer', 'Obtenez une Essence du protecteur de Fer.', 'Échangez votre essence du protecteur de Fer auprès de l’exarque Maladaar pour obtenir une arme de votre choix.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'itIT', 'Essenza del Protettore di Ferro', 'Consegna un\'Essenza del Protettore di Ferro.', 'Riscatta la tua Essenza del Protettore di Ferro dall\'Esarca Maladaar e seleziona un pezzo d\'armatura a tua scelta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'koKR', '강철 수호자 정수', '강철 수호자 정수를 획득해야 합니다.', '총독 말라다르에게 강철 수호자 정수를 가져다주면, 원하는 보상을 선택할 수 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'ptBR', 'Essência do Protetor Férreo', 'Obtenha uma essência do Protetor Férreo.', 'Entregue a Essência do Protetor Férreo ao Exarca Maladaar para receber um armamento de sua escolha.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'ruRU', 'Сущность железного защитника', 'Получите сущность железного защитника.', 'Отдайте экзарху Маладаару сущность железного защитника в обмен на предмет экипировки по вашему выбору.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'zhCN', '钢铁保卫者的精华', '收集一个钢铁保卫者的精华。', '带着你的钢铁保卫者的精华去找大主教玛拉达尔，从他那里选一件合用的装备吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37562, 'zhTW', '鋼鐵保衛者精華', '取得一個鋼鐵保衛者精華。', '和瑪拉達爾主教兌換你的鋼鐵保衛者精華，並選擇一項軍備。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'deDE', 'Die Frostwölfe sind bereit.', 'Wählt einen Anhänger (entweder Großmutter Geyah, Kal\'gor den Ehrenhaften oder Lokra) und sprecht dann mit Makar Steinbinder.', 'Obwohl wir heute viel verloren haben, sind die Frostwölfe bereit, unseren Pakt mit Eurem Volk zu erfüllen.$b$bUm diese Übereinkunft einzuhalten haben einige meiner Klansmitglieder darum gebeten, sich Euch anschließen und Seite an Seite mit Euren Truppen kämpfen zu dürfen.$b$bUnsere Reihen haben sich gelichtet, also bitte ich Euch, nur einen von ihnen auszuwählen. Sprecht mit mir, wenn Ihr Eure Entscheidung getroffen habt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'esES', 'Los Lobo Gélido están preparados', 'Elige a un seguidor (la Abuela Geyah, Kal\'gor el Honorable o Lokra) y habla con Makar Amarrapiedra.', 'Aunque hoy hemos perdido mucho, los Lobo Gélido estamos dispuestos a cumplir el pacto que tenemos con tu pueblo.$b$bPara honrar este acuerdo, algunos miembros de mi clan han solicitado unirse a tus filas y combatir junto a tus fuerzas.$b$bNuestros números son escasos, así que te pido que escojas solo a uno.$b$bHabla conmigo cuando te hayas decidido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'esMX', 'Los Lobo Gélido están listos.', 'Elije un seguidor (entre la Abuela Geyah, Kal\'gor el Honorable, o Lokra) y luego habla con Makar Vinculador Pétreo.', 'Aunque perdimos mucho este día, los Lobo Gélido permanecemos listos para defender nuestro pacto con tu gente.$b$bPara honrar este acuerdo, algunos de mi clan han solicitado unirse a tus filas y pelear junto a tus fuerzas.$b$bNuestros números están mermados, así que te pido que elijas solo a uno.$b$bHabla conmigo cuando hayas tomado una decisión.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'frFR', 'Les Loups-de-Givre parés à l’action', 'Choisissez un sujet (Grande-mère Geyah, Kal’gor l’Honorable ou Lokra), puis parlez à Makar Lieur-de-Pierre.', 'Même si nous avons perdu beaucoup aujourd’hui, les Loups-de-Givre sont prêts à respecter le pacte conclu avec votre peuple.$b$bPour honorer cet accord, des membres de mon clan ont demandé à rejoindre vos rangs pour combattre à vos côtés.$b$bNous sommes peu nombreux, alors je vous demande de ne choisir qu’une personne.$b$bVenez me parler lorsque vous aurez arrêté votre choix.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'itIT', 'I Lupi Bianchi sono pronti', 'Scegli un seguace tra la Madre Anziana Geyah, Kal\'gor l\'Onorevole e Lokra, quindi parla con Makar Vincolapietra.', 'Oggi abbiamo perso molto, ma i Lupi Bianchi sono pronti a tener fede al patto stipulato con la tua gente.$b$bPer onorare questo accordo, alcuni membri del mio clan hanno chiesto di unirsi alle tue armate per combattere al tuo fianco.$b$bI nostri numeri sono ridotti al minimo, quindi ti chiedo di sceglierne soltanto uno.$b$bParla con me quando avrai preso la tua decisione.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'koKR', '당당히 일어선 서리늑대', '대모 게야, 명예로운 칼고르, 로크라 중에서 추종자 한 명을 선택하고 마카르 스톤바인더와 대화해야 합니다.', '오늘 많은 동료를 잃었지만, 서리늑대는 자네의 일행과 맺은 맹약을 지키기 위해 당당히 일어설 걸세.$b$b이 명예로운 약속을 지킨다는 뜻에서, 우리 부족의 일원이 자네의 곁에서 함께 싸울 것이네.$b$b지금은 우리 부족의 수가 많이 부족하니, 부디 한 명만 골라 주길 바라네.$b$b심사숙고하여 결정을 내린 후, 내게 말해 주게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'ptBR', 'Os Lobos do Gelo estão prontos', 'Escolha um seguidor (a Grande Mãe Geyah, Kal\'gor, o Honorável, ou Lokra) e depois fale com Makar Atapedras.', 'Embora tenhamos perdido muito hoje, os Lobo do Gelo estão prontos para cumprir nosso pacto com o seu povo.$b$bPar honrar esse acordo, alguns do meu clã pediram para se unir às suas tropas e lutar ao lado delas.$b$bNosso contingente está baixo, então devo pedir que você escolha apenas um.$b$bFale comigo quando tomar sua decisão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'ruRU', 'Решимость Северных Волков', 'Выберите одного из соратников (великую мать Гейю, Кал\'гора Благородного или Локру), затем снова поговорите с Маккаром Камневязом.', 'Сегодня мы понесли тяжелую утрату, но слово Северного Волка тверже гранита, и наш договор остается в силе.$b$bНесколько бойцов моего клана вызвались присоединиться к твоему войску, чтобы вместе сражаться против общего врага.$b$bУвы, нас осталось очень мало, поэтому я могу отпустить лишь одного.$b$bКогда ты сделаешь свой выбор, сообщи мне.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'zhCN', '准备就绪的霜狼', '选择一位追随者（盖亚安祖母，可敬的卡尔戈或洛卡拉），然后与马卡·缚石谈一谈。', '虽然我们今天损失惨重，但霜狼们随时可以起来履行跟你们的约定。$b$b为了遵守这个协议，我的一些族人主动要求加入你的队伍，与你的部队并肩作战。$b$b但我们的人手紧张，所以我只能让你从中挑选一位追随者。$b$b你做出决定之后，就告诉我一声吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37563, 'zhTW', '霜狼備戰', '選擇一位追隨者(可以選擇主母吉雅、『尊榮的』卡爾戈或羅克拉)並和馬卡爾‧縛石者交談。', '雖然我們今天損失慘重，但霜狼會和你們的人民一塊相互扶持、隨時待命。$b$b為了表揚這項協議，氏族中有些人想要加入你的行列，和你的部隊一塊奮戰。$b$b我們的人口有限，所以請你挑出其中一人。$b$b在你決定之後，告訴我一聲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'deDE', 'Euer erster Arbeitsauftrag für Schmiedekunst', 'Beginnt Euren ersten Schmiedekunstarbeitsauftrag in der Schmiede und holt in anschließend ab.', 'Jetzt, da Ihr über eine Schmiede verfügt, können wir Echtstahlbarren für Euch fertigen. Sprecht mit meinem Gehilfen, wenn Ihr die Bestandteile für den ersten Arbeitsauftrag zusammengetragen habt, sodass wir damit beginnen können.', '', '', '', '', '', 'Kehrt zum Schmiedegehilfen zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'esES', 'Tu primer pedido de herrería', 'Comienza y consigue tu primer pedido de herrería en la forja.', 'Ahora que la forja está finalizada, podemos comenzar a producir lingotes de veracero para ti. Cuando tengas los ingredientes, habla con mi ayudante para iniciar el primer pedido.', '', '', '', '', '', 'Vuelve con: Ayudante del herrero.', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'esMX', 'Tu primer pedido de herrería', 'Comienza y recolecta tu primer pedido de herrería en la forja.', 'Ahora que tienes una forja, podemos comenzar a producir lingotes de acero puro para ti. Cuando tengas los ingredientes, habla con mi asistente para comenzar con el primer pedido.', '', '', '', '', '', 'Vuelve con el asistente del herrero.', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'frFR', 'Votre première commande de forge', 'Lancez et récupérez votre première commande de forge à la Forge.', 'Maintenant que la forge est achevée, nous pouvons commencer à vous fournir des lingots de véracier. Quand vous aurez les composants, allez parler à mon assistant pour lancer la première commande.', '', '', '', '', '', 'Retournez voir l’assistant du forgeron.', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'itIT', 'La tua prima produzione di Forgiatura', 'Avvia la tua prima produzione di Forgiatura e poi recuperala una volta completata.', 'Ora che hai una Forgia, possiamo iniziare a produrre lingotti di Veracciaio. Quando avrai gli ingredienti necessari, parla con il mio assistente per iniziare il primo incarico di lavoro.', '', '', '', '', '', 'Torna dall\'assistente del fabbro.', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'ptBR', 'Primeira ordem de serviço de ferraria', 'Inicie e colete sua primeira ordem de serviço de ferraria na Forja.', 'Agora que a forja está concluída, podemos começar a produzir lingotes de vero-aço. Quando tiver a matéria-prima, fale com meu assistente para iniciar a primeira ordem de serviço.', '', '', '', '', '', 'Fale novamente com o assistente de ferraria.', 0);
INSERT INTO `quest_template_locale` VALUES (37564, 'ruRU', 'Первый заказ для кузницы', 'Сделайте свой первый заказ для кузницы, затем заберите готовую продукцию.', 'Теперь у тебя есть кузница, и мы можем начать производство слитков истинной стали. Как только добудешь нужные материалы, поговори с моей помощницей, и мы начнем работу над первой партией.', '', '', '', '', '', 'Вернитесь к помощнику кузнеца.', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'deDE', 'Genau die richtige Waffe', 'Bewaffnet $1oa schiffbrüchige Gefangene.', '<Ihr und Okuna wolltet die Gefangenen hier drin bewaffnen. Die Idee war, sie zu befreien und so viel Chaos wie möglich anzurichten. Das ist die einzige Möglichkeit, an den Prinzen heranzukommen.\n\nIhr habt immer noch die Waffen. Ihr müsst Euch nur vorsichtig an den Riesen vorbeischleichen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'esES', 'El arma adecuada para el trabajo', 'Arma a $1oa cautivos naufragados.', '<Okuna y tú ibais a armar a los cautivos aquí. La idea era liberarlos y armar tanto caos como fuera posible. Es la única forma segura de conseguir acercarse al príncipe.\n\nAún tienes las armas, pero deberás tener cuidado para que no te descubran los gigantes>.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'esMX', 'El arma correcta para la tarea', 'Equipa a $1oa náufragos capturados.', '<Okuna y tú iban a armar a los capturados que están aquí. La idea era liberarlos y provocar tanto caos como fuera posible. Es la única forma segura de acercarnos al príncipe.\n\nTodavía tienes las armas. Solo tendrás que tener cuidado al escabullirte entre los gigantes.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'frFR', 'L’arme de la situation', 'Armez $1oa Naufragés captifs.', '<Okuna et vous aviez l’intention d’armer les détenus. Votre plan consistait à les libérer afin de causer le plus grand chaos possible. C’est le seul moyen de pouvoir approcher le prince à coup sûr.\n\nVous avez toujours les armes. Il va juste falloir faire très attention en contournant les géants.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'itIT', 'L\'arma adatta per questo lavoro', 'Arma $1oa Prigionieri Naufragati.', '<Tu e Okuna avreste dovuto armare i prigionieri. L\'idea era liberarli e creare quanto più caos possibile. È l\'unico modo sicuro per riuscire ad avvicinarsi al principe.>\n\n<Hai ancora le armi. Devi semplicemente stare $gattento:attenta; a non svegliare i giganti passandoci accanto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'koKR', '목적 달성을 위한 무기', '난파당한 포로 $1oa명에게 무기를 줘야 합니다.', '<당신과 오쿠나는 이곳에 갇힌 이들을 무장시키기로 했습니다. 이들을 풀어주고 가능한 한 큰 소동을 일으켜야 왕자에게 접근할 수 있기 때문입니다.\n\n당신은 아직 무기를 갖고 있으니 거인들 사이를 몰래 지나가기만 하면 됩니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'ptBR', 'A Arma Certa', 'Arme $1oa Cativos Naufragados.', '<Você e Okuna iam armar os prisioneiros do lugar. A ideia era libertá-los e provocar o máximo de caos possível. Era a única forma garantida de chegar ao príncipe.\n\nVocê ainda possui as armas. Só vai ter que passar pelos gigantes com cuidado.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'ruRU', 'Самое подходящее оружие', 'Вооружите $1oa |4плененную жертву:плененные жертвы:плененных жертв; кораблекрушения.', '<Вы с Окуной собирались вооружить всех содержащихся здесь пленников. Согласно первоначальному замыслу, их надо было освободить и устроить переполох. Это единственный надежный способ подобраться поближе к принцу.\n\nОружие все еще при вас. Нужно только аккуратно проскользнуть мимо великанов, чтобы они вас не заметили.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'zhCN', '武装起来', '武装$1oa名海难俘虏。', '<你和欧库纳打算将这里的俘虏们都武装起来。你们的计划就是让他们逃走，并且尽量制造混乱，只有这样才有可能接近王子。>\n\n<武器还在你手上，你需要在巨人眼皮底下神不知鬼不觉地行动。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37565, 'zhTW', '工欲善其事，必先利其器', '武裝$1oa名船難俘虜。', '<你和歐庫納的計畫是武裝這裡的俘虜。關鍵在於釋放俘虜，然後盡可能製造混亂。這是接近巨人王子的唯一方法。\n\n你還有足夠的武器。你只需要神不知鬼不覺地在巨人眼皮底下行動。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'deDE', 'Das Ende des Prinzen', 'Befreit Okuna Langhauer und tötet Prinz Ozeanus.', '$n! Ich wusste, dass Ihr kommen würdet.\n\nSchnell, lasst mich hier raus, damit wir diesen Riesen erledigen können.', '', 'Versetzt die Riesen in Aufruhr, indem Ihr ihren Prinzen tötet.', 'Prinz Ozeanus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'esES', 'El príncipe va a pringar', 'Libera a Okuna Colmillo Largo y mata al príncipe Oceanus.', '¡$n! Sabía que vendrías.\n\nRápido, déjame salir de aquí y tumbaremos a ese gigante.', '', 'Provoca el caos entre los gigantes matando a su príncipe.', 'Príncipe Oceanus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'esMX', 'El príncipe caerá', 'Libera a Okuna Colmillo Largo y mata al príncipe Oceanus.', '¡$n! Sabía que vendrías.\n\nRápido, sácame de aquí y acabaremos con ese gigante.', '', 'Siembra el caos en los gigantes derrotando a su príncipe.', 'Príncipe Oceanus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'frFR', 'Le prince doit mourir', 'Libérez Okuna Longue-Défense et tuez le Prince Océanus.', '$n ! Je savais que vous viendriez.\n\nVite, tirez-moi de là et on se chargera du géant.', '', 'Semez la zizanie parmi les géants en tuant leur prince.', 'Prince Océanus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'itIT', 'Il principe sta cadendo', 'Libera Okuna Zannelunghe e uccidi il Principe Oceanus.', '$n! Sapevo saresti $gvenuto:venuta;.\n\nVeloce, fammi uscire di qui e uccidiamo quel gigante.', '', 'Crea scompiglio tra i giganti uccidendo il loro principe.', 'Principe Oceanus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'koKR', '왕자의 최후', '오쿠나 롱터스크를 구출하고 왕자 오세아누스를 처치해야 합니다.', '오실 줄 알았습니다, $n 님!\n\n힘을 합쳐 이 거인을 쓰러뜨립시다. 빨리 여기서 꺼내주십시오.', '', '왕자를 처치하여 거인들을 혼란에 빠뜨리십시오.', '왕자 오세아누스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'ptBR', 'O Príncipe Vai Cair', 'Liberte Okuna Presalonga e mate o Príncipe Oceanus.', '$n! Eu sabia que você viria.\n\nRápido, me tire daqui para derrubarmos esse gigante.', '', 'Desbarateie os gigantes matando o príncipe deles.', 'Príncipe Oceanus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'ruRU', 'Принцу пора на покой', 'Освободите Окуну Большого Бивня и убейте принца Океания.', '$n! Я знал, что ты придешь.\n\nСкорее! Выпусти меня отсюда, и вместе мы затопчем этого великана.', '', 'Внесите сумятицу в ряды великанов, убив их принца.', 'Принц Океаний', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37566, 'zhCN', '王子的陨落', '救出欧库纳·长牙并消灭欧逊努斯王子。', '$n！我就知道你会来。\n\n快，放我出去，我们去干掉那个巨人。', '', '消灭欧逊努斯王子，让巨人们群龙无首。', '欧逊努斯王子', '', '', '', 0);
