INSERT INTO `quest_template_locale` VALUES (37570, 'deDE', 'Euer erster Arbeitsauftrag für Verzauberkunst', 'Beginnt Euren ersten Arbeitsauftrag für Verzauberkunst beim Verzauberungsatelier und holt ihn anschließend ab.', 'Jetzt, da Ihr über ein Verzauberungsatelier verfügt, können wir draenischen Staub für Euch fertigen. Sprecht mit meiner Gehilfin, wenn Ihr die Bestandteile zusammengetragen habt, damit wir mit dem ersten Arbeitsauftrag beginnen können.', '', '', '', '', '', '', 0);
