INSERT INTO `quest_template_locale` VALUES (37570, 'koKR', '첫 번째 마법부여 제품', '마법부여사의 연구실에서 첫 번째 제품을 주문하고 생산된 제품을 획득해야 합니다.', '이제 마법부여사의 연구실이 완성되었으니, 사령관님을 위해 드레나이 가루를 제작할 수 있습니다. 재료를 다 구한 후 제 조수에게 말씀해 주시면 작업을 시작하겠습니다.', '', '', '', '', '', '', 0);
