INSERT INTO `quest_template_locale` VALUES (37571, 'ruRU', 'Первый заказ для инженерной мастерской', 'Сделайте свой первый заказ для инженерной мастерской, затем заберите готовую продукцию.', 'Теперь у тебя есть инженерная мастерская, и мы можем начать производство частей механизмов. Как только добудешь нужные материалы, поговори с моей помощницей, и мы начнем работу над первой партией.', '', '', '', '', '', '', 0);
