INSERT INTO `quest_template_locale` VALUES (37572, 'itIT', 'La tua prima produzione di Runografia', 'Avvia la tua prima produzione di Runografia e riscuoti l\'ordine alla fine dei lavori.', 'Ora che hai un Ateneo di Runografia, possiamo iniziare a produrre Colori di Guerra. Quando avrai i materiali necessari, parla con il mio assistente per iniziare il primo incarico di lavoro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37572, 'koKR', '첫 번째 주문각인 제품', '주문각인사의 처소에서 첫 번째 제품을 주문하고 생산된 제품을 획득해야 합니다.', '이제 주문각인사의 처소가 완성되었으니, 사령관님을 위해 전쟁 물감을 제작할 수 있어요. 재료를 다 구한 후 제 조수에게 말씀해 주시면 작업을 시작하겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37572, 'ptBR', 'Primeira ordem de serviço de escrivania', 'Inicie e colete sua primeira ordem de serviço de escrivania na Ala do Escriba.', 'Agora que a ala do escriba está concluído, podemos começar a produzir tinturas de guerra. Quando tiver a matéria-prima, fale com meu assistente para iniciar a primeira ordem de serviço.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37572, 'ruRU', 'Первый заказ для мастерской начертателя', 'Сделайте свой первый заказ для мастерской начертателя, затем заберите готовую продукцию.', 'Теперь у тебя есть мастерская начертателя, и мы можем начать производство красок сражений. Как только добудешь нужные материалы, поговори с моим помощником, и мы начнем работу над первой партией.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37572, 'zhCN', '你的第一份铭文订单', '在铭文师之家开始你的第一份铭文订单，并收集产品。', '既然你已经有了铭文师之家，我们可以开始为你生产战争颜料了。等你找到材料就跟我的助手说一声，开始第一份产品订单吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37572, 'zhTW', '你的第一張銘文學訂單', '在雕銘書房開始和點收你的第一張銘文學訂單。', '現在雕銘書房已經完工了，我們可以開始為你製作戰爭顏料。當你取得足夠的材料之後，請和我的助手交談，就可以進行第一張訂單。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'deDE', 'Euer erster Arbeitsauftrag für Juwelierskunst', 'Beginnt Euren ersten Arbeitsauftrag für Juwelierskunst bei der Juwelenschleiferei und holt ihn anschließend ab.', 'Jetzt, da Ihr über eine Juwelenschleiferei verfügt, können wir Taladitkristalle für Euch fertigen. Sprecht mit meinem Gehilfen, wenn Ihr die Bestandteile zusammengetragen habt, damit wir mit dem ersten Arbeitsauftrag beginnen können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'esES', 'Tu primer pedido de joyería', 'Empieza y recoge tu primer pedido de joyería en el tenderete de gemas.', 'Ahora que el tenderete de gemas está completado, podemos comenzar a producir cristales de taladita para ti. Cuando tengas los ingredientes, habla con mi ayudante para iniciar el primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'esMX', 'Tu primer pedido de joyería', 'Comienza y recoge tu primer pedido de joyería en la boutique de gemas.', 'Ahora que la boutique de gemas está lista, podemos comenzar a producir cristales de taladita para ti. Cuando tengas los ingredientes, habla con mi asistente para empezar el primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'frFR', 'Votre première commande de joaillerie', 'Lancez puis récupérez votre première commande de Joaillerie à la Boutique de gemmes.', 'Maintenant que la boutique de gemmes est ouverte, nous pouvons commencer à vous fournir des cristaux de taladite. Quand vous aurez les composants, il vous suffira de parler à mon assistant pour lancer la première commande.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'itIT', 'La tua prima produzione di Oreficeria', 'Avvia la tua prima produzione di Oreficeria e riscuoti l\'ordine alla fine dei lavori.', 'Ora che hai una Boutique d\'Oreficeria, possiamo iniziare a produrre Cristalli di Taladite. Quando avrai i materiali necessari, parla con il mio assistente per iniziare il primo incarico di lavoro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'koKR', '첫 번째 보석세공 제품', '보석 양품점에서 첫 번째 제품을 주문하고 생산된 제품을 획득해야 합니다.', '이제 보석 양품점이 완성되었으니, 사령관님을 위해 탈라다이트 수정을 제작할 수 있습니다. 재료를 다 구한 후 제 조수에게 말씀해 주시면 작업을 시작하겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'ptBR', 'Primeira ordem de serviço de joalheria', 'Inicie e colete sua primeira ordem de serviço de joalheria na Butique de Gemas.', 'Agora que a butique de gemas está concluída, podemos começar a produzir cristais de taladita. Quando tiver a matéria-prima, fale com meu assistente para iniciar a primeira ordem de serviço.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'ruRU', 'Первый заказ для лавки самоцветов', 'Сделайте свой первый заказ для лавки самоцветов, затем заберите готовую продукцию.', 'Теперь у тебя есть лавка самоцветов, и мы можем начать производство таладитовых кристаллов. Как только добудешь нужные материалы, поговори с моим помощником, и мы начнем работу над первой партией.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37573, 'zhCN', '你的第一份珠宝加工订单', '在珠宝店开始你的第一份珠宝加工订单，并收集产品。', '既然你已经有了珠宝店，我们可以开始为你生产塔拉迪特水晶了。等你找到材料就跟我的助手说一声，开始第一份产品订单吧。', '', '', '', '', '', '', 0);
