INSERT INTO `quest_template_locale` VALUES (37627, 'deDE', 'Angriff auf die Zerschlagene Anhöhe', 'Eliminiert so viele Schattenmondorcs und ihre Diener der Leere, wie Ihr könnt.', '[PH]Commander! You just missed a group of rangari with some disturbing news. The Shadowmoon Orcs have been spotted in the hills above Anguish Fortress. The scouts reported seeing strange rituals and beings of pure darkness. Those orcs must be up there trying to summon more minions from the Void!$B$BIf we do not intervene all we have gained in Shadowmoon Valley might be lost!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37627, 'esES', 'Asalto al Despeñadero Quebrado', 'Elimina a tantos orcos Sombraluna y a sus esbirros del Vacío como puedas.', '[PH]Commander! You just missed a group of rangari with some disturbing news. The Shadowmoon Orcs have been spotted in the hills above Anguish Fortress. The scouts reported seeing strange rituals and beings of pure darkness. Those orcs must be up there trying to summon more minions from the Void!$B$BIf we do not intervene all we have gained in Shadowmoon Valley might be lost!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37627, 'esMX', 'Asalto al Precipicio Roto', 'Derrota a la mayor cantidad posible de orcos Sombraluna y a sus esbirros del vacío.', '[PH]¡Comandante! Acaba de perderse a un grupo de Rangari con horribles noticias. Vieron a los orcos Sombraluna en las colinas sobre la Fortaleza Angustia. Los exploradores reportaron haber visto rituales extraños y seres de pura oscuridad. ¡Esos orcos deben estar ahí arriba tratando de invocar a más esbirros del vacío!$B$BSi no intervenimos, ¡todo lo que hemos conseguido en el Valle de Sombraluna podría estar perdido!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37627, 'frFR', 'Assaut sur le précipice Brisé', 'Éliminez autant d’Orcs ombrelunes et de leurs Serviteurs du Vide que vous le pourrez.', '[PH]Commander! You just missed a group of rangari with some disturbing news. The Shadowmoon Orcs have been spotted in the hills above Anguish Fortress. The scouts reported seeing strange rituals and beings of pure darkness. Those orcs must be up there trying to summon more minions from the Void!$B$BIf we do not intervene all we have gained in Shadowmoon Valley might be lost!', '', '', '', '', '', '', 0);
