INSERT INTO `quest_template_locale` VALUES (37645, 'ruRU', 'Властелин зверинца', 'Победите питомцев в боевой зоне.', 'Ты знаешь, что делать. Победи питомцев в гарнизоне, и я, быть может, награжу тебя.', '', '', '', '', '', '', 0);
