INSERT INTO `quest_template_locale` VALUES (37669, 'koKR', '전문 기술 건물', '전문 기술 건물을 지어야 합니다.', '전문 기술 건물의 도면을 구했군. 설계사의 작업대를 사용해서 소형 공터에 건물을 지어 보라고.', '', '', '', '', '', '', 0);
