INSERT INTO `quest_template_locale` VALUES (37669, 'zhCN', '建造专业技能建筑', '建造任意的专业技能建筑。', '你弄到了一个专业技能建筑的图纸！快用建筑设计桌选一块小空地来建造它吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (37669, 'zhTW', '建造專業建築', '建造任一專業建築。', '看來你已經取得專業建築的工程藍圖了！使用建築繪圖桌在小型的空建地上建造。', '', '', '', '', '', '', 0);
