INSERT INTO `quest_template_locale` VALUES (38008, 'ruRU', 'Дорога Славы', 'Убейте 5 бойцов Орды на Дороге Славы по просьбе Криса Кларки из Преграды Ветров.', 'Орда намеревается вытеснить нас из Ашрана, $n. Их планы не должны осуществиться. Отправляйся на Дорогу Славы и сократи численность ордынцев хотя бы на 5 воинов.', '', '', '', '', '', 'Поговорите с Крисом Кларки в Преграде Ветров.', 0);
