INSERT INTO `quest_template_locale` VALUES (38299, 'deDE', 'Geschöpfe von Draenor', 'Besiegt Erris die Sammlerin in einem Haustierkampf.', 'Ich bin immer auf der Suche nach neuen Haustieren für meine Sammlung und hier in Draenor habe ich schon einige ungewöhnliche Haustiere ergattert. Was haltet Ihr von einem Haustierkampf? Wenn Ihr mein bescheidenes Team besiegt, lernen wir beide vielleicht ein paar neue Tricks.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38299, 'esES', 'Alimañas de Draenor', 'Derrota a Erris la Coleccionista en un duelo de mascotas.', 'Me encanta coleccionar mascotas, y aquí en Draenor he conseguido algunas bastante inusuales. ¿Qué te parece si luchamos en un duelo? Si derrotas a mi humilde equipo, creo que tanto tú como yo aprenderemos algunos trucos nuevos.', '', '', '', '', '', '', 0);
