INSERT INTO `quest_template_locale` VALUES (38351, 'deDE', 'Portal fürs Handgepäck', 'Sprecht mit Varsha vor dem Geisterhaus.', 'Meine Gehilfen haben herausgefunden, wie sich Ogerportalsteine in brauchbare Gegenstände für unsere Garnison umwandeln lassen. Sprecht mit Varsha draußen, um Euren ersten Arbeitsauftrag zu starten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'esES', 'Portales portátiles', 'Habla con Varsha fuera del recinto de los espíritus.', 'Mis ayudantes han descubierto cómo convertir piedras de puerta ogra en objetos útiles para nuestra ciudadela. Habla con Varsha en el exterior para empezar tu primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'esMX', 'Portales portátiles', 'Habla con Varsha afuera del Recinto de los Espíritus.', 'Mis asistentes han descubierto una forma de convertir piedras de camino ogras en objetos útiles para nuestro cuartel. Habla con Varsha fuera para empezar con tu primer trabajo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'frFR', 'Portails mobiles', 'Parlez à Varsha devant l’Autel des esprits.', 'Mes assistants ont trouvé un moyen de transformer les pierres d’accès ogres en objets utiles pour votre fief. Parlez à Varsha, à l’extérieur, pour passer votre première commande.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'itIT', 'Portali portatili', 'Parla con Varsha fuori dal Rifugio degli Spiriti.', 'I miei assistenti hanno scoperto un modo per convertire le Pietre Energizzate degli Ogre in utili oggetti per la nostra Guarnigione. Parla con Varsha qui fuori per iniziare il tuo primo incarico di lavoro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'koKR', '유용한 차원석', '정기의 오두막 바깥에 있는 바르샤와 대화해야 합니다.', '내 조수들이 오우거 차원석을 주둔지에 유용한 물건들로 바꾸는 법을 알아냈다네. 바깥에 있는 바르샤와 얘기해 처음으로 제품을 주문해 보게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'ptBR', 'Portais portáteis', 'Fale com Varsha do lado de fora da Tenda dos Espíritos.', 'Meus assistentes descobriram como converter pedras de pórtico ôgrico em itens úteis para a nossa guarnição. Fale com Varsha para dar início à sua primeira ordem de serviço.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'ruRU', 'Портативные порталы', 'Поговорите с Варшей рядом с Обителью Духов.', 'Мои помощники нашли способ превращать путевые камни огров в кое-что полезное для гарнизона. Иди, поговори с Варшей – и можно будет приступать к выполнению первого заказа.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'zhCN', '便携式传送门', '在灵魂小屋外面与瓦莎谈一谈。', '我的助手们已经研究出了如何将食人魔道标石转化为对我们要塞有用的物品。到外面去跟瓦莎谈一谈，下达你的第一份产品订单吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38351, 'zhTW', '隨身傳送門', '到靈魂小屋外與瓦莎交談。', '我的助手已經知道要怎麼把巨魔甬道石轉化成我們要塞用得上的東西了。到外面找瓦莎交談，開始進行你的第一張訂單。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'deDE', 'Portal fürs Handgepäck', 'Sprecht mit Lehrling Var\'nath vor dem Magierturm.', 'Meine Gehilfen haben herausgefunden, wie sich Ogerportalsteine in brauchbare Dinge für unsere Garnison umwandeln lassen. Sprecht mit Lehrling Var\'nath draußen, um Euren ersten Arbeitsauftrag zu starten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'esES', 'Portales portátiles', 'Habla con el aprendiz Var\'nath fuera de la torre de magos.', 'Mis ayudantes han descubierto cómo convertir piedras de puerta ogra en objetos útiles para nuestra ciudadela. Habla con el aprendiz Var\'nath en el exterior para empezar tu primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'esMX', 'Portales portátiles', 'Habla con el aprendiz Var\'nath afuera de la Torre de los Magos.', 'Mis asistentes descubrieron cómo convertir piedras del camino ogras en objetos útiles para la fortaleza. Habla con el aprendiz Var\'nath afuera para iniciar tu primer pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'frFR', 'Portails mobiles', 'Parlez à l’apprenti Var’nath devant la tour des Mages.', 'Mes assistants ont trouvé un moyen de transformer les pierres d’accès ogres en objets utiles pour votre fief. Parlez à l’apprenti Var’nath, à l’extérieur, pour passer votre première commande.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'itIT', 'Portali portatili', 'Parla con l\'Apprendista Var\'nath fuori dalla Torre della Magia.', 'I miei assistenti hanno scoperto un modo per convertire le Pietre Energizzate degli Ogre in utili oggetti per la nostra Guarnigione. Parla con l\'Apprendista Var\'nath qui fuori per iniziare il tuo primo incarico di lavoro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'koKR', '유용한 차원석', '마법사 탑 바깥에 있는 수습생 바르나스와 대화해야 합니다.', '제 조수들이 오우거 차원석을 주둔지에 유용한 물건들로 바꾸는 법을 알아냈습니다. 바깥에 있는 수습생 바르나스와 얘기해 처음으로 제품을 주문해 보시지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'ptBR', 'Portais portáteis', 'Fale com o Aprendiz Var\'nath do lado de fora da Torre dos Magos.', 'Meus assistentes descobriram como converter pedras de pórtico ôgrico em itens úteis para a nossa guarnição. Fale com o Aprendiz Var\'nath para dar início à sua primeira ordem de serviço.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'ruRU', 'Портативные порталы', 'Поговорите с учеником Вар\'натом рядом с башней магов.', 'Мои помощники нашли способ превращать путевые камни огров в кое-что полезное для гарнизона. Поговори снаружи с Варшей – и можно приступать к выполнению первого заказа.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'zhCN', '便携式传送门', '在法师塔外面与学徒瓦纳斯谈一谈。', '我的助手们已经研究出了如何将食人魔道标石转化为对我们要塞有用的物品。到外面去跟学徒瓦纳斯谈一谈，下达你的第一份产品订单吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38354, 'zhTW', '隨身傳送門', '到法師塔外與見習法師瓦納斯交談。', '我的助手已經知道要怎麼把巨魔甬道石轉化成我們要塞用得上的東西了。到外面找見習法師瓦納斯交談，開始進行你的第一張訂單。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'deDE', 'Wir brauchen Bass', 'Besorgt einen Bass-Blaster, ein Laz-Tron-Laufwerk, einen phonischen Verstärker, einen F.N.K.-Kondensator und einen Klafter uraltes Holz.', 'Es ist so ruhig, jetzt, wo unsere Garnison ausnahmsweise mal nicht von feindlichen Truppen belagert wird.\n\nGut, dass Ihr mich habt, Kommandant! Ich weiß, wie wir ein bisschen Leben in die Bude bringen und gleichzeitig die Moral unserer Leute stärken.\n\nBesorgt mir ein paar Sachen aus ganz Draenor, und ich erfülle Eure Garnison mit Musik. Ich würde sogar ein paar Musikrollen aus meiner eigenen Sammlung beisteuern!\n\nWas sagt Ihr dazu?', '', '', 'Fix \"der Knirps\" Großzang', '', 'Fix \"der Knirps\" Großzang', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'esES', 'Que vuelva la música', 'Reúne un radiocasete portátil, un lector de discos Lasertron, un amplificador fónico, un condensador C.H.S.P. y una cuerda de madera antigua.', 'Está todo demasiado tranquilo por aquí, entre invasiones y tal.\n\n¡Pero estás de suerte, Comandante! ¡Yo puedo arreglarlo! Y subirle la moral a tus tropas al mismo tiempo.\n\nLo único que tienes que hacer es traerme unos cuantos objetos que encontrarás por Draenor y llenaré de música tu ciudadela. ¡Hasta te daré unos cuantos rollos de música de mi propia colección para ayudarte a empezar!\n\n¿Qué me dices?', '', '', 'Fix \"Peque\" Afinador', '', 'Fix \"Peque\" Afinador', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'esMX', 'Poniendo el ritmo', 'Consigue un revientabajos, un lector de discos Laz-tron, un amplificador fónico, un condensador S.P.R.K y un cable de madera antigua.', 'Está algo callado aquí entre las invasiones y todo.\n\n¡Pero tienes suerte, Comandante! Puedo arreglar eso y levantar el ánimo de tus tropas al mismo tiempo.\n\nTodo lo que debes hacer es traerme unos cuantos objetos de todas partes de Draenor y llenaré tu fortaleza de música. ¡Incluiré unos cuantos rodillos de música de mi colección personal para empezar!\n\n¿Qué dices?', '', '', 'Fix \"Pequeñín\" Llavegrande', '', 'Fix \"Pequeñín\" Llavegrande', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'frFR', 'À fond les basses !', 'Rassemblez un Caisson de basses, un Lecteur de disques Laz-tron, un Amplificateur phonique, un Condensateur E.K.L.R. et un Stère de bois ancien.', 'C’est plutôt calme par ici, entre les invasions et tout le reste.\n\nMais on dirait que c’est votre jour de chance, commandant ! Je peux vous arranger ça, et améliorer le moral des troupes par la même occasion.\n\nTout ce que vous avez à faire, c’est me rapporter des matériaux disséminés sur Draenor, et moi je me charge de faire vibrer les murs de votre fief avec de la bonne musique. Je peux même vous prêter quelques rouleaux à musique de ma collection personnelle pour commencer !\n\nQu’en dites-vous ?', '', '', '« P’tit » Réparo Grossclé', '', '« P’tit » Réparo Grossclé', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'itIT', 'Vai con i bassi', 'Recupera un Equalizzatore dei Bassi, un Mangiaschede Lasertronico, un Amplificatore Sonoro, un Condensatore S.P.R.K. e un Connettore di Legno Antico.', 'È tutto un po\' troppo tranquillo qui, considerando le invasioni e tutto il resto.\n\nSei $gfortunato:fortunata;, comandante! Ho la soluzione che potrebbe dare una bella spinta al morale delle tue truppe.\n\nDovrai solo portarmi alcuni oggetti che si trovano su Draenor e io penserò a diffondere della musica fantastica nella Guarnigione. Metterò a disposizione anche un paio di audioschede della mia collezione personale!\n\nChe ne dici?', '', '', 'Fix \"il Piccolo\" Granpistone', '', 'Fix \"il Piccolo\" Granpistone', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'koKR', '음악이 있는 곳', '저음 증폭기, 자동 음반 판독기, 음성 증폭기, 둥둥 축전기, 고대 나뭇가지를 모아야 합니다.', '침략이니 뭐니 하는 일이 없을 때의 여긴 너무 조용한 것 같아요.\n\n하지만 사령관님은 운이 좋으십니다! 제가 그 문제를 해결할 뿐 아니라 병사들의 사기까지 올려줄 수 있거든요.\n\n그저 드레노어 곳곳에서 물건을 좀 구해다 주시면, 제가 이 주둔지를 음악으로 채워 드리겠습니다. 심지어 제가 개인적으로 보유한 악보도 몇 개 넘겨 드릴 거라고요!\n\n자, 어떠세요?', '', '', '\"꼬맹이\" 픽스 비그스렌치', '', '\"꼬맹이\" 픽스 비그스렌치', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'ptBR', 'Grave bombando', 'Colete uma Bomba de Grave, um Toca-disco-tron, um Amplificador Fônico, um Capacitor S.P.R.K e um Cordão de Madeira de Anciente.', 'Entre uma invasão e outra, fica tranquilo até demais por aqui.\n\nMas você está com sorte, Comandante! Eu posso resolver esse problema e ainda elevar o moral da sua tropa.\n\nBasta você me trazer umas peças que estão espalhadas por Draenor que eu faço a música bombar na sua guarnição. Eu até posso colocar uns rolos de música da minha coleção para começar!\n\nQue me diz?', '', '', 'Tico Chavegrande, o \"Polegar\"', '', 'Tico Chavegrande, o \"Polegar\"', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'ruRU', 'Врубай басы', 'Соберите басовую примочку, устройство для считывания лаз-троновых дисков, усилитель звука, конденсатор И.С.К.Р. и древнюю древесину.', 'Между вторжениями тут такая скука.\n\nНо тебе повезло, командир! Я это исправлю! И заодно подниму боевой дух твоей армии.\n\nМне понадобится всего пара предметов, которые можно найти на Дреноре – и в твоем гарнизоне заиграет музыка! Я даже подкину пару мелодий из собственной коллекции!\n\nЧто скажешь?', '', '', 'Фикс \"Коротышка\" Большекрутс', '', 'Фикс \"Коротышка\" Большекрутс', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'zhCN', '动感低音', '收集低音炮、镭射读碟器、音效增幅器、S.P.R.K.电容器和古木电线。', '相对这片战火纷飞、动荡不安的大陆来说，这地方有点太过安静了。\n\n但你运气不错，指挥官！我能改变这个局面，还能让你的士兵们士气大振。\n\n你只要从德拉诺各地帮我找几样东西来，我就能让你的要塞里充满音乐。我甚至愿意贡献出几张私人珍藏的唱碟来帮你开个好头！\n\n这主意怎么样？', '', '', '“小小”菲克斯·巨扳', '', '“小小”菲克斯·巨扳', '', 0);
INSERT INTO `quest_template_locale` VALUES (38356, 'zhTW', '音浪太強', '收集低音喇叭，鐳射光碟機，音訊擴大機，S.P.R.K.電容器，以及古木纜線。', '這裡好像有點太安靜了，特別是沒有敵人入侵的時候。\n\n你還真走運，指揮官！交給我就對了，我還能幫你的部隊提升一下士氣。\n\n你只需從德拉諾的各處幫我找一些東西回來，我會讓你的要塞飄揚著美妙的音符。我也會給你一些我自己珍藏的樂譜，讓你立刻開始享受！\n\n你覺得怎麼樣？', '', '', '『小個子』修修‧畢格扳手', '', '『小個子』修修‧畢格扳手', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'deDE', 'Mögliche Nebenwirkungen: leichtes Untotsein', 'Tötet $1oa instabile Bären oder Seemänner.', 'Die Effekte dieses \"Nebenprodukts\" sind recht drastisch.$B$BAufgrund der rapiden Zellendegeneration werden die Subjekte blitzschnell in willenlose Hüllen verwandelt, die nur noch von blindem Hass auf alles Lebende angetrieben werden.$B$BWie Ihr Euch sicher vorstellen könnt, hat die lokale Fauna einigen Schaden genommen. Oh, und auch der Großteil der Besatzung...$B$BAuf jeden Fall wünsche ich Euch viel Glück!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'esES', 'Posibles efectos secundarios: una leve no-muerte', 'Mata a $1oa osos volátiles o marineros volátiles.', 'La exposición a este \"derivado\" en concreto es muy desagradable.$B$BLa degeneración celular acelerada reduce rápidamente al sujeto a una cáscara desgarbada, incapaz de razonar o pensar más allá de un violento e intenso odio hacia la calidez de la vida.$B$BComo puedes imaginar, la fauna local se ha visto afectada por el vertido. Ah, y la mayoría de la tripulación también.$B$B¡En fin, buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'esMX', 'Los efectos secundarios pueden incluir no-muerte leve', 'Mata a $1oa osos volátiles o marineros volátiles.', 'La exposición a este particular \"derivado\" es bastante desagradable.$B$BLa rápida degeneración celular convierte rápidamente al sujeto en un ser vacío, incapaz de razonar o pensar más allá del odio profundo que siente por la vida.$B$BComo imaginarás, la fauna local se ha visto afectada por el derrame. Ah, y la mayoría de la tripulación también... $B$BCómo sea, ¡buena suerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'frFR', 'Peut entraîner un léger effet de non-mort', 'Tuez $1oa Ours instables ou Marins instables.', 'Ce « dérivé » est particulièrement dangereux. Il vaut mieux ne pas s’y trouver exposé.$B$BUne dégénérescence rapide des cellules transforme rapidement le sujet en une carcasse titubante, incapable de raisonnement et uniquement habitée par une haine farouche de tout ce qui vit.$B$BEt comme vous pouvez vous en douter, la faune locale a été affectée par cette fuite. Oh, et la majorité de l’équipage, aussi…$B$BEnfin, bref, bonne chance !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'itIT', 'Gli effetti collaterali includono una leggera Non Morte', 'Uccidi $1oa Orsi Ammorbati o Marinai Ammorbati.', 'L\'esposizione a questo \"sottoprodotto\" è piuttosto sgradevole.$B$BUna rapida degenerazione delle cellule riduce il soggetto a un guscio barcollante incapace di ragionare o anche solo di pensare. L\'unica cosa che il soggetto è in grado di fare in questo stato è provare un odio smisurato per qualunque forma di vita.$B$BCome puoi immaginare, la fauna locale è stata infettata. Oh, dimenticavo, questo vale anche per gran parte dell\'equipaggio...$B$BComunque sia, buona fortuna!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'koKR', '부작용: 경미한 언데드화', '불안정한 곰 또는 불안정한 선원 $1oa명을 처치해야 합니다.', '이 특정 부산물에 노출되면 꽤 끔찍한 일이 벌어지지.$B$B세포가 빠르게 퇴화하면서 가죽만 남아 어기적거리며 걸어 다니게 된다네. 생명의 온기를 증오하고 파괴하려는 것 외에는 아무 생각도 할 수 없게 되고.$B$B자네 추측대로 이 부근에 사는 야생동물도 이런 증상을 보이는 것 같네. 아, 선원들도 대부분 그렇고...$B$B어쨌든, 행운을 비네!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'ptBR', 'Efeitos colaterais podem incluir leve morte', 'Mate $1oa Ursos Voláteis ou Marinheiros Voláteis.', 'A exposição a esse produto é bem nojenta.$B$BDegeneração rápida das células reduz a vítima a uma casca trôpega incapaz de usar a razão, repleta de ódio por tudo que é vivo.$B$BComo você pode imaginar, a fauna local foi afetada pelo derramamento. Ah, e a maioria da tripulação também...$B$BBom, boa sorte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'ruRU', 'Побочные эффекты могут стать причиной нежизни в легкой форме', 'Убейте $1oa |4нестабильного медведя:нестабильных медведей:нестабильных медведей; или |4нестабильного матроса:нестабильных матросов:нестабильных матросов;.', 'Конкретно этот \"побочный продукт\" оказывает довольно неприятное воздействие.$B$BБыстрая деградация клеток приводит к тому, что организм буквально на глазах превращается в безмозглую оболочку, видящую единственный смысл своего существования в истреблении всего живого.$B$BКак у тебя уже была возможность убедиться, от утечки биоматериала пострадала вся местная фауна. Ах да, и почти вся команда тоже...$B$BКороче, удачи!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'zhCN', '副作用可能包括轻度亡灵化', '消灭$1oa个变异熊或变异水手。', '曝露在这种特殊“副产品”中的后果非常可怕。$B$B细胞的快速衰竭会让主体成为呆滞的躯壳，失去理智和思考能力，并且充满暴力和对温暖生命的强烈憎恶。$B$B和你想象的一样，本地的动物已经被溢出物质感染了。噢，还有大部分的船员……$B$B不管怎么说，祝你好运！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38357, 'zhTW', '副作用可能包括輕微不死化', '殺掉$1oa個異變野熊或異變水手。', '暴露在這種特別的「副產品」中，下場十分不愉快。$B$B細胞加速衰退，會使個體快速退化成搖晃不穩的空殻，無法進行理智思考、只剩一股對溫暖生命的憎恨。$B$B你應該不難想像，溢出的物質已經對本地的動物群造成嚴重的影響。噢，還有大部份的船員也是…$B$B反正，祝你好運！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'deDE', 'Immer rein damit', 'Füllt den Sicherheitsbehälter mit instabilem Abfall.', 'Die Schwarze Rose war ein mobiles Marinelabor der Königlichen Apothekervereinigung. Und natürlich hatten wir auch ein paar... instabile \"Nebenprodukte\" an Bord.$B$BBedauerlicherweise ist eine dieser Substanzen während des Gefechtes mit dem Allianzschlachtschiff über Bord gegangen. Und es hat die Küste ganz schön hart getroffen.$B$BDie K.A.V. muss sich darum kümmern, bevor wir weiter ins Inland drängen.$B$BBitte nehmt Euch einen Sicherheitsbehälter und füllt ihn auf. Dann werden wir uns später um die korrekte Entsorgung kümmern.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'esES', 'Chapapoteando', 'Llena la unidad de contención con residuos volátiles.', 'La Rosa Negra era un laboratorio flotante de la Sociedad Real de Boticarios. A bordo teníamos ciertos... \"derivados\" volátiles, claro.$B$BLamentablemente, una de dichas sustancias se vertió durante nuestra escaramuza con la nave de guerra de la Alianza. Como ves, está afectando gravemente a la costa.$B$BLa SRB debe remediar esta situación antes de adentrarnos más en tierra.$B$BCoge una unidad de contención y llénala de residuos. Para que luego nosotros podamos eliminarlos como es debido, claro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'esMX', 'A bombear', 'Llena la unidad de contención con desperdicio volátil.', 'La Rosa Negra era un laboratorio marítimo de la Sociedad Real de Boticarios. Naturalmente, teníamos algunos... “derivados” volátiles a bordo.$B$BPor desgracia, una de esas sustancias se filtró durante nuestra contienda con la nave de guerra de la Alianza. Como podrás ver, le está causando mucho daño a la costa.$B$BLa SRB debe solucionar la situación antes de seguir avanzando.$B$BToma una unidad de contención y llénala de desperdicio, para deshacernos de él más tarde, claro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'frFR', 'Opération de pompage', 'Remplissez l’Unité de confinement de Déchets instables.', 'La Rose noire était un laboratoire marin de la Société royale des apothicaires. Comme vous devez vous en douter, il y avait quelques… « dérivés » instables à bord.$B$BEt, à notre grand regret, l’une de ces substances s’est répandue durant notre escarmouche contre la canonnière de l’Alliance. Comme vous pouvez le constater, l’effet sur la côte est bien loin d’être négligeable.$B$BLa S.R.A. se doit de remédier à la situation avant que nous progressions vers l’intérieur des terres.$B$BJe vous en prie, prenez une unité de confinement et stockez-y les déchets contaminés. Nous nous en débarrasserons comme il se doit ultérieurement.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'itIT', 'Raccogliere i rifiuti', 'Riempi l\'Unità di Contenimento con dei Rifiuti Instabili.', 'La Rosa Nera era un laboratorio marino dell\'Ordine Reale degli Speziali. Naturalmente, avevamo qualche... \"sottoprodotto\" instabile a bordo.$B$BSfortunatamente, una di queste sostanze è stata rilasciata durante la nostra ultima schermaglia con una nave dell\'Alleanza. Come puoi vedere, ha causato parecchi danni alla costa.$B$BL\'O.R.S. deve porre rimedio a questa situazione prima di spingerci oltre verso l\'interno.$B$BPrendi questa unità di contenimento e riempila con dei rifiuti. Ce ne occuperemo più tardi... al momento giusto, ovviamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'koKR', '폐기물 취급 주의', '보관 장치에 불안정한 쓰레기를 채워야 합니다.', '왕립 연금술 학회에서는 흑장미호를 해상 연구소로 사용했었네. 사정이 그렇다 보니 배에는 불안정한... \"부산물\"이 많았지.$B$B안타깝게도 얼라이언스 비행포격선과 격전을 벌이는 동안 그런 물질 중 하나가 유출되었네. 그 때문에 해안이 이런 꼴로 변했고.$B$B내륙으로 이동하기 전에 왕립 연금술 학회가 책임지고 이 문제를 해결해야 하네.$B$B이 보관 장치를 가지고 가서 불안정한 쓰레기를 채워 오게... 물론 우리가 나중에 적절한 절차에 따라 폐기하겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'ptBR', 'Lixo extraordinário', 'Encha a Unidade de Contenção com Refugo Volátil.', 'A Rosa Negra era um laboratório naval da Real Sociedade de Boticários. Naturalmente, nós tínhamos alguns materiais... \"voláteis\" a bordo.$B$BInfelizmente, uma dessas substâncias foi derramada durante nossa luta com a aeronave da Aliança. Como você pode ver, a costa está sofrendo um impacto bem agressivo.$B$BA R.S.B. precisa remediar essa situação antes que penetremos mais para o interior.$B$BPegue uma unidade de contenção e encha-a com o produto químico — para nós descartamos depois, é claro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'ruRU', 'Закачка отходов', 'Заполните герметичный контейнер нестабильной субстанцией.', '\"Черная роза\" была мобильной морской испытательной лабораторией Королевского фармацевтического общества. Естественно, на борту находились нестабильные... \"побочные продукты\" наших исследований.$B$BК большому сожалению, при стычке с боевым кораблем Альянса случился выброс одного из таких продуктов в окружающую среду. Как видишь, все побережье при этом изрядно пострадало.$B$BКФО должно ликвидировать последствия катастрофы, прежде чем мы сможем продвигаться дальше.$B$BВозьми вот этот контейнер и заполни его нестабильной субстанцией. Ее можно будет... уничтожить потом в полном соответствии с техникой безопасности.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'zhCN', '废料密封', '用密封装置收集易爆废料。', '黑玫瑰号曾是皇家药剂师协会的航海实验室。船上自然有一些不稳定的……“副产品”。$B$B可惜的是，在我们和联盟舰艇的冲突之中，有一种这类物质被泄露了出去。如你所见，它让沿岸损失惨重。$B$B在我们进一步深入内陆之前，协会必须进行补救。$B$B请拿上一个密封装置去收集废料——以便我们稍后进行处理。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38358, 'zhTW', '吸滿抽飽', '將易燃廢料裝進封存裝置。', '黑玫瑰號原本是皇家藥劑師學會的航海實驗室。船上自然有一些不穩定的…「副產品」。$B$B不幸的是，當我們和聯盟砲艇發生衝突時，這樣的物質漏了出來。如你所見，它讓沿岸損傷慘重。$B$B皇家藥劑師學會必須先挽救目前的局面，才能繼續往內陸推進。$B$B請拿一個封存裝置，並裝滿廢料 - 當然是為了讓我們之後可以妥善處理。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'deDE', 'Das Schicksal der Windläufer', 'Sucht am Wrack der Windläufer und an der nahe gelegenen Küste nach Anzeichen auf Sylvanas.', 'Sylvanas zu finden, hat jetzt oberste Priorität!$B$BWir können einen größeren Suchbereich abdecken, wenn wir uns aufteilen. Ich werde mit einem Team in die Hügel über dem Lager der Feinde gehen, und Ihr untersucht das Wrack selbst nach Anzeichen der Besatzung.$B$BWenn wir da oben fertig sind, treffen wir uns am anderen Ende des Strandes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'esES', 'El destino de El Brisaveloz', 'Busca entre los restos de El Brisaveloz y en la costa cercana algún rastro de Sylvanas.', '¡Encontrar a Sylvanas es nuestra máxima prioridad!$B$BCubriremos más terreno si nos separamos. Yo conduciré un equipo a las colinas que dominan el campamento enemigo mientras tú buscas entre los restos alguna señal de la tripulación.$B$BEn cuanto hayamos despejado la zona de arriba me reuniré contigo al otro lado de la playa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'esMX', 'El destino del Brisaveloz', 'Investiga los restos del Brisaveloz y la costa cercana para ver si hay señales de Sylvanas.', '¡Encontrar a Sylvanas es nuestra única prioridad!$B$BAvanzaremos más rápido si nos dividimos. Yo llevaré un equipo a las colinas sobre el campamento enemigo, mientras tú investigas los restos para ver si hay señales de la tripulación.$B$BUna vez que despejemos el terreno elevado, me reuniré contigo en el extremo de la playa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'frFR', 'Le destin du Coursevent', 'Fouillez l’Épave du Coursevent et la côte environnante pour essayer de retrouver la trace de Sylvanas.', 'Retrouver Sylvanas est notre priorité absolue !$B$BNous couvrirons plus de terrain si nous nous séparons. Je vais emmener une équipe dans les collines qui surplombent le camp ennemi. De votre côté, fouillez l’épave pour essayer de retrouver l’équipage.$B$BUne fois les hauteurs dégagées, je vous retrouverai à l’extrémité de la plage.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'itIT', 'Il fato della Ventolesto', 'Ispeziona i relitti della Ventolesto e la costa vicina in cerca di qualche indizio su Sylvanas.', 'Trovare Sylvanas è la nostra priorità!$B$BCopriremo una distanza maggiore se ci dividiamo. Condurrò una squadra sulle colline sopra al campo del nemico mentre cerchi un qualunque indizio sull\'equipaggio fra i relitti.$B$BUna volta setacciata la zona, ti attenderò alla fine della costa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'koKR', '윈드러너의 운명', '윈드러너호의 잔해와 주변 해안에서 실바나스의 흔적을 찾아야 합니다.', '일단 실바나스 님부터 찾아야 하네!$B$B흩어져서 찾아보는 게 낫겠군. 난 다른 이들을 이끌고 적의 야영지 위쪽 언덕을 수색할 테니, 자네는 윈드러너호의 잔해를 수색해 선원들이 남긴 흔적은 없는지 찾아보게.$B$B위쪽 언덕 수색이 끝나면 해변 끝으로 가겠네. 그곳에서 만나세.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'ptBR', 'O destino do Correventos', 'Vasculhe os destroços do Correventos e a costa próxima em busca de Sylvana.', 'Encontrar Sylvana é nossa prioridade!$B$BNós vamos cobrir mais terreno se nos dividirmos. Eu vou liderar uma equipe até as colinas acima do acampamento inimigo enquanto você vasculha os destroços em busca da tripulação.$B$BDepois que eliminarmos a oposição no alto, encontro você no final da praia.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'ruRU', 'Судьба Ветрокрылой', 'Осмотрите место крушения \"Ветрокрылой\" и побережье в поисках следов Сильваны.', 'Отыскать Сильвану – наша первоочередная задача!$B$BНам стоит разделиться – так мы прочешем большую площадь. Я поведу свой отряд в холмы над вражеским лагерем, а ты осмотри само место крушения – может быть, кому-то из команды корабля удалось выжить.$B$BКогда мы захватим и зачистим высоту, я отправлюсь на дальний край побережья и буду ждать тебя там.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'zhCN', '风行者的命运', '搜索风行者号的残骸以及附近的海岸，寻找希尔瓦娜斯的踪迹。', '寻找希尔瓦娜斯是我们的唯一优先事项！$B$B分头找的话可以搜索更大的范围。我带一支队伍到敌人营地上方的山岭去找，你到坠毁的地方去搜索飞艇的残骸，寻找船员的踪迹。$B$B等我们搜完高地，就到海滩的另一头汇合。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38360, 'zhTW', '風行者的命運', '搜索風行者號的殘骸和周遭的海岸，尋找希瓦娜斯的下落。', '找到希瓦娜斯是當前唯一的首要之務！$B$B如果我們分頭行動，可以搜索更大的範圍。我帶領一支隊伍前往能夠俯視敵人營地的山丘，你就留在這裡搜索殘骸，尋找船員留下的蹤跡。$B$B一旦我們把高地搜索完畢，我就到海灘另一頭和你會面。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'deDE', 'Der Zorn des Pestrufers', 'Tötet $1oa Fallschirmjäger der 7. Legion oder Greifenreiter der Himmelsfeuer.', 'An diesem Brocken werden sich die Allianzhunde verschlucken. Sie haben die Bansheekönigin angegriffen und sich den Zorn ihres Champions zugezogen!$B$BGenau wie meine Bogeneinheiten, Axtkämpfer und Höllenhunde werdet Ihr ein Werkzeug meiner Rache sein. Ich will, dass Ihr Leichenberge hinterlasst, $C. Und ich werde das Gleiche tun. Wenn wir uns wiedertreffen, will ich Blut an Euren Händen sehen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'esES', 'La ira del Clamañublo', 'Mata a $1oa paracaidistas de la Séptima Legión o jinetes de grifos de El Abrasacielos.', 'Los perros de la Alianza no saben dónde se han metido. ¡Han atacado a la Reina Alma en Pena y provocado la ira de su campeón!$B$BAl igual que mi arco, mis hachas y mis sabuesos, serás un agente de venganza. Haz que se acumulen los cadáveres, $c, que lo mismo haré yo. Quiero ver sangre en tus manos cuando volvamos a vernos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'esMX', 'Cólera del Clamañublo', 'Mata $1oa paracaidistas de la Séptima Legión o jinetes de grifos del Abrasacielos.', 'Esos perros de la Alianza no saben en lo que se están metiendo. ¡Atacaron a la Reina alma en pena e incurrieron en la ira de su campeón!$B$BAl igual que mi arco, mis hachas y mis sabuesos, tú serás un miembro más de mi venganza. Acumula cadáveres, $c, y recuerda que yo estaré haciendo lo mismo. La próxima vez que nos veamos, espero ver tus manos llenas de sangre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'frFR', 'Le courroux du Flétrisseur', 'Tuez $1oa Parachutistes de la 7e Légion ou Chevaucheurs de griffons du Brûleciel.', 'Ces chiens de l’Alliance ont eu les yeux plus gros que le ventre. Ils ont attaqué la reine banshee et, ce faisant, ils se sont exposés au courroux de son champion !$B$BTout comme mon arc, mes haches et mes chiens, vous serez l’instrument de ma vengeance ! Empilez les cadavres de votre côté, $c, et sachez que je ferai de même. La prochaine fois que nous nous verrons, j’espère voir vos mains maculées de sang.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'itIT', 'L\'ira di Selvamorta', 'Uccidi $1oa Paracadutisti della VII Legione o Cavalieri di Grifoni della Fuoco Celeste.', 'Quei cani dell\'Alleanza hanno fatto il passo più lungo della gamba. Hanno attaccato la Regina Banshee e risvegliato l\'ira del suo campione!$B$BCome il mio arco, la mia ascia e i miei segugi, tu sarai $gun portatore:una portatrice; di vendetta. Uccidine il più possibile, $C, e sappi che io farò lo stesso. Quando ci incontreremo nuovamente, mi aspetto di vedere le tue mani coperte di sangue.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'koKR', '블라이트콜러의 분노', '7군단 공수부대원 또는 하늘불꽃호 그리핀 기수 $1oa명을 처치해야 합니다.', '얼라이언스 조무래기들이 상대를 잘못 골랐네. 감히 밴시 여왕님을 공격해서 여왕님을 따르는 용사를 진노하게 하다니!$B$B내 활과 도끼, 사냥개처럼 자네도 내 복수의 도구가 되어 주게. $c, 이제부터 함께 얼라이언스 놈들의 시체를 산처럼 쌓아 보세. 다음에 만날 때는 자네의 두 손에 피가 흥건하길 기대하겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'ptBR', 'A fúria do Arauto da Praga', 'Mate $1oa membros da 7ª Legião Paraquedista ou Cavalga-grifos do Celesfogo.', 'Os cães da Aliança morderam mais do que podem mastigar. Eles atacaram a Rainha Banshee e causaram a fúria do seu campeão!$B$BVocê será meu agente de vingança, assim como meus arcos, machados e cães. Empilhe os cadáveres, $c, e saiba que eu farei o mesmo. Quando nos encontrarmos da próxima vez, quero ver sangue nas suas mãos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'ruRU', 'Гнев Гнилостня', 'Убейте $1oa |4парашютиста:парашютистов:парашютистов; 7-го легиона или |4наездника на грифоне:наездников на грифонах:наездников на грифонах; \"Небесного огня\".', 'Псы Альянса отхватили кусок не по зубам. Они напали на Королеву-банши и пробудили ярость ее защитника!$B$BПодобно моему луку, моим топорам и верным псам, ты тоже станешь инструментом нашей мести. Убивай этих гнусных альянсовцев всех без разбору, |3-6($c), и знай, что я в это время тоже не буду сидеть сложа руки. Надеюсь, когда мы встретимся снова, твои руки будут по локоть в крови.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'zhCN', '凋零者之怒', '消灭$1oa个第七军团伞兵或天火号狮鹫骑士。', '联盟的渣滓这次惹到不该惹的人了。他们对女妖之王的攻击点燃了我的怒火！$B$B就像我的长弓、利斧和猎犬一样，你也将成为复仇的象征。让敌人的尸体堆积成山吧，$c，别忘了我的动作不会比你慢。下次再会时，希望能看到你的双手沾满鲜血。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38361, 'zhTW', '凋零者之怒', '殺掉$1oa個第七軍團傘兵或天火獅鷲騎士。', '這下聯盟的混蛋可真的踢到鐵板了。他們攻擊了女妖之王，必須讓他們嚐嚐女妖勇士的怒火！$B$B就如我的弓、我的斧和我的獵犬一樣，你會成為復仇的利刃。$c，讓我們一起把敵人的屍體堆得像山一樣高。下次見面時，我希望能看到你的雙手沾滿鮮血。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'deDE', 'Eine grausame Trophäe', 'Schlagt Than Sturmhimmel den Kopf ab.', 'Es hat den Anschein, dass Sylvanas nicht will, dass man ihr folgt... weder wir noch andere.$B$BUnd ihr Wille zählt. Ich habe ihn nicht zu hinterfragen, sondern ihm nur zu gehorchen.$B$BIn diesen nahe gelegenen Hügeln bereitet sich gerade ein Greifenmeister auf den Abflug vor. Ich gehe davon aus, dass er die Königin aus der Luft suchen will. Meine Waldläufer sind schon in Stellung gegangen. Sie warten nur noch auf den richtigen Moment.$B$BWenn dieser Auftrag erledigt ist, will ich seinen Kopf als Trophäe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'esES', 'Un trofeo lúgubre', 'Consigue la cabeza de Thane Cielobravo.', 'Parece que Sylvanas no desea que la sigan; ni nosotros ni nadie.$B$BEs su voluntad y no me corresponde cuestionarla. Solo obedecerla.$B$BEn las colinas cercanas, un maestro de grifos se prepara para alzar el vuelo, sin duda para perseguir a nuestra Reina desde los cielos. Mis exploradores están a la espera: solo tenemos que cerrar nuestras fauces en torno a él.$B$BCuando esto acabe, su cabeza será mía.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'esMX', 'Un trofeo aterrador', 'Córtale la cabeza a Thane Cielobravo.', 'Al parecer Sylvanas no quiere que nadie la siga, ni siquiera nosotros.$B$BHará lo que desee, no tengo derecho a cuestionarla. Solo debo obedecerla.$B$BEn las colinas cercanas, hay un maestro de grifos preparándose para despegar, sin duda para perseguir a nuestra reina desde el cielo. Mis forestales están a la espera, solo queda acabarlo.$B$BTendré su cabeza cuando todo esto haya terminado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'frFR', 'Un trophée des plus funestes', 'Récupérez la Tête du thane Ciel-Furieux.', 'Il semblerait que Sylvanas n’ait pas envie d’être suivie… pas plus par nous que par les autres.$B$BCette décision lui appartient. Je n’ai pas à m’interroger sur ses motivations, juste à lui obéir.$B$BDans les collines non loin d’ici, un maître des griffons s’apprête à s’envoler, sans doute afin de poursuivre notre reine par la voie des airs. Mes forestiers l’attendent déjà. Il ne nous reste plus qu’à refermer les mâchoires sur notre proie.$B$BJ’aurai sa tête quand tout sera fini !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'itIT', 'Un lugubre trofeo', 'Prendi la testa di Thane Cielobrado.', 'Sembra che Sylvanas non voglia essere seguita, né da noi né da nessun altro.$B$BSe questo è ciò che vuole, non sarò di certo io a ostacolarla. Devo solamente obbedire.$B$BSulle colline qui vicino, un Maestro dei Grifoni sta per prendere il volo, sicuramente nel tentativo di seguire la nostra regina. Le mie Guardaboschi sono in attesa e aspettano solo il tuo segnale per catturarlo.$B$BVoglio la sua testa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'koKR', '음울한 전리품', '영주 와일드스카이의 머리카락을 획득해야 합니다.', '실바나스 여왕님은 적은 물론 우리에게도 행방을 감추고 싶어하시는 것 같네.$B$B여왕님의 뜻이 그렇다면 난 의문 따위는 가질 수 없네. 그저 복종할 뿐.$B$B이 근처 언덕으로 가면 비행을 준비 중인 그리핀 조련사가 있을 걸세. 공중에서 여왕님을 추적하려는 수작이 틀림없어. 내 휘하의 순찰대원들이 이미 그 주변에 매복해 있으니 놈은 독 안에 든 쥐나 마찬가지야.$B$B자네가 일을 마무리하고 놈의 머리카락을 가져오게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'ptBR', 'Troféu macabro', 'Traga a Cabeça do Thane Céu-feroz.', 'Parece que Sylvana não quer ser seguida — nem por nós nem por ninguém.$B$BÉ a vontade dela, e não me compete questionar isso. Só obedecer.$B$BNuma colina perto daqui, um mestre de grifos está se preparando para alçar voo, sem dúvida para perseguir nossa rainha no céu. Meus patrulheiros estão esperando. Só precisamos fechar o cerco em cima dele.$B$BQuando isso acabar, eu quero a cabeça dele.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'ruRU', 'Мрачный трофей', 'Добудьте голову тана Диконеба.', 'Похоже, Сильвана желает пока оставаться в одиночестве.$B$BЧто ж, не наше дело задаваться вопросами о намерениях повелительницы. Наша задача – служить ей и исполнять ее волю.$B$BСреди холмов, что неподалеку отсюда, наши разведчики видели укротителя грифонов Альянса. Очевидно, он собирается преследовать нашу королеву. Мои следопыты пока выжидают. Он уже в ловушке – только еще не подозревает об этом.$B$BМне нужна его голова.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'zhCN', '残忍的战利品', '获取蛮天领主的徽记。', '看来希尔瓦娜斯不希望任何人跟随——不管是我们还是其他的人。$B$B她有自己的想法，我不该质疑，只需要服从。$B$B附近的山岭中有一位狮鹫骑士正在准备起飞，打算从空中搜寻我们的女王。我的游侠们已经就位，随时准备挟制住他。$B$B在这一切结束以后，我要拿到他的徽记。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (38362, 'zhTW', '殘忍的戰利品', '拿下蠻空族長的頭。', '看來希瓦娜斯不希望被跟蹤 - 不管是我們還是其他人都一樣。$B$B這是她的意志，不容質疑，只有服從。$B$B附近的山丘上，有一位獅鷲騎士正準備起飛，打算從空中尾隨女王。我的遊俠已經埋伏待命 - 只差最後一步。$B$B在這結束之後，我要拿下他的腦袋。', '', '', '', '', '', '', 0);
