INSERT INTO `quest_template_locale` VALUES (39082, 'deDE', 'An die Arbeit', 'Trefft Euch mit Solog am Trockendock.', 'Jetzt, wo wir eine Werft haben, müssen wir uns an die Arbeit machen und Schiffe bauen! Solog ist schon zu den Trockendocks gegangen. Wollt Ihr ihn nicht dort aufsuchen? Er sagte, er habe einen Plan.', '', 'Sucht Solog Roark am Trockendock.', 'Solog Roark', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'esES', 'Manos a la obra', 'Reúnete con Solog en el dique seco.', 'Ahora que se ha completado el astillero, ¡tenemos que ponernos a construir barcos! Solog ha ido al dique seco. ¿Por qué no te reúnes allí con él? Dijo que tenía un plan.', '', 'Encuentra a Solog Roark en el dique seco.', 'Solog Roark', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'esMX', 'A trabajar', 'Encuentra a Solog en los muelles secos.', 'Ahora que el astillero está completo, necesitamos construir naves. Solog se fue a los muelles secos, búscalo allí. Dice que tiene un plan.', '', 'Encuentra a Solog Roark en los muelles secos.', 'Solog Roark', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'frFR', 'Au travail !', 'Rejoignez Solog à la cale sèche.', 'Maintenant que ce chantier naval est terminé, il faut s’occuper de produire des navires ! Solog s’est rendu à la cale sèche. Pourquoi ne pas aller le rejoindre ? Il a dit qu’il avait un plan.', '', 'Retrouvez Solog Roark à la cale sèche.', 'Solog Roark', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'itIT', 'Al lavoro!', 'Incontra Solog presso il bacino di carenaggio.', 'Ora che il Cantiere Navale è completo, dobbiamo iniziare a costruire delle navi! Solog è andato al bacino di carenaggio. Perché non vai laggiù a incontrarlo? Ha detto di avere un piano.', '', 'Trova Solog Roark presso il bacino di carenaggio.', 'Solog Roark', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'koKR', '작업을 시작하자', '배 건조장에 있는 솔로그를 만나야 합니다.', '이제 조선소가 완공되었으니, 열심히 함선을 건조하는 일만 남았군요! 솔로그가 배 건조장으로 달려가는 걸 보았습니다. 거기 가서 그를 만나보시는 게 어떻습니까? 뭔가 계획이 있다던데요.', '', '배 건조장에 있는 솔로그를 찾아야 합니다.', '솔로그 로아크', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'ptBR', 'Ao Trabalho!', 'Encontre Solog no porto seco.', 'Agora que o estaleiro está completo, vamos começar a construir navios! Solog foi até o porto seco, por que não o encontra lá? Ele disse que tinha um plano.', '', 'Encontre Solog Roark no porto seco.', 'Solog Roark', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'ruRU', 'За работу!', 'Найдите Солога в сухом доке.', 'Раз пристань теперь у нас уже есть, пора осваивать кораблестроение! Солог уже в сухом доке, так что ищи его там. Он сказал, что у него есть какой-то план.', '', 'Разыщите Солога в сухом доке.', 'Солог Рырк', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'zhCN', '开始干活', '在干船坞与索洛格碰头。', '现在有了这座船坞，就该忙着造船了！索洛格已经去了干船坞，你要不要去那里见见他？他说他已经有主意了。', '', '在干船坞与索洛格碰头。', '索洛格·拉克', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39082, 'zhTW', '我們開工吧', '到乾船塢和所羅格會面。', '我們現在有了這座船塢，得開始忙著造船了！所羅格跑去乾船塢了，你直接去下面找他如何？他說他有個計畫。', '', '到乾船塢找所羅格‧羅亞克。', '所羅格‧羅亞克', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'deDE', 'Tötet sie alle!', 'Tötet 200 gegnerische Spielercharaktere.', 'Findet sie, jagt sie, tötet sie. Erteilt unseren Feinden eine Lektion, die sie niemals vergessen werden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'esES', '¡Mátalos a todos!', 'Mata a 200 jugadores enemigos.', 'Encuéntralos, persíguelos, mátalos. Enséñales a nuestros enemigos una lección que nunca olviden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'esMX', '¡Mátalos a todos!', 'Mata a 200 jugadores enemigos.', 'Encuéntralos, cázalos, mátalos. Enséñales a tus enemigos una lección que nunca olvidarán.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'frFR', 'Tuez-les jusqu’au dernier !', 'Tuez 200 personnages-joueurs ennemis.', 'Traquez-les, trouvez-les et tuez-les. Donnez à nos ennemis une leçon qu’ils ne sont pas près d’oublier.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'itIT', 'Uccidili tutti!', 'Uccidi 200 membri della fazione opposta.', 'Trovali, dai loro la caccia, uccidili. Dai ai nostri nemici una lezione che non dimenticheranno mai.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'koKR', '모두 쓸어버려라!', '적 플레이어 200명을 처치해야 합니다.', '적을 찾아내 추격하고, 모두 처치해 버리십시오. 놈들에게 평생 잊지 못할 교훈을 가르쳐줘야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'ptBR', 'Mate todos!', 'Mate 200 jogadores inimigos.', 'Encontre eles, cace eles, mate eles. Ensine uma lição a nossos inimigos que eles jamais esquecerão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'ruRU', 'Убить их всех!', 'Убейте 200 членов противоположной фракции.', 'Выслеживай их – и убивай. Преподай нашим врагам урок, который они нескоро забудут.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'zhCN', '杀光他们！', '杀死200名敌对玩家。', '找到他们，追猎他们，杀死他们。给我们的敌人上一堂永生难忘的课！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39090, 'zhTW', '殺光他們！', '殺死200個敵對玩家。', '找到他們，追殺他們，宰掉他們。給敵人一個永生難忘的教訓。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'deDE', 'Aszendentenriten', 'Versucht, den Segen an den vier Schreinen im Tempel von Skold-Ashil zu erlangen und dann mit der Statue zu kommunizieren.', 'Unsere nächsten Schritte sind klar. Wir müssen in die Kammer eindringen und verhindern, dass Sylvanas die Macht erlangt, weitere Val\'kyr zu erschaffen. \n\nDer Eingang ist versperrt und hat bis jetzt auch sie davon abgehalten, dort einzudringen. Aber Ihr habt vielleicht den Schlüssel zu dieser Barriere bereits entdeckt.\n\nIhr müsst $gder nächste Aszendent:die nächste Aszendentin; werden.\n\nVollzieht die auf den Schilden dargestellten Riten und schaltet die Barriere vor der Kammer aus. Ich werde Graumähne von unseren Ergebnissen berichten. Er wird am Kampf gegen Sylvanas teilnehmen wollen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'esES', 'Convertirse en ascendiente', 'Obtén las bendiciones de los cuatro santuarios del templo de Skold-Ashil y luego entra en comunión con la estatua.', 'Ya sabemos qué hacer. Debemos entrar en la cámara y evitar que Sylvanas obtenga la capacidad de crear más Val\'kyr.\n\nLa entrada está bloqueada, lo que la ha mantenido fuera hasta ahora, pero es posible que hayas desvelado la clave del bloqueo.\n\nDebes convertirte en $gel próximo:la próxima; ascendiente.\n\nCompleta los ritos tal como se muestra en los escudos y elimina la barrera hacia la cámara. Yo informaré a Cringrís de nuestros hallazgos; querrá enfrentarse a Sylvanas en persona.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'esMX', 'Convirtiéndose en el ascendente', 'Busca las bendiciones en los cuatro altares del templo en Skold-Ashil, luego comulga con la estatua.', 'Está claro lo que tenemos que hacer. Debemos entrar a la bóveda e impedir que Sylvanas obtenga el poder para crear más val\'kyr. \n\nLa entrada está bloqueada, por eso aún no pudo entrar, pero tal vez ya hayas descubierto la forma de atravesar la barrera.\n\nDebes convertirte en $Gel:la; siguiente ascendente\n\nCompleta los rituales como aparecen en los escudos y derriba la barrera de la bóveda. Informaré a Cringris sobre lo que descubrimos. Querrá enfrentarse a Sylvanas cara a cara.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'frFR', 'Ascension', 'Obtenez la bénédiction de chacun des quatre autels du Temple de Skold-Ashil, puis entrez en communion avec la Statue.', 'Notre objectif est clair, désormais. Nous devons pénétrer dans le caveau et empêcher Sylvanas de s’emparer du pouvoir de créer d’autres Val’kyrs.\n\nL’accès est barré, ce qui explique sans doute pourquoi elle n’a pas encore réussi à entrer, mais il semblerait que vous ayez déjà découvert la clé permettant de franchir cet obstacle.\n\nVous devez devenir $gle prochain ascendant:la prochaine ascendante;.\n\nAccomplissez les rites détaillés sur les boucliers et abattez la barrière du caveau. J’aviserai Grisetête de nos découvertes, car je sais qu’il voudra affronter Sylvanas en personne.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'itIT', 'Ascendere', 'Ricevi le benedizioni dei quattro santuari nel tempio di Skold-Ashil, quindi entra in comunione con la statua.', 'Ora è chiaro ciò che ci aspetta. Dobbiamo entrare nella volta e impedire a Sylvanas di ottenere il potere per creare altre Val\'kyr.\n\nL\'ingresso è sbarrato, e probabilmente è per questo che non sono ancora entrati, ma potresti aver già scoperto la chiave per attraversare questa barriera.\n\nDevi diventare $gil prossimo Asceso:la prossima Ascesa;.\n\nCompleta i riti descritti sugli scudi e abbassa la barriera per accedere alla volta. Io invierò un messaggio a Mantogrigio sulle nostre scoperte. Vorrà affrontare Sylvanas di persona.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'koKR', '승천 의식', '스콜드아실의 사원에 있는 제단 네 곳에서 축복을 받고 조각상과 교감해야 합니다.', '이제 목표가 뚜렷해졌어요. 석실에 들어가서 실바나스가 발키르를 만들 힘을 얻지 못하도록 막아야 해요.\n\n실비나스도 입구가 막혀 있어서 아직 못 들어간 것 같지만, 당신은 이미 이 방벽에 대한 비밀을 이미 알아낸 것 같네요.\n\n당신이 다음번 승천자가 돼야 해요.\n\n방패에 나온 대로 의식을 마치고 방벽 너머의 석실로 가세요. 우리가 알아낸 사실은 제가 그레이메인 님께 전해 드릴게요. 그레이메인 님도 실바나스와 직접 대면하고 싶으실 테니까요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'ptBR', 'Tornando-se o ascendente', 'Busque a bênção dos santuários no templo de Skold-Ashil e comungue com a estátua.', 'Nosso caminho é claro. Precisamos entrar na câmara e impedir que Sylvana obtenha o poder de criar mais Val\'kyr. \n\nA entrada está bloqueada, o que provavelmente foi o que a manteve fora até agora, mas você pode já ter descoberto a chave desta barreira.\n\nVocê precisa se tornar $go próximo:a próxima; ascendente.\n\nComplete os rituais representados nos escudos e derrube a barreira da câmara. Eu informarei Greymane sobre nossas descobertas. Ele vai querer enfrentar Sylvana pessoalmente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'ruRU', 'Перерождение', 'Получите благословение четырех святилищ в храме Скольд-Ашиля и установите контакт со статуей.', 'План наших дальнейших действий ясен. Мы должны проникнуть в склеп и не дать Сильване обрести возможность создавать новых валь\'кир. \n\nВход в склеп запечатан, и, видимо, только поэтому она до сих пор не получила желаемого, но, возможно, тебе уже удалось найти ключ от этого барьера.\n\nТебе предстоит стать $gследующим Перерожденным:следующей Перерожденной;.\n\nСоверши обряды, изображенные на щитах, и сними печать с входа в склеп. Я пока отправлю к Седогриву гонца с новостями – вне всякого сомнения, он пожелает лично встретиться с Сильваной.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'zhCN', '成为晋升者', '在斯考德-艾希尔神殿的四座祭坛获得祝福，然后与雕像沟通。', '现在我们的目标很明确了。我们必须进入宝库，阻止希尔瓦娜斯获得创造更多瓦格里的力量。 \n\n入口处有屏障，难怪她在这里耗了这么久，不过你似乎已经找到了破除屏障的关键。\n\n你必须成为下一位晋升者。\n\n完成盾牌上面描述的仪式，并打开宝库入口的屏障。我会把我们所知的情况转告给格雷迈恩——他一定希望亲自面对希尔瓦娜斯。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39092, 'zhTW', '成為晉升者', '在斯寇亞胥的神廟中獲得4座祭壇的祝福，然後和雕像溝通。', '現在的目標很明確了。我們必須進入神殿，阻止希瓦娜斯獲得創造更多華爾琪的力量。\n\n入口被封住了，可能是因為這樣她才沒能進去，不過你或許已經找到破除這道屏障的關鍵了。\n\n你必須成為下一位晉升者。\n\n完成盾牌上描述的儀式，並打破神殿入口的屏障。我會把目前所知的一切告訴葛雷邁恩，他會想親自面對希瓦娜斯的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'deDE', 'Tötet sie alle!', 'Tötet 200 gegnerische Spielercharaktere.', 'Die Übergriffe der Horde dürfen wir nicht erlauben. Bekämpft sie, bis niemand verbleibt!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'esES', '¡Mátalos a todos!', 'Mata a 200 jugadores enemigos.', 'No podemos tolerar las invasiones de la Horda. ¡Reduce sus efectivos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'esMX', '¡Mátalos a todos!', 'Mata a 200 jugadores enemigos.', 'La transgresión de la Horda no puede ser tolerada. ¡Reduce sus números hasta que no quede nada!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'frFR', 'Tuez-les jusqu’au dernier !', 'Tuez 200 personnages-joueurs ennemis.', 'Il n’est pas question de tolérer les abus de la Horde. Allez faire un massacre dans ses rangs !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'itIT', 'Uccidili tutti!', 'Uccidi 200 membri della fazione opposta.', 'L\'invasione dell\'Orda non può essere tollerata ulteriormente. Riduci al minimo le loro file!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'koKR', '모두 쓸어버려라!', 'Slay 200 enemy players.', '호드의 침략을 두고만 볼 순 없습니다. 놈들의 씨를 말려 버리십시오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'ptBR', 'Mate todos!', 'Mate 200 jogadores inimigos.', 'A aproximação da Horda não pode ser tolerada. Desbaste o contingente deles!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'ruRU', 'Убить их всех!', 'Убейте 200 членов противоположной фракции.', 'Мы не будем мириться с ползучей экспансией Орды. Прореди-ка ее ряды, да хорошенько!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'zhCN', '杀光他们！', '击杀200名部落成员。', '绝不能容忍部落的入侵。把他们都干掉！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39096, 'zhTW', '殺光他們！', '殺死200個敵方玩家。', '絕對不能容忍部落的侵佔。將他們屠殺殆盡，一個不留！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'deDE', 'Noch fettere Beute', 'Beschafft schäbige Eisenmünzen für Gaunah.', 'Diese Schergen in Tanaan haben alle möglichen wertvoll\'n Schätze. Ist noch immer schwierig, aber ich kann Euch jetzt viel mehr geb\'n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'esES', 'Ladrón de guante blanco', 'Obtén monedas de hierro deslucidas para Griftah.', 'Esos estúpidos de Tanaan se están llevando todos los tesoros valiosos habidos y por haber. Sigue siendo complicado, pero ahora puedo darte mucho más.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'esMX', 'Un botín aún mayor', 'Obtén monedas de hierro deslucidas para Griftah.', 'Los tontos en Tanaan, llevan todo tipo de tesoros valiosos. Aún es complicado, ¡pero ahora te puedo dar mucho más!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'frFR', 'Après le pompon, la timbale', 'Obtenez des Pièces de fer ternies pour Eskrokar.', 'Ces crétins de Tanaan, ils transportent des objets d’valeur en tous genres. C’est pas encore le rêve, mais je peux tout de même vous récompenser nettement mieux qu’avant !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'itIT', 'Un punteggio ancora più alto', 'Recupera le Monete di Ferro per Griftah.', 'Chelli idioti a Tanaan stanno portando con loro ogni sorta di tesoro di valore. Sarà complicato, ma mo\' posso dartene molte di più!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'koKR', '완전 대성공', '그립타를 위해 때묻은 강철 동전을 모아야 합니다.', '타나안에 있는 불량한 녀석들 말인데, 온갖 값진 보물을 가지고 다닌다더군. 왜 그런지 설명하긴 여전히 복잡하네만, 하여튼 이젠 동전을 갖다주면 더 많은 보수를 자네에게 지불할 수 있다네!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'ptBR', 'Se Dando Melhor Ainda', 'Obtenha Moedinhas de Ferro para Trambik.', 'Tinha uns trouxa em Tanaan carregando um monte de treco valioso. Ainda é bem complicado, mas eu posso te pagar melhor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'ruRU', 'Еще более крупное ограбление', 'Добудьте тусклые железные монеты для Жуля.', 'У этих балбесов в Танаане чего только нет в карманах! Куча самых разных ценностей! Думаю, в этот раз я смогу выделить тебе долю побольше!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'zhCN', '更高的分数', '收集肮脏的铁币并把它们交给格里伏塔。', '那些塔纳安的蠢货，他们身上带着各种各样的财富。情况还很复杂，但我现在已经有很多东西可以给你了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39107, 'zhTW', '做一票更大的', '為格利夫塔取得髒鐵幣。', '原來這些塔南的呆子帶了各式各樣的財寶。這還是很複雜，但我現在可以給你更多！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'deDE', 'Es hat sich ausgekrächzt!', 'Findet Rottschnabel am Bradenbach und schlagt der Bestie den Kopf ab.', 'Papa hat dieses Dorf geliebt. Er wollte nie von hier fortgehen, nicht einmal in diesen schlimmen Zeiten.$B$BBevor er starb, sagte er mir, was ihn angegriffen hat: ein monströser Falke namens Rottschnabel.$B$BIhr kämpft doch gegen diese Untoten, oder? Würdet Ihr meinen Vater rächen? Wenn ja, dann findet Ihr die Bestie nordwestlich von hier. Sie fliegt entlang unserer Westroute zum Meer auf und ab.', '', '', 'Rottschnabel', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'esES', 'Basta de chillidos', 'Busca a Pico Pútrido en Arroyo Braden y hazte con la cabeza de la bestia.', 'Papá adoraba esta aldea. No quería ni oír hablar de irse, ni siquiera en estos tiempos funestos.$B$BAntes de morir, me dijo qué lo atacó: un halcón monstruoso al que llamamos Pico Pútrido.$B$BTú luchas contra los no-muertos, ¿verdad? ¿Vengarás a mi padre? Si es así, encontrarás a la bestia al noroeste de aquí, patrullando en nuestra ruta occidental hacia el mar.', '', '', 'Pico Pútrido', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'esMX', 'No chilles más', 'Encuentra a Pico Pútrido en Bradensbrook y consigue la cabeza de la bestia.', 'Papá adoraba esta aldea. Nunca se iría, ni en estos momentos terribles.$B$BAntes de que muriera, me contó qué fue lo que lo atacó: un halcón monstruoso al que llamamos Pico Pútrido.$B$BEstás luchando contra los no-muertos, ¿verdad? ¿Vengarás a mi padre? En ese caso, puedes encontrar a la bestia al noroeste de aquí, seguro vigila la ruta oeste hacia el mar.', '', '', 'Pico Pútrido', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'frFR', 'Le dernier cri', 'Trouvez Putribec le long de Ruissecôte et tranchez-lui la tête.', 'Papa adorait ce village. Même en ces temps si effroyables, il refusait catégoriquement de quitter cet endroit.$B$BAvant de mourir, il m’a dit qui l’avait attaqué : un épervier monstrueux répondant au nom de Putribec.$B$BVous vous battez contre ces morts-vivants, n’est-ce pas ? Vous voulez bien venger mon père ? Si oui, vous trouverez cette créature au nord-ouest, sur la route de l’ouest qui mène à la mer.', '', '', 'Putribec', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'itIT', 'Niente più starnazzi', 'Trova Beccomarcio a Rivofosco e ottieni la sua testa.', 'Mio papà amava questo villaggio. Non voleva sentir parlare di andarsene, nemmeno in questi tempi bui.$B$BPrima di morire, mi ha detto cosa lo aveva attaccato: un mostruoso falcone che chiamiamo Beccomarcio.$B$BCombatterai questi non morti, vero? Vendicherai mio padre? Potrai trovare quella bestia a nord-ovest, mentre pattuglia la nostra rotta occidentale per il mare.', '', '', 'Beccomarcio', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'koKR', '추락하는 것은 날개가 있다', '브레이든스브룩 주변을 배회하는 썩은부리를 찾아 머리를 구해야 합니다.', '아버지는 마을에 애착이 많았어. 이렇게 시절이 하 수상한데도 떠나자는 말은 꺼내지도 말라고 하셨지.$B$B아버지가 돌아가시기 전에 말씀하시기를, 썩은부리라는 괴물 매한테 공격을 당했대.$B$B지금 언데드와 싸우고 있지? 혹시 아버지의 복수를 해줄 수 있어? 여기서 북서쪽으로 가면 썩은부리가 있어. 바다로 통하는 서쪽 길을 따라 돌아다녀.', '', '', '썩은부리', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'ptBR', 'Bico Fechado', 'Encontre Putribico em Bradensbrook e corte-lhe a cabeça.', 'Papai adorava essa aldeia. Eles nunca cogitaria ir embora, nem com tantas dificuldades.$B$BAntes de morrer, ele me contou quem o atacou: um falcão monstruoso chamado Putribico.$B$BVocê está combatendo os mortos-vivos, não está? Você poderia vingar o meu pai? O bicho vive a noroeste daqui e está sempre zanzando pela estrada que leva ao mar.', '', '', 'Putribico', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'ruRU', 'Дочирикался', 'Найдите Гнилоклюва в районе Браденсбрука, убейте и обезглавьте его.', 'Папа очень любил эту деревню. Он и слышать ничего не хотел о том, чтобы перебраться в другое место – даже в эти ужасные времена.$B$BПеред смертью он успел рассказать, кто напал на него – огромный чудовищный сокол, которого мы зовем Гнилоклювом.$B$BТы ведь уже $gсражался:сражалась; с этой нежитью, да? Ты отомстишь за моего отца? Эта тварь стережет западный выход к морю, к северо-западу отсюда.', '', '', 'Гнилоклюв', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'zhCN', '猎隼止声', '沿着鲑鱼溪找到烂喙，然后砍下它的脑袋。', '老爸热爱这个村庄。就算在如此恐怖的时刻，他也听不进任何要离开的建议。$B$B他离世之前告诉我，攻击他的是一只被我们称为烂喙的可怕猎隼。$B$B你正在对付这些亡灵，对吗？你可以帮我的父亲报仇吗？如果可以的话，往西北方向走就能找到那只禽兽，它正在通往海边的路上徘徊。', '', '', '烂喙', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39117, 'zhTW', '終止尖嘯', '沿著布瑞登溪找到爛喙，並取回這頭野獸的腦袋。', '爸爸很喜歡這個村子。就算在這種最悲慘的時候，他也聽不進任何要他離開這裡的建議。$B$B他在死前告訴我攻擊他的是一隻恐怖的獵鷹，叫做爛喙。$B$B你正在對抗這些不死生物，對吧？你能幫我父親復仇嗎？如果願意的話，那頭野獸就在此地的西北方，在通往海邊的路上徘徊。', '', '', '爛喙', '', '', '', 0);
