INSERT INTO `quest_template_locale` VALUES (39370, 'esMX', 'Plano de equipo: Reservas de munición', 'Devuélvele los planos al Especialista en equipamiento naval en el astillero de tu fortaleza.', 'Esto parece ser el plano de algún tipo de equipo naval. Deberíamos llevarlo de vuelta al astillero y ver si alguien puede descifrarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39370, 'frFR', 'Plan d’équipement : réserves de munitions', 'Apportez les Plans à un Spécialiste de l’équipement naval, au Chantier naval de votre Fief.', 'Il s’agit apparemment de plans d’équipement naval. Nous devrions les rapporter à votre chantier naval, afin de voir si quelqu’un peut déchiffrer tout ça.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39370, 'itIT', 'Progetto: Munizioni di Riserva', 'Riporta il progetto a uno Specialista d\'Equipaggiamento Navale al Cantiere Navale della tua Guarnigione.', 'Questo sembra essere un progetto per qualche sorta di equipaggiamento navale. Dovremmo riportarlo al nostro Cantiere Navale e vedere se qualcuno è in grado di decifrarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39370, 'ptBR', 'Diagrama de Equipamento: Reservas de Munição', 'Leve os diagramas para um Especialista em Equipamento Naval no estaleiro da sua guarnição.', 'Parece um diagrama de equipamento naval. É melhor levar de volta ao estaleiro e ver se alguém consegue decifrar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (39370, 'ruRU', 'Чертеж оборудования: большой боезапас', 'Отнесите чертежи специалисту по корабельному оборудованию на пристань вашего гарнизона.', 'Судя по всему, это чертежи какого-то корабельного оборудования. Нужно отнести их на пристань и попытаться расшифровать.', '', '', '', '', '', '', 0);
