INSERT INTO `quest_template_locale` VALUES (40279, 'itIT', 'Allenamento con Durian', 'Sconfiggi Durian Fruttoforte in uno scontro tra mascotte.', 'Fammi vedere se hai davvero conquistato la fiducia dei tuoi piccoli amici.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40279, 'koKR', '듀리안과의 훈련', '애완동물 대전에서 듀리안 스트롱프룻을 꺾어야 합니다.', '정말로 당신의 애완동물의 신뢰를 받고 있는지 보여줘라.', '', '', '', '', '', '', 0);
