INSERT INTO `quest_template_locale` VALUES (40279, 'zhCN', '杜里安的训练', '在宠物战斗中击败杜里安·强果。', '如果你真的获取了伙伴们的信任，那就证明给我看。', '', '', '', '', '', '', 0);
