INSERT INTO `quest_template_locale` VALUES (40297, 'esES', 'Lyana Penaoscura', 'Destruye el cosechador de almas.', '<Una cazadora de demonios cautiva flota en estasis frente a ti; su alma se vacía lentamente en el cristal recolector.>\n\n<Para liberarla de su estado tendrás que destruir el cosechador de almas.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'esMX', 'Lyana Doloroscuro', 'Destruye la cosechadora de almas.', 'Una cazadora de demonios cautiva flota inmóvil frente a ti. Su alma se drena despacio y poco a poco entra en el cristal segador.>\n\n<Para sacarla de este estado, tendrás que destruir la cosechadora de almas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'frFR', 'Lyana Sombrepeine', 'Détruisez le Moissonneur d’âmes.', '<En état de stase, une chasseuse de démons captive flotte dans les airs devant vous, le cristal moissonneur aspirant son âme peu à peu.>\n\n<Vous allez devoir détruire le moissonneur d’âmes pour la libérer.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'itIT', 'Lyana Dolornero', 'Distruggi il Mietitore d\'Anime.', '<Una Cacciatrice di Demoni prigioniera fluttua in uno stato di stasi di fronte a te. La sua anima sta venendo lentamente risucchiata dal cristallo mietitore.>\n\n<Per liberarla dalla stasi, dovrai distruggere il Mietitore d\'Anime.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'koKR', '리아나 다크소로우', '영혼 수확기를 파괴해야 합니다.', '<바로 앞에 악마사냥꾼이 정지장에 꼼짝없이 붙잡혀 있습니다. 그녀의 영혼은 수확기 수정으로 서서히 빨려 들어가고 있습니다.>\n\n<정지장에서 그녀를 구하려면 영혼 수확기를 파괴해야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'ptBR', 'Lyana Umbramágoa', 'Destrua o Ceifalmas.', '(Uma caçadora de demônios cativa flutua em estase diante de você e sua alma é drenada lentamente para o cristal ceifador.>\n\n<Para libertá-la do estase, você precisa destruir o ceifalmas.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'ruRU', 'Лиана Отчаяние Тьмы', 'Уничтожьте пожинатель душ.', '<Перед вами в стазисе парит тело плененной охотницы на демонов; ее душа медленно перетекает в кристалл пожинателя душ.>\n\n<Чтобы освободить пленницу, пожинатель душ нужно уничтожить.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'zhCN', '莉安娜·悲夜', '摧毁灵魂收割器。', '<一个恶魔猎手俘虏静静地浮在你的面前，她的灵魂被缓缓地吸入收割器水晶之中。>\n\n<想要让她脱离静滞状态，你就必须摧毁灵魂收割器。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (40297, 'zhTW', '萊安娜‧暗愁', '破壞靈魂收割者。', '<一名被俘的惡魔獵人陷入靜滯，漂浮在你眼前。她的靈魂緩緩的被收割水晶吸去。>\n\n<要讓她脫離靜滯狀態，你得破壞靈魂收割者。>', '', '', '', '', '', '', 0);
