INSERT INTO `quest_template_locale` VALUES (40533, 'esMX', 'Dando la talla', 'Talla una azsunita rápida con la herramienta de corte de Tiffany.', 'Como has sido un recurso muy confiable, quiero enseñarte cómo cortar una azsunita rápida.$b$bUsaremos mi herramienta de corte para hacerlo, así que, cuando estés $glisto:lista;, siéntate frente a ella.', '', '', '', '', '', '', 0);
