INSERT INTO `quest_template_locale` VALUES (40534, 'deDE', 'Gut abgeschnitten', 'Stellt mithilfe von Tiffanys Schneidewerkzeug einen meisterlichen Königinnenopal her.', 'Da Ihr mir bis jetzt treu und verlässlich zu Diensten wart, werde ich Euch zeigen, wie man einen meisterlichen Königinnenopal schleift.$b$bWir werden dazu mein Schleifwerkzeug verwenden. Wenn Ihr also bereit seid, dann setzt Euch einfach davor.', '', '', '', '', '', '', 0);
