INSERT INTO `quest_template_locale` VALUES (41110, 'koKR', '복수 준비', '히포그리프에 타야 합니다.', '히포그리프가 호기심 어린 눈빛으로 당신의 손을 바라봅니다.', '', '', '', '', '', '', 0);
