INSERT INTO `quest_template_locale` VALUES (41368, 'koKR', '잃어버린 우편', '잃어버린 우편을 달라란 마법의 뒤안길에 있는 마님 고야에게 배달해야 합니다.', '수신: 달라란 마법의 뒤안길의 마님 고야\n\n긴급!\n\n한시가 급함!\n\n즉시 배달 요망!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41368, 'ptBR', 'Correspondência Extraviada', 'Entregue a Correspondência Extraviada para Madame Goya nos Esgotos de Dalaran.', 'PARA: MADAME GOYA, ESGOTOS DE DALARAN\n\nURGENTE!\n\nEXTREMA IMPORTÂNCIA! \n\nENTREGAR IMEDIATAMENTE!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41368, 'ruRU', 'Утерянное письмо', 'Доставьте утерянное письмо мадам Гойе в Клоаку Даларана.', 'КОМУ: МАДАМ ГОЙЕ; КЛОАКА ДАЛАРАНА\n\nСРОЧНО!\n\nЗАДЕРЖКИ НЕДОПУСТИМЫ! \n\nДОСТАВИТЬ НЕМЕДЛЕННО!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41368, 'zhCN', '遗失的邮件', '将遗失的邮件交给达拉然下水道的郭雅夫人。', '致：达拉然下水道，郭雅夫人\n\n重要！\n\n特急件！\n\n立即投递！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41368, 'zhTW', '失落的信件', '把失落的信件交給達拉然城底區的苟雅女士。', '致苟雅女士\n城底區\n\n緊急！\n\n特急件！\n\n立即送件！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'deDE', 'Ein kleiner Nebenverdienst', 'Verkauft Unbesiegbars Zügel im Auftrag von Johnny Imba an God Wiedelspann.', 'Oh, wie einfältig Ihr seid, $R.$B$BIch habe nicht etwa \'vergessen\', nach meinem Triumph über diesen läppischen Lichkönig die Zügel aufzusammeln. Ich hielt es einfach nicht für nötig, da ich das mächtige Ross bereits letzte Woche bei einem Schlachtzug erhalten habe.$B$B<Johnny zeigt auf das Pferd zu seiner Linken.>$B$BAber da Ihr schon hier seid und verständlicherweise meinem unwiderstehlichen Charme verfallen seid, erlaube ich Euch, etwas für mich zu erledigen. Verkauft den überflüssigen Gaul in der Stadt beim stellvertretenden Resortdirektor. Zu einem kleinen Nebenverdienst sage ich nicht Nein.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'esES', 'Servir con una sonrisa', 'Vende las riendas de Invencible a Gott Zalamerías en nombre de Johnny Fenómeno.', 'Ay, pobre $r. Pobre $r, ¡qué zoquete eres!$B$BNo \"olvidé\" coger las riendas de ese caballerucho conocido como el Rey Exánime, simplemente no me tomé la molestia de hacerlo, pues ya había recibido al poderoso corcel durante la banda de la semana anterior.$B$B<Johnny señala el caballo que hay a su izquierda.>$B$BPero ya que estás aquí, y está claro que te intriga mi apabullante grandeza, ¿por qué no le llevas ese semental superfluo al director adjunto del complejo de la ciudad? A mis arcas no les vendría mal una aportación adicional.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'esMX', 'A la orden, señor', 'Vende las Riendas de Invencible a Gott Zalamerías para Johnny Fenómeno.', 'Ah, $r $gingenuo:ingenua;. Pobre $r $gingenuo:ingenua;.$B$BNo \"olvidé\" robar las riendas de ese caballero debilucho al que llaman Rey Exánime. No me molesté en robarlas porque ya había recibido el corcel poderoso durante mi banda de la semana pasada.$B$B<Johnny señala al corcel a su izquierda.>$B$BYa que estás aquí y te intriga mi grandeza sofocante, ¿no llevarías este semental superfluo al director adjunto del complejo del pueblo? No me vendrían mal unos revestimientos más para mis arcas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'frFR', 'Avec le sourire', 'Vendez les Rênes d’Invincible à Gott Travégorneur pour Johnny La Classe.', 'Oh, pauvre $R. Pauvre, pauvre $R.$B$BJe n’ai pas « oublié » de ramasser ces rênes sur ce minable chevalier qu’on appelle le roi-liche. Je n’ai simplement pas eu envie de le faire, puisque j’avais déjà reçu ce puissant destrier lors de mon raid de la semaine dernière.$B$B<Johnny indique le cheval à sa gauche.>$B$BPuisque vous êtes là et de toute évidence $gintrigué:intriguée; par mon incommensurable grandeur, pourquoi ne pas conduire cet étalon inutile au directeur adjoint de la station en ville ? Pour garnir un peu plus mes coffres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'itIT', 'Servire col sorriso', 'Vendi le Redini di Invincibile a Gott Bladivite per Johnny Meraviglia.', 'Oh, $gpiccolo:piccola; $R. $gPiccolo, piccolo:Piccola, piccola; $R.$B$BNon mi sono dimenticato di prendere le briglie da quella sottospecie di cavaliere che chiamano Re dei Lich. Semplicemente non mi interessavano, dato che avevo già ricevuto il poderoso destriero durante l\'incursione della settimana scorsa.$B$B<Johnny indica il cavallo alla sua sinistra.>$B$BDato che sei qui e che sei chiaramente $gaffascinato:affascinata; dalla mia incredibile magnificenza, perché non porti quello stallone superfluo all\'Assistente Direttore delle Terme in città? I miei forzieri hanno sempre bisogno di un po\' di imbottitura aggiuntiva.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'koKR', '웃으며 하는 일', '조니 잘나스 대신 천하무적의 고삐를 고트 위들스팬에게 팔아야 합니다.', '아아, 어리석은 $r|1이로군요;로군요;. 어리석고, 어리석은 $r|1이에요;예요;.$B$B저는 리치 왕이라는 그 멍청하고 나약한 기사의 고삐를 \'깜박하고\' 놓고 온 게 아닙니다. 이미 그 전 주 공격대에서 그 명마를 얻었기 때문에 굳이 챙기지 않은 것이지요.$B$B<조니가 왼쪽에 있는 말을 가리킵니다.>$B$B어쨌든 당신이 이왕 찾아왔고 나의 멋짐에 흥미를 느끼는 모양이니, 남는 말을 마을에 있는 휴양지 부지배인에게 데려가 주시겠습니까? 금고를 조금 채우기도 해야 하고 말이지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'ptBR', 'Serviço com gentileza', 'Venda as Rédeas do Invencível para Gott Espancapino, em nome de Jãozinho Manda-muito.', 'Ah, pobre $r. $GMeu:Minha; pobre $r.$B$BEu não me \"esqueci\" de embolsar as rédeas daquele arremedo de cavaleiro que chamam de Lich Rei. Eu apenas não me dei ao trabalho, pois já tinha ganhado o corcel no raide da semana passada.$B$B<Jãozinho indica o cavalo à esquerda.>$B$BComo você já está aqui, assombrad$Go:a; pela minha magnanimidade, por que não leva o garanhão sobressalente para o diretor assistente do resort? Assim eu aproveito para revestir meu cofre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'ruRU', 'Рад служить', 'Продайте поводья Непобедимого Ботту Воттакенсу по просьбе Джонни Потрясного.', 'О, $gнаивный:наивная:r; |3-6($r). $GНаивный-наивный:наивная-наивная:r; |3-6($r).$B$BЯ не \"забыл\" подобрать эти поводья, сразив то жалкое подобие рыцаря, которое называют \"Королем-личом\". Я уже получил своего могучего скакуна в рейде на прошлой неделе.$B$B<Джонни кивает на коня, стоящего слева от него.>$B$BНо раз уж ты здесь и явно пребываешь в восхищении от моего знойного величия, сделай милость, доставь лишнего скакуна в город и сдай его заместителю директора курорта. Никогда не помешает потуже набить кошелек.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'zhCN', '微笑服务', '为强尼·傲森将无敌的缰绳卖给哥特·韦德斯潘。', '噢，$r啊$r，总是这么单纯。$B$B我没有“忘记”从巫妖王，就是那位步态愚蠢的骑士那找回这些缰绳。我只是觉得麻烦，因为上周我已经在团队副本里拿到了这匹良驹。$B$B<强尼走向他左手边的那匹马。>$B$B不过既然你在这里，又显然被我惊世的才华所吸引，那不如你把那匹多余的马带给镇上的度假地主管助理？我的金库也能多点额外的金币。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41394, 'zhTW', '笑著服務', '幫『棒透了』強尼把無敵的韁繩賣給戈特‧哄誘。', '喔，單純的$r，單純到不行的$r。$B$B我並不是「忘記」從那個叫巫妖王的沒用騎士身上取走韁繩，我只是嫌麻煩。我上星期出團就已經拿到這個強大的坐騎了。$B$B<強尼往他左邊的戰馬一指。>$B$B不過既然你都來了，而且顯然受到我偉大的氣質吸引，要不要乾脆幫我把那匹多出來的戰馬帶去城裡面，交給渡假村副主管？我想給我的保險櫃添加內襯。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'deDE', 'Der gebührende Lohn', 'Legt Glitzers Lieblingsspielzeug an seinem Grab auf den Schlickfeldern im Vorgebirge des Hügellands nieder.', 'Ihr habt mir treu gedient in den vergangenen paar Minuten, die wir Seite an Seite verbracht haben – Minuten, die ich nicht so schnell vergessen werde.$B$BFür Eure unverbrüchliche Treue habt Ihr Euch eine Belohnung verdient. Euch gebührt die unsterbliche Ehre, im Andenken an Glitzer sein Lieblingsspielzeug an seinem Grab niederzulegen.$B$BBitte, bitte – hebt Euch die Dankesreden für Glitzer auf. Es passiert nicht alle Tage, dass man ein Pferd aus STERNEN ehrt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'esES', 'Una recompensa merecida', 'Coloca el juguete favorito de Centella junto a su tumba en Los Campos de Lodo, en las Laderas de Trabalomas.', 'Me has servido bien durante estos últimos minutos que hemos pasado juntos. Minutos que tardaré en olvidar.$B$BCreo que te mereces una recompensa digna de tu lealtad inquebrantable: te concederé el honor de colocar el juguete favorito de Centella en su tumba para honrar su memoria.$B$BNo me des las gracias, prefiero que las reserves para Centella. Al fin y al cabo, no todos los días puedes honrar a un caballo hecho de estrellas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'esMX', 'Recompensa merecida', 'Coloca el juguete favorito de Centella junto a su tumba en los Campos de Lodo, en las Laderas de Trabalomas.', 'Me has servido bien durante estos últimos momentos que hemos vivido, minutos que no olvidaré pronto.$B$BCreo que mereces una recompensa digna de tu lealtad inquebrantable. Tendrás el distinguido honor de colocar el juguete favorito de Centella junto a su tumba para honrar su memoria.$B$BPor favor, por favor, guárdate tu agradecimiento para Centella, no todos los días se puede honrar a una yegua hecha de ESTRELLAS.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'frFR', 'Une juste récompense', 'Placez le jouet préféré de Firmament sur sa tombe aux Champs Fangeux, dans les Contreforts de Hautebrande.', 'Vous m’avez servi avec talent pendant toutes ces minutes passées ensemble… Des minutes qui resteront gravées dans ma mémoire.$b$bUne récompense digne de votre loyauté sans faille est de mise. Vous aurez l’insigne honneur de placer le jouet préféré de Firmament sur sa tombe pour honorer sa mémoire.$b$bNon, ne me remerciez pas. Gardez ça pour Firmament. Ce n’est pas tous les jours qu’on honore un cheval fait d’étoiles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'itIT', 'Una ricompensa dovuta', 'Posiziona il giocattolo preferito di Scintilla sulla sua tomba ai Campi Fangosi nelle Alture di Colletorto.', 'Mi hai servito bene nei pochi minuti che abbiamo passato insieme. Non lo dimenticherò molto presto.$B$BCredo che una ricompensa adeguata sia d\'obbligo. Avrai l\'onore di lasciare il giocattolo preferito di Scintilla sulla sua tomba, per onorarne la memoria.$B$BTi prego, risparmia i ringraziamenti per Scintilla, non capita tutti i giorni di onorare un cavallo fatto di STELLE.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'koKR', '정당한 보상', '별보라가 가장 좋아하던 장난감을 언덕마루 구릉지의 진흙 농장에 있는 별보라의 무덤 곁에 놓아야 합니다.', '당신은 우리가 함께한 지난 몇 분간 제게 충성하셨습니다. 아마도 이 시간을 쉽게 잊지 못하겠지요.$B$B당신의 흔들리지 않는 충심에 어울리는 상을 드려야겠군요. 별보라가 가장 좋아하던 장난감을 그 녀석의 무덤 곁에 놓아 두는 영광을 당신께 양보하겠습니다.$B$B감사 인사는 별보라에게 해 주시지요. 별로 만들어진 말을 기리는 건 매일 할 수 있는 일이 아니니까요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'ptBR', 'Recompensa merecida', 'Leve o brinquedo favorito do Brilhoso ao seu túmulo, nos Campos Lodosos, no Contraforte de Eira dos Montes.', 'Você me serviu bem nos últimos minutos que passamos juntos... minutos de que não me esquecerei tão cedo.$B$BA ocasião pede uma recompensa à altura da sua lealdade inabalável. Você fará as honras de levar o brinquedo favorito do Brilhoso ao túmulo, em honra de sua memória.$B$BPor favor, por favor, guarde os agradecimentos para o Brilhoso. Não é todo dia que honramos um cavalo feito de ESTRELAS.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'ruRU', 'Достойная награда', 'Возложите любимую игрушку Звездочки на ее могилу, что на Топких полях в предгорьях Хилсбрада.', 'Ты $gсослужил:сослужила; мне хорошую службу за несколько минут нашего знакомства – и я их запомню надолго.$B$BЗа исключительную преданность тебе полагается достойная награда. Тебе выпала честь почтить память Звездочки, возложив ее любимую игрушку на ее могилу.$B$BО, прибереги все благодарности для нее. Не каждый день удается воздать должное лошади, состоящей из ЗВЕЗД.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'zhCN', '应得的报酬', '将烁影最爱的玩具放在希尔斯布莱德丘陵药渣农场的坟墓里。', '我们在一起的这几分钟你把我侍奉得不错——我不会马上就把这几分钟抛之脑后的。回报你忠心的奖励已经准备好了。你将有荣幸把烁影最爱的玩具放在他的坟墓里，以此来纪念他。$B$B拜托，拜托——要谢就谢烁影吧；纪念一匹由星星汇成的马可不是每天都有的事情。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41395, 'zhTW', '應得的獎勵', '把閃閃最喜歡的玩具放在牠位於希爾斯布萊德丘陵淤泥農場的墳上。', '在我們過去相處的這幾分鐘裡面，你為我提供了不少服務，我不會輕易忘記這過去幾分鐘的。$B$B你理當為你所展現的忠誠，獲得相應的獎勵。我賜你這份殊榮，讓你把閃閃最愛的玩具放在他的墓前，以表達你對他的懷念。$B$B不用謝我，你要是有謝意就說給閃閃聽吧，畢竟，我們可不是天天都有機會紀念一匹由繁星組成的駿馬。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'deDE', 'Ein Riesenpaket', 'Bringt 1.362 robuste Steine zum Portal der Astralen in Dalaran.', 'Aufgrund zahlreicher Beschwerden in den letzten Monaten wurde ein Auslieferungsverbot für... r... r...$B$B<Der Postmeister kämpft mit den Tränen, sein Blick ist schmerzverzerrt.>$B$B... robusten Stein erteilt. Unsere Lager quellen förmlich über von robustem Stein – wenn wir ihn nicht ausliefern können, wissen wir bald nicht mehr wohin damit – $B$B<Das Gesicht des Postmeisters erhellt sich plötzlich.>$B$BIch hab\'s!$B$BHier in der Nähe gibt es ein altes Konsortiumportal, in dem wir die Steine abladen können, ohne dass jemand etwas davon mitbekommt!$B$BKönnt Ihr mir helfen, $n? Die Steine sind extrem schwer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'esES', 'Un paquete enorme', 'Lleva 1362 trozos de piedra sólida al portal etéreo, en Dalaran.', 'Debido al número creciente de quejas durante los últimos meses, ya no podemos enviar p... p...$B$B<El jefe de correos contiene las lágrimas, pero el dolor es visible en sus ojos.>$B$BPiedras sólidas. Estamos nadando en ellas. Si no conseguimos entregarlas pronto, no tardaremos en...$B$B<Los ojos del jefe de correos se iluminan de golpe.>$B$B¡Lo tengo!$B$BHay un viejo portal del Consorcio cerca de aquí, ¡podríamos poner allí todas las piedras!$B$B¿Me ayudarás, $n? Las piedras son extraordinariamente pesadas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'esMX', 'Un paquete enorme', 'Entrega 1362 fragmentos de piedra sólida al Portal etéreo de Dalaran.', 'Debido al increíble volumen de quejas que hemos recibido en los últimos meses, no hemos podido entregar p... p...$B$B<El jefe de correos intenta contener las lágrimas, se puede ver el dolor en sus ojos.>$B$BPiedra sólida. Estamos nadando en ella, y si no podemos entregarla, tarde o temprano nos aplast...$B$B<De pronto, los ojos del jefe de correos se iluminan.>$B$B¡Ya sé!$B$B¡Hay un antiguo portal del Consorcio cerca de aquí en el que podríamos depositarla sin que nadie se entere!$B$B¿Podrías ayudar, $n? La piedra es extremadamente pesada.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'frFR', 'Un gros colis', 'Livrez 1362 morceaux de Pierre solide au Portail éthérien, à Dalaran.', 'En raison du grand nombre de plaintes de ces derniers mois, nous n’avons plus l’autorisation de livrer de la p… p…$B$B<Le maître de poste retient ses larmes, une grande souffrance visible dans son regard.>$B$BPierre solide. Nous croulons littéralement dessous : si nous ne les livrons pas rapidement, nous serons bientôt subm…$B$B<Le regard du maître de poste s’illumine tout à coup.>$B$BVoilà la solution !$B$BIl y a un ancien portail du Consortium pas loin d’ici où nous pourrions l’entreposer discrètement !$B$BPouvez-vous nous aider, $n ? Ces pierres sont incroyablement lourdes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'itIT', 'Un pacco enorme', 'Consegna 1.362 pezzi di Pietra Solida al Portale Etereo di Dalaran.', 'A causa dell\'incredibile quantità di lamentele ricevute negli ultimi mesi, ci è stato impedito di consegnare Pi... P...$B$B<Il Postino trattiene le lacrime, con uno sguardo di dolore ben visibile nei suoi occhi.>$B$BPietra Solida. Ne abbiamo in grande abbondanza, e se non la consegniamo, presto ne saremo sommer...$B$B<Gli occhi del Postino si illuminano improvvisamente.>$B$BCi sono!$B$BC\'è un vecchio portale del Consorzio non lontano da qui nel quale potremmo depositarla tutta!$B$BTi andrebbe di aiutarmi, $n? Quella pietra è pesantissima.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'koKR', '커다란 소포', '달라란에 있는 에테리얼 차원문으로 견고한 암석 1,362개를 배달해야 합니다.', '최근 몇 달간 민원이 폭주하는 바람에 겨... 겨...$B$B<고통스러운 눈빛으로 눈물을 애써 참는 우체국장>$B$B견고한 암석을 배송할 수가 없다오. 암석이 하도 많이 쌓여 있어서, 빨리 배달하지 못하면 암석 더미에 깔려 죽을지도 모른-$B$B<문득 눈빛이 밝아지는 우체국장>$B$B방법이 있소!$B$B이 근처에 옛 무역연합 차원문이 있는데, 거기다 넣어 버리면 되겠군!$B$B$n 님, 도와주시겠소? 암석이 어마어마하게 무거워서 말이오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'ptBR', 'Uma grande encomenda', 'Entregue 1.362 pedras maciças no Portal Etéreo, em Dalaran.', 'Devido ao volume incrível de queixas nos últimos meses, nós não conseguimos realizar a entrega de p... p...$B$B<O Chefe do Correio segura as lágrimas, mas o sofrimento é visível.>$B$BPedra. Tem pedra que não acaba mais, se não conseguirmos entregar isso logo, vamos acabar soterr...$B$B<Seus olhos se iluminam de repente.>$B$BJá sei!$B$BTem um portal velho do Consórcio, não muito longe daqui, que podemos usar para depositar a encomenda.$B$BVocê pode ajudar, $n? As pedras pesam uma tonelada.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'ruRU', 'Крупная посылка', 'Доставьте 1362 кусков твердого камня к эфириальному порталу в Даларане.', 'Из-за невероятного количества жалоб в течение последних месяцев нам запретили поставлять т... т...$B$B<Почтальон пытается сдержать выступившие слезы.>$B$B...твердый камень. Мы буквально тонем в нем. Если от него скорее не избавиться, случится непопр...$B$B<Вдруг в глазах почтальона вспыхивает надежда.>$B$BНу конечно!$B$BРядом есть старый портал Консорциума. Так как все равно делать нечего, можно сбросить излишки туда!$B$BПоможешь, $n? Твердый камень очень тяжелый.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'zhCN', '巨大的包裹', '用达拉然的虚灵传送门运送1362块坚固的石头。', '由于我们收到了大量投诉，最近几个月我们都不能邮寄坚……坚……$B$B<邮政长忍住眼泪，眼中闪烁着痛苦>$B$B坚固的石头。这些石头已经堆到我们的胸口了——要是不快点把它送出去，我们很快就会被——$B$B<邮政长眼睛突然亮了起来。>$B$B对了！$B$B离这不远有座陈旧的星界财团传送门，我们可以神不知鬼不觉地把石头放进去！$B$B能帮帮忙吗，$n？这石头非常沉。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41397, 'zhTW', '超大的包裹', '把1362塊沉重的岩石遞送到達拉然的以太傳送門。', '由於近幾個月來收到大量投訴，我們被禁止遞送沉…沉…$B$B<郵務大師忍住要奪眶而出的眼淚，雙眼明顯傳達出痛苦。>$B$B沉重的岩石。我們已經被這些岩石給淹沒了，要是不快點遞送出去，我們一定會滅…$B$B<郵務大師突然眼睛一亮。>$B$B我知道了！$B$B離這裡不遠的地方，有個老舊的聯合團傳送門，我們可以神不知鬼不覺地把這些岩石丟在那裡！$B$B你能幫我這個忙嗎，$n？這些岩石實在重到不行。', '', '', '', '', '', '', 0);
