INSERT INTO `quest_template_locale` VALUES (41704, 'deDE', 'Subjekt 16', 'Bringt Theryn zu Thalyssra.', '<Theryn scheint noch immer unter dem Zauber des Arkanisten zu stehen. In Kel\'danaths Abwesenheit könnte ein lebendes Versuchsobjekt Thalyssra dabei helfen, seine Forschung nachzuvollziehen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'esES', 'Sujeto 16', 'Entrega a Theryn a Thalyssra.', '<Parece que Theryn sigue bajo el hechizo del arcanista. A falta del propio Kel\'danath, un sujeto vivo podría resultar útil para ayudar a Thalyssra a desentrañar su investigación.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'esMX', 'Sujeto 16', 'Lleva a Theryn con Thalyssra.', '<Parece que Theryn sigue bajo el hechizo del arcanista. En la ausencia de Kel\'danath, un sujeto vivo podría ser útil para ayudar a Thalyssra a resolver su investigación.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'frFR', 'Le sujet n°16', 'Emmenez Theryn à Thalyssra.', '<Apparemment, le sort de l’arcaniste continue d’affecter Theryn. En l’absence de Kel’danath, un cobaye encore en vie pourrait aider Thalyssra à comprendre ses recherches.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'itIT', 'Soggetto 16', 'Porta Theryn da Thalyssra.', '<Sembra che Theryn si trovi ancora sotto l\'influenza dell\'incantesimo dell\'Arcanista. In assenza di Kel\'danath, un soggetto vivo potrebbe essere utile per aiutare Thalyssra a completare questa ricerca.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'koKR', '16번 실험체', '테린을 탈리스라에게 데려다주어야 합니다.', '<테린은 여전히 비전술사의 주문에 걸려 있습니다. 켈다나스는 없지만, 살아 있는 실험체가 있다면 탈리스라가 그의 연구의 비밀을 푸는 데 도움이 될 것입니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'ptBR', 'Cobaia 16', 'Entregue Theryn a Thalyssra.', '<Parece que Theryn está sob o feitiço do arcanista. Na ausência do próprio Kel\'danath, uma cobaia viva seria útil para ajudar Thalyssra a desvendar a pesquisa dele.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'ruRU', 'Подопытный номер 16', 'Отведите Терина к Талисре.', '<Похоже, этот Терин все еще находится под действием заклинания чародея. В отсутствие самого Кел\'даната его живой подопытный может помочь Талисре разобраться в его исследованиях.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'zhCN', '16号实验体', '把泰林交给塔莉萨。', '<泰林似乎还在受到奥术师法术的影响。既然凯尔丹纳斯本人已经帮不上忙了，一个活体实验体应该有助于塔莉萨探索他的研究。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41704, 'zhTW', '受試者16號', '將瑟倫帶給薩莉瑟拉。', '<瑟倫似乎仍受到秘法師法術的影響。在沒有克達納茲本人的情況下，活物可能有助於薩莉瑟拉揭露克達納茲的研究內容。>', '', '', '', '', '', '', 0);
