INSERT INTO `quest_template_locale` VALUES (41880, 'esES', 'Guarida de las Sombras', 'Contacta con Arluin Hoja de las Sombras.', 'Placeholder: Thalyssra te envía en busca de Arluin Hoja de las Sombras, un hábil asesino que estuvo en su círculo social y profesional. (Lo que mejor se ajuste). Indica que sería una baza muy valiosa para hacer progresos en la ciudad con operaciones encubiertas.', '', '', '', '', '', '', 0);
