INSERT INTO `quest_template_locale` VALUES (41880, 'frFR', 'Le repaire des ombres', 'Entrez en contact avec Arluin Lame-de-l’Ombre.', 'Temporaire : Thalyssra envoie le personnage-joueur à la recherche d’Arluin Lame-de-l’Ombre, un assassin chevronné de sa connaissance. Souligner le fait qu’il serait un atout considérable pour la progression des opérations secrètes dans la ville.', '', '', '', '', '', '', 0);
