INSERT INTO `quest_template_locale` VALUES (41880, 'ptBR', 'Covil das Sombras', 'Faça contato com Arluin Umbralâmina.', 'Temporário: Thalyssra envia você para encontrar Arluin Umbralâmina, um assassino habilidoso que já esteve a serviço dela/fez parte do círculo social dela. (o que encaixar melhor) Indicar que ele seria alguém inestimável para se progredir na Cidade por meio de operações sigilosas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (41880, 'ruRU', 'Логово Теней', 'Установите контакт с Арлуином Теневым Клинком.', 'Placeholder: Талисра отправляет вас на поиски Арлуина Теневого Клинка, убийцы, который когда-то работал на нее/принадлежал к ее кругу. (Выбрать подходящий вариант). Указать, что он совершенно необходим для проведения в городе секретных операций.', '', '', '', '', '', '', 0);
