INSERT INTO `quest_template_locale` VALUES (42149, 'deDE', 'Wissen, das man nicht teilen sollte', 'Sammelt 5 Dämonenfolianten der Empyreumgesellschaft ein.', 'Mir ist nicht entgangen, dass einige der Bücher der Empyreumgesellschaft mit Teufelsmagie verzaubert sind. Das ist Blasphemie! Wir sollten so viele davon konfiszieren wie möglich.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'esES', 'Algunos conocimientos no se deben compartir', 'Reúne 5 tomos viles empíreos.', 'Me he dado cuenta de que hay bastantes libros de la Sociedad Empírea inscritos con magia vil. Eso es blasfemo. Debemos confiscar tantos como podamos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'esMX', 'Ciertos conocimientos no se deben compartir', 'Reúne 5 tomos viles empíreos.', 'Noté que algunos libros de la Sociedad Empírea están escritos con magia vil. Eso es una blasfemia. Tenemos que confiscar tantos como podamos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'frFR', 'Un savoir nuisible', 'Récupérez 5 Tomes gangrenés empyréens.', 'J’ai remarqué que plusieurs livres de la Société empyréenne avaient été rédigés à l’aide de magie gangrenée. Quel blasphème ! Il faut impérativement en confisquer le plus possible !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'itIT', 'Alcune conoscenze non andrebbero divulgate', 'Recupera 5 Viltomi Empirei.', 'Ho notato che molti di questi libri della Società Empirea sono stati iscritti con la vilmagia. Che blasfemia. Dobbiamo confiscarne il più possibile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'koKR', '모르는 게 약', '창공의 지옥 고서 5권을 회수해야 합니다.', '창공 협회의 책 중 상당수가 지옥 마법으로 쓰였다는 것을 알았습니다. 정말 불경하군요. 최대한 많이 압수해야만 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'ptBR', 'Certos conhecimentos não deveriam ser compartilhados', 'Recolha 5 Tomos Vis Empíreos.', 'Percebi que muitos dos livros da Sociedade Empírea estão inscritos com magia vil. Isso é blasfêmia. Devemos confiscar o máximo deles que pudermos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'ruRU', 'Не всеми знаниями надо делиться', 'Соберите 5 фолиантов Скверны Небесной академии.', 'Книги Небесной академии опасны! Многие из них содержат записи, сделанные магией Скверны. Это настоящее кощунство. Необходимо конфисковать как можно больше таких книг!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42149, 'zhCN', '不应分享的知识', '收集5本苍穹邪能宝典。', '我发现很多苍穹会的书籍中都铭刻了邪能魔法。这真是一种亵渎。我们必须尽可能多地没收它们。', '', '', '', '', '', '', 0);
