INSERT INTO `quest_template_locale` VALUES (42170, 'koKR', '몽술사', '발샤라에서 전역 퀘스트 4개를 완료해야 합니다.', '전역 퀘스트 4개를 완료하여 발샤라의 드루이드를 도와야 합니다.', '', '', '', '', '', '발샤라에 있는 실비아 하트쇼른에게 가야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (42170, 'ptBR', 'Os Tece-sonhos', 'Conclua 4 missões mundiais em Val\'Sharah.', 'Ajude os Druidas de Val\'sharah: complete 4 missões mundiais.', '', '', '', '', '', 'Fale novamente com Sílvia Cordiscorno em Val\'Sharah.', 0);
INSERT INTO `quest_template_locale` VALUES (42170, 'ruRU', 'Ткачи Снов', 'Выполните 4 локальных задания в Валь\'шаре.', 'Помогите друидам в Валь\'шаре, выполнив 4 локальных задания.', '', '', '', '', '', 'Вернитесь к Сильвии Олений Рог в Валь\'шару.', 0);
INSERT INTO `quest_template_locale` VALUES (42170, 'zhCN', '织梦者', '完成瓦尔莎拉的任意4个世界任务。', '帮助瓦尔莎拉的德鲁伊完成4个世界任务。', '', '', '', '', '', '回瓦尔莎拉找西尔维娅·鹿角。', 0);
INSERT INTO `quest_template_locale` VALUES (42170, 'zhTW', '織夢者', '在維爾薩拉完成4個世界任務。', '完成4個世界任務來協助瓦爾薩拉的德魯伊。', '', '', '', '', '', '回去維爾薩拉找西爾維亞‧鹿角。', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'deDE', 'Letzter Ausweg', 'Verwendet die Rune des Öffnens, um die Tür des Gebäudes der Empyreumgesellschaft zu öffnen.', 'Die Empyreumgesellschaft zählt jetzt ganz sicher einige Mitglieder weniger. Für eine Weile werden sie wohl nicht mehr versuchen, neue Mitglieder zu rekrutieren.\n\nEs gibt nur noch einen Ort, den wir noch nicht untersucht haben, und ich vermute, dass die Rune, die wir gefunden haben, diese Tür öffnen kann. Lasst uns nachsehen, was sich in dem Gebäude befindet.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'esES', 'Salida final', 'Usa la runa de apertura para abrir la puerta del edificio de la Sociedad Empírea.', 'Sin duda hemos reducido el número de miembros de la Sociedad Empírea. No creo que vayan a reclutar a más miembros para su causa durante un tiempo.\n\nSolo hay un lugar que no hemos investigado y sospecho que esa runa que encontramos está armonizada con la puerta. Veamos qué hay en el edificio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'esMX', 'Última salida', 'Utiliza la Runa de apertura para abrir la puerta del edificio de la Sociedad Empírea.', 'Ciertamente hemos reducido los números de la Sociedad Empírea. No creo que puedan atraer a más miembros a su causa durante un tiempo. Solo queda un lugar que no hemos investigado, y sospecho que la runa que encontramos está armonizada a la puerta. Veamos qué hay dentro del edificio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'frFR', 'Sortie définitive', 'Utilisez la Rune d’ouverture pour déverrouiller la porte du bâtiment de la Société empyréenne.', 'Nous avons indéniablement éclairci les rangs de la Société empyréenne. En l’état, je doute qu’elle rallie d’autres membres à sa cause avant un bon moment.\n\nIl reste néanmoins un endroit que nous n’avons pas inspecté, et je soupçonne la rune que nous avons trouvée d’être harmonisée avec la porte. Allons voir ce qui se trouve dans ce bâtiment, voulez-vous ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'itIT', 'Ultima uscita', 'Usa la Runa dell\'Apertura per sbloccare la porta del palazzo della Società Empirea.', 'Abbiamo di sicuro sfoltito i ranghi della Società Empirea. Non penso che tenteranno di convincere altri giovani membri a unirsi alla loro causa.\n\nC\'è solo un posto in cui non abbiamo indagato, e sospetto che la runa che abbiamo trovato sia collegata a quella porta. Vediamo cosa c\'è nel palazzo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'koKR', '마지막 출구', '열림의 룬을 사용해 창공 협회 건물의 문을 열어야 합니다.', '창공 협회의 세력을 확실히 약화시켰습니다. 당분간은 젊은 마법사들을 꼬드기지 못할 겁니다.\n\n이제 한 군데만 더 조사하면 됩니다. 아마 우리가 찾은 룬으로 그 문을 열 수 있을 것 같군요. 자, 어서 건물 안에 뭐가 있는지 확인해보죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'ptBR', 'Saída final', 'Use a Runa de Abertura para destrancar a porta da construção da Sociedade Empírea.', 'Certamente provocamos uma baixa no número de membros da Sociedade Empírea. Acho que agora eles ficarão um tempo sem atrair novos asseclas para a causa. \n\nSó há um lugar onde falta investigar, e eu suspeito que aquela runa que encontramos esteja sintonizada com a porta. Vejamos o que há dentro da construção.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'ruRU', 'Финальный выход', 'Отоприте дверь штаб-квартиры Небесной академии с помощью руны открытия.', 'Мы заметно сократили численность приверженцев этой так называемой Небесной академии. Думаю, в ближайшее время они точно не смогут пополнить свои ряды. \n\nОсталось лишь одно место, которое мы еще не осмотрели, и, думаю, что найденная нами руна связана как раз с нужной дверью. Посмотрим, что в этом здании.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'zhCN', '最后出口', '使用开启符文打开苍穹会建筑的大门。', '我们已经削减了苍穹会成员的数量。而且他们这阵子是没办法再招兵买马了。\n\n还剩一个地方没有调查，我觉得我们找到的符文就是开门的关键。去看看里面到底有什么。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42171, 'zhTW', '最後的出口', '使用開啟符文打開秘天學會建築物的大門。', '我們除掉不少秘天學會的成員。他們應該有一陣子都沒辦法誘拐其他人加入了。\n\n還剩下一個地方沒調查，我們先前發現的符文應該可以用在那個大門。讓我們看看建築裡有什麼秘密吧。', '', '', '', '', '', '', 0);
