INSERT INTO `quest_template_locale` VALUES (42175, 'frFR', 'Puissance grandissante', 'Allez voir Meryl Gangrorage au Hall du Gardien.', 'Maintenant que vous avez gagné en force, la Tirisgarde a besoin de votre aide, $n. Venez me trouver au hall du Gardien quand vos affaires en cours seront en ordre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42175, 'itIT', 'Potere crescente', 'Incontra Meryl Viltempesta nella Sala del Guardiano.', '$n, ora che il tuo potere è cresciuto, il Tirisgarde ha bisogno del tuo aiuto. Incontriamoci nella Sala del Guardiano quando avrai terminato le tue avventure.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42175, 'koKR', '강해지는 힘', '수호자의 전당에 있는 메릴 펠스톰과 만나야 합니다.', '$n, 이제 힘도 더 커졌으니 어서 티리스가드를 도와야 하네. 모험을 마치는 대로 수호자의 전당으로 돌아와서 나를 찾게나.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42175, 'ptBR', 'Poder crescente', 'Encontre-se com Meryl Tempestavil no Salão do Guardião.', 'O Tirisgarde precisa da sua ajuda agora que você ficou mais forte, $n. Encontre-me no Salão do Guardião quando tiver terminado as suas aventuras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42175, 'ruRU', 'Растущая сила', 'Вернитесь к Мерилу Буре Скверны в Оплот Хранителя.', 'Твои силы возросли, $n, и Стражи Тирисфаля нуждаются в твоей помощи. Встретимся в Оплоте Хранителя, когда захочешь отдохнуть от приключений.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42175, 'zhCN', '力量增长', '在守护者圣殿中与梅瑞尔·邪风会面。', '你变得更加强大了，而提瑞斯秘法会需要你的帮助，$n。等你的这段冒险结束之后，就来守护者圣殿找我吧。', '', '', '', '', '', '', 0);
